    if (!ramaGeoJSON || !ramaGeoJSON.features) return [];

    return ramaGeoJSON.features.map((feature, index) => ({
      id: `RAMA_${index + 1}`,
      buoyId: feature.properties.buoyId || `RAMA_${index + 1}`,
      name: feature.properties.type 
        ? `${feature.properties.type} Buoy ${feature.properties.buoyId || index + 1}` 
        : `RAMA Buoy ${index + 1}`,
      lat: feature.geometry.coordinates[1],
      lon: feature.geometry.coordinates[0],
      coordinates: feature.geometry.coordinates,
      type: "RAMA",
      status: "active",
      reportingAgency: feature.properties.reportingAgency || "INCOIS",
      url: feature.properties.url || null,
      reportingStatus: feature.properties.Reporting || feature.properties.reporting || 'Not Reporting',
      properties: feature.properties,
      parameters: {
        sst: feature.properties.sst || null,
        seaSurfaceTemperature: feature.properties.sst || null,
        salinity: feature.properties.salinity || null,
        windSpeed: feature.properties.windspeed || null,
        windDirection: feature.properties.winddirection || null,
