                {loading ? (
                  <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white mx-auto"></div>
                    <p className="text-gray-200 mt-2">Loading buoy data...</p>
                  </div>
                ) : selectedBuoy ? (
                  <div className="space-y-6">

                    {/* Basic Buoy Information */}
                    <div className="bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-lg p-4 border border-blue-500/20">
                      <h4 className="text-xl font-semibold mb-3 text-yellow-300">
                        {selectedBuoy.id} ({selectedBuoy.type} Network)
                      </h4>

                      {selectedBuoy.name && (
                        <p className="text-cyan-300 mb-3 font-medium text-lg">📍 {selectedBuoy.name}</p>
                      )}

                      <div className="bg-white/10 rounded-lg p-3 mb-3">
                        <p className="text-gray-300 font-semibold">📍 Location Details</p>
                        <p className="text-white">Coordinates: {formatCoordinates(selectedBuoy)}</p>
                        <p className="text-white">Region: {getBuoyRegion(selectedBuoy)}</p>
                      </div>

                      <div className="bg-white/10 rounded-lg p-3">
                        <p className="text-gray-300 font-semibold">🏢 Organization Info</p>
                        <p className="text-white">Status: 
                          <span className={selectedBuoy.status === 'active' ? 'text-green-400 ml-1' : 'text-red-400 ml-1'}>
                            {selectedBuoy.status === 'active' ? '🟢 Active' : '🔴 Inactive'}
                          </span>
                        </p>
                        {selectedBuoy.buoyId && (
                          <p className="text-white">Buoy ID: <span className="text-cyan-300">{selectedBuoy.buoyId}</span></p>
                        )}
                        {selectedBuoy.reportingAgency && (
                          <p className="text-white">Agency: <span className="text-cyan-300">{selectedBuoy.reportingAgency}</span></p>
                        )}
                        <p className="text-white">Data Source: <span className="text-green-300">{selectedBuoy.dataSource || 'INCOIS'}</span></p>

                        {/* INCOIS Interface Button for MOORED buoys */}
                        {selectedBuoy.type === 'MOORED' && (
                          <div className="mt-3">
                            <button
                              onClick={() => setShowINCOISInterface(true)}
                              className="w-full px-4 py-2 bg-gradient-to-r from-green-500 to-blue-500 rounded-lg font-semibold hover:from-green-600 hover:to-blue-600 transition-all duration-300 shadow-lg"
                            >
                              🌊 Open REAL INCOIS Interface - Enhanced Debug Mode
                            </button>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Current Parameters */}
                    {selectedBuoy.parameters && Object.keys(selectedBuoy.parameters).length > 0 ? (
                      <div>
                        <h5 className="text-lg font-semibold mb-4 text-cyan-300">🌊 Current Measurements</h5>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">

                          {Object.entries(selectedBuoy.parameters).map(([key, value]) => {
                            if (value === null || value === undefined) return null;

                            const parameterInfo = {
                              'sst': { label: 'Sea Surface Temperature', unit: '°C', color: 'text-red-400' },
                              'seaSurfaceTemperature': { label: 'Sea Surface Temperature', unit: '°C', color: 'text-red-400' },
                              'salinity': { label: 'Salinity', unit: 'PSU', color: 'text-blue-400' },
                              'windSpeed': { label: 'Wind Speed', unit: 'm/s', color: 'text-green-400' },
                              'windspeed': { label: 'Wind Speed', unit: 'm/s', color: 'text-green-400' },
                              'windDirection': { label: 'Wind Direction', unit: '°', color: 'text-cyan-400' },
                              'winddirection': { label: 'Wind Direction', unit: '°', color: 'text-cyan-400' },
                              'waveHeight': { label: 'Wave Height', unit: 'm', color: 'text-purple-400' },
                              'waveheight': { label: 'Wave Height', unit: 'm', color: 'text-purple-400' },
                              'pressure': { label: 'Pressure', unit: 'hPa', color: 'text-orange-400' },
                              'airTemperature': { label: 'Air Temperature', unit: '°C', color: 'text-pink-400' },
                            };

                            const info = parameterInfo[key] || { 
                              label: key.charAt(0).toUpperCase() + key.slice(1), 
                              unit: '', 
                              color: 'text-gray-400' 
                            };

                            return (
                              <div key={key} className="bg-white/10 rounded-lg p-4 hover:bg-white/15 transition-colors">
                                <p className="text-gray-300 text-xs uppercase tracking-wide">{info.label}</p>
                                <p className={`text-2xl font-bold ${info.color} mt-1`}>
                                  {value} {info.unit}
                                </p>
                              </div>
                            );
                          })}
                        </div>
                      </div>
                    ) : (
                      <div className="text-center py-8 bg-white/10 rounded-lg">
                        <p className="text-orange-300 text-lg">📊 No current measurement data available</p>
                        <p className="text-gray-400 text-sm mt-2">
                          {selectedBuoy.type === 'MOORED' 
                            ? 'Use the REAL INCOIS interface button above to access real oceanographic data'
                            : `This ${selectedBuoy.type} buoy is registered but real-time parameters are not currently accessible`
                          }
                        </p>
                      </div>
                    )}

                    {/* RAMA Properties */}
                    {selectedBuoy.type === 'RAMA' && selectedBuoy.properties && (
                      <div className="bg-gradient-to-r from-green-500/10 to-cyan-500/10 rounded-lg p-4 border border-green-500/20">
                        <h5 className="text-lg font-semibold mb-3 text-green-300">🌊 RAMA Network Properties</h5>
                        <div className="grid grid-cols-1 gap-3">
                          {Object.entries(selectedBuoy.properties).map(([key, value]) => (
                            <div key={key} className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                              <div className="flex justify-between items-center">
                                <span className="text-gray-300 font-medium">{formatPropertyName(key)}:</span>
                                <span className="text-white font-semibold">
                                  {key === 'URL' ? (
                                    <a 
                                      href={value} 
                                      target="_blank" 
                                      rel="noopener noreferrer"
                                      className="text-cyan-400 hover:text-cyan-300 underline break-all text-sm"
                                    >
                                      {value}
                                    </a>
                                  ) : key === 'Reporting' ? (
                                    <span className={value && value.toLowerCase().includes('reporting') && !value.toLowerCase().includes('not') ? 'text-green-400' : 'text-red-400'}>
                                      {String(value || 'Not Reporting')}
                                    </span>
                                  ) : (
                                    String(value || 'N/A')
                                  )}
                                </span>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Temperature and Salinity Profiles for ARGO */}
                    {tempChartData && (
                      <div>
                        <h5 className="text-lg font-semibold mb-3 text-red-300">Temperature Profile</h5>
                        <div style={{ height: '200px' }}>
                          <Line
                            data={tempChartData}
                            options={{
                              responsive: true,
                              maintainAspectRatio: false,
                              plugins: { 
                                legend: { labels: { color: 'white' } },
                                title: { display: true, text: 'Temperature vs Depth', color: 'white' }
                              },
                              scales: {
                                x: { 
                                  title: { display: true, text: 'Depth', color: 'white' },
                                  ticks: { color: 'white' },
                                  grid: { color: 'rgba(255,255,255,0.1)' }
                                },
                                y: { 
                                  title: { display: true, text: 'Temperature (°C)', color: 'white' },
                                  ticks: { color: 'white' },
                                  grid: { color: 'rgba(255,255,255,0.1)' }
                                },
                              },
                            }}
                          />
                        </div>
                      </div>
                    )}

                    {salinityChartData && (
                      <div>
                        <h5 className="text-lg font-semibold mb-3 text-blue-300">Salinity Profile</h5>
                        <div style={{ height: '200px' }}>
                          <Line
                            data={salinityChartData}
                            options={{
                              responsive: true,
                              maintainAspectRatio: false,
                              plugins: { 
                                legend: { labels: { color: 'white' } },
                                title: { display: true, text: 'Salinity vs Depth', color: 'white' }
                              },
                              scales: {
                                x: { 
                                  title: { display: true, text: 'Depth', color: 'white' },
                                  ticks: { color: 'white' },
                                  grid: { color: 'rgba(255,255,255,0.1)' }
                                },
                                y: { 
                                  title: { display: true, text: 'Salinity (PSU)', color: 'white' },
                                  ticks: { color: 'white' },
                                  grid: { color: 'rgba(255,255,255,0.1)' }
                                },
                              },
                            }}
                          />
                        </div>
                      </div>
                    )}

                    {/* Data Quality Footer */}
                    <div className="p-4 bg-gradient-to-r from-green-500/10 to-blue-500/10 rounded-lg border border-green-500/20">
                      <div className="flex items-center gap-2 mb-2">
                        <span className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></span>
                        <span className="text-green-300 font-semibold">
                          REAL INCOIS Data System - Enhanced Debug
                        </span>
                      </div>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-3 text-xs text-gray-300">
                        <div>
                          🌊 <strong>Data Source:</strong> {selectedBuoy.dataSource || 'INCOIS'}
                          <br />
                          📡 <strong>Network:</strong> {selectedBuoy.type}
                          <br />
                          {selectedBuoy.type === 'MOORED' && (
                            <>🎯 <strong>Interface:</strong> Real INCOIS proxy integration<br /></>
                          )}
                        </div>
                        <div>
                          📍 <strong>Precision:</strong> 6 decimal places
                          <br />
                          ⏰ <strong>Status:</strong> {selectedBuoy.status}
                          <br />
                          ✅ <strong>Debug Mode:</strong> Enhanced logging enabled
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <p className="text-gray-300 text-lg mb-4">
                      🔍 Click on any buoy marker to view data analysis
                    </p>
                    <div className="bg-white/10 rounded-lg p-4">
                      <h6 className="font-semibold mb-2">🌊 Enhanced Debug Features:</h6>
                      <div className="text-sm text-gray-300 space-y-1">
                        <p>• <strong>Console Logging:</strong> All API calls and responses logged</p>
                        <p>• <strong>Error Tracking:</strong> Detailed error messages</p>
                        <p>• <strong>URL Testing:</strong> Direct INCOIS URL test button</p>
                        <p>• <strong>Debug Info:</strong> Real-time status display</p>
                      </div>
                      <div className="mt-3 pt-2 border-t border-white/20">
                        <p className="text-sm text-cyan-300 font-medium">
                          📊 {filteredStats.total} buoys • Debug mode active
                        </p>
                        <p className="text-xs text-green-400 mt-1">
                          🐛 Check browser console for detailed logs
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
