// App.js
import { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import L from "leaflet";

// ✅ Fix for missing Leaflet marker icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

// ✅ Register Chart.js modules
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [selectedStation, setSelectedStation] = useState(null);

  // Example stations
  const stations = [
    {
      name: "Kavaratti",
      lat: 10.567,
      lon: 72.633,
      tideData: [0.5, 1.2, 0.8, 1.6, 1.1, 0.7],
    },
    {
      name: "Chennai",
      lat: 13.1,
      lon: 80.3,
      tideData: [0.3, 0.9, 1.5, 1.2, 0.6, 0.4],
    },
    {
      name: "Kakinada",
      lat: 16.933,
      lon: 82.25,
      tideData: [1.0, 1.3, 0.9, 1.4, 0.8, 0.5],
    },
  ];

  // Chart data
  const chartData = selectedStation
    ? {
        labels: ["00:00", "04:00", "08:00", "12:00", "16:00", "20:00"],
        datasets: [
          {
            label: `Tide levels @ ${selectedStation.name}`,
            data: selectedStation.tideData,
            borderColor: "#3b82f6",
            backgroundColor: "rgba(59,130,246,0.2)",
          },
        ],
      }
    : null;

  return (
    <div className="p-4 grid grid-cols-1 lg:grid-cols-2 gap-4 h-screen">
      {/* Map Section */}
      <div className="rounded-2xl shadow-lg overflow-hidden">
        <MapContainer
          center={[12.5, 80]}
          zoom={5}
          style={{ height: "500px", width: "100%" }} // ✅ Fixed height
        >
          <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {stations.map((station, idx) => (
            <Marker
              key={idx}
              position={[station.lat, station.lon]}
              eventHandlers={{
                click: () => setSelectedStation(station),
              }}
            >
              <Popup>{station.name}</Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Details + Chart */}
      <div className="rounded-2xl shadow-lg p-4 flex flex-col">
        {selectedStation ? (
          <>
            <h2 className="text-xl font-semibold mb-2">{selectedStation.name}</h2>
            <p className="text-sm text-gray-600 mb-4">
              Lat: {selectedStation.lat}, Lon: {selectedStation.lon}
            </p>
            <div className="flex-1">
              <Line data={chartData} />
            </div>
          </>
        ) : (
          <p className="text-gray-500">Click on a station marker to view details.</p>
        )}
      </div>
    </div>
  );
}

export default App;
