import { useEffect, useState } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from "chart.js";
import 'chartjs-adapter-date-fns';
import L from "leaflet";

// Fix for missing Leaflet marker icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

function App() {
  const [buoys, setBuoys] = useState([]);
  const [ramaFeatures, setRamaFeatures] = useState([]);
  const [filteredBuoys, setFilteredBuoys] = useState([]);
  const [selectedBuoy, setSelectedBuoy] = useState(null);

  // REAL INCOIS interface states - NO DUMMY DATA
  const [showINCOISInterface, setShowINCOISInterface] = useState(false);
  const [showRealINCOISProxy, setShowRealINCOISProxy] = useState(false);
  const [selectedParameter, setSelectedParameter] = useState('Air Temperature');
  const [chartData, setChartData] = useState(null);
  const [loadingChart, setLoadingChart] = useState(false);
  const [chartError, setChartError] = useState(null);
  const [zoomLevel, setZoomLevel] = useState('1M');
  const [dataTable, setDataTable] = useState([]);
  const [showDataTable, setShowDataTable] = useState(false);
  const [dataSource, setDataSource] = useState('');
  const [incoisProxyUrl, setIncoisProxyUrl] = useState('');

  // Chat interface states
  const [chatMessages, setChatMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [chatLoading, setChatLoading] = useState(false);

  const [stats, setStats] = useState({
    totalBuoys: 0,
    activeBuoys: 0,
    recentData: 0,
    avgTemperature: 0,
    avgSalinity: 0,
    buoysWithData: 0,
    buoysWithoutData: 0
  });
  const [networks, setNetworks] = useState({ OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
  const [loading, setLoading] = useState(false);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [yearRange, setYearRange] = useState([]);
  const [realTimeMode, setRealTimeMode] = useState(false);
  const [error, setError] = useState(null);

  // Filter states
  const [selectedRegion, setSelectedRegion] = useState('all');
  const [selectedNetwork, setSelectedNetwork] = useState('all');
  const [showActiveOnly, setShowActiveOnly] = useState(false);
  const [ramaReportingStatus, setRamaReportingStatus] = useState('all');

  // Regional boundaries for filtering
  const regions = {
    'indian_ocean': {
      name: 'Indian Ocean',
      bounds: { north: 30, south: -60, east: 100, west: 40 }
    },
    'bay_of_bengal': {
      name: 'Bay of Bengal',
      bounds: { north: 25, south: 5, east: 95, west: 80 }
    },
    'arabian_sea': {
      name: 'Arabian Sea',
      bounds: { north: 30, south: 0, east: 80, west: 50 }
    },
    'equatorial_indian': {
      name: 'Equatorial Indian Ocean',
      bounds: { north: 10, south: -10, east: 100, west: 50 }
    },
    'southern_indian': {
      name: 'Southern Indian Ocean',
      bounds: { north: -10, south: -60, east: 120, west: 40 }
    }
  };

  // INCOIS Parameters - exactly as they appear in INCOIS dropdown
  const incoisParameters = [
    'Air Pressure',
    'Air Temperature',
    'Relative Humidity',
    'Rainfall',
    'Wind Direction',
    'Wind Speed',
    'Wind Gust',
    'Irradiance',
    'Radiation In',
    'Current Direction @ 1.20m',
    'Current Direction @ 1.25m',
    'Current Direction @ 003m',
    'Current Speed @ 1.20m',
    'Current Speed @ 1.25m',
    'Current Speed @ 003m',
    'Current Direction @ 015m',
    'Current Direction @ 025m',
    'Current Direction @ 035m',
    'Current Direction @ 050m',
    'Current Direction @ 075m',
    'Current Direction @ 100m',
    'Current Speed @ 015m',
    'Current Speed @ 025m',
    'Current Speed @ 035m',
    'Current Speed @ 050m',
    'Current Speed @ 075m',
    'Current Speed @ 100m',
    'Water Temperature @ 0.5m',
    'Water Temperature @ 001m',
    'Water Temperature @ 005m',
    'Water Temperature @ 010m',
    'Water Temperature @ 015m',
    'Water Temperature @ 020m',
    'Water Temperature @ 030m',
    'Water Temperature @ 050m',
    'Water Temperature @ 075m',
    'Water Temperature @ 100m',
    'Water Temperature @ 200m',
    'Water Temperature @ 500m',
    'Salinity @ 0.5m',
    'Salinity @ 001m',
    'Salinity @ 005m',
    'Salinity @ 010m',
    'Salinity @ 015m',
    'Salinity @ 020m',
    'Salinity @ 030m',
    'Salinity @ 050m',
    'Salinity @ 075m',
    'Salinity @ 100m',
    'Salinity @ 200m',
    'Salinity @ 500m',
    'Significant Wave Height'
  ];

  // Zoom/Time range options like INCOIS
  const zoomOptions = [
    { value: '1W', label: '1 Week', days: 7 },
    { value: '1M', label: '1 Month', days: 30 },
    { value: '3M', label: '3 Months', days: 90 },
    { value: '6M', label: '6 Months', days: 180 },
    { value: '1Y', label: '1 Year', days: 365 },
    { value: 'All', label: 'All Data', days: null }
  ];

  // ðŸ¤– Dummy chat Q&A data
  const dummyQAData = [
    {
      question: "What is the current sea surface temperature in the Bay of Bengal?",
      answer: "Based on our RAMA and MOORED buoy data, the current sea surface temperature in the Bay of Bengal ranges from 28.2Â°C to 30.1Â°C. The northern regions show higher temperatures around 29.8Â°C, while southern areas average 28.5Â°C.",
      category: "Temperature"
    },
    {
      question: "How many active ocean buoys are currently operational?",
      answer: "We currently have 202 active ocean buoys operational across the Indian Ocean, including 25 RAMA buoys, 166 ARGO floats, and 11 MOORED buoys. All are providing real-time oceanographic data.",
      category: "Buoy Status"
    },
    {
      question: "What parameters can I monitor using INCOIS ocean buoys?",
      answer: "Our buoys monitor 53+ oceanographic parameters including sea surface temperature, salinity, wave height, wind speed/direction, air temperature, pressure, currents at various depths, and water temperature/salinity profiles up to 500m depth.",
      category: "Parameters"
    },
    {
      question: "How accurate is the real-time ocean data?",
      answer: "INCOIS ocean data is highly accurate with precision instruments. Temperature sensors have Â±0.1Â°C accuracy, salinity measurements have Â±0.02 PSU precision, and wind measurements are accurate to Â±0.5 m/s. Data undergoes quality control before distribution.",
      category: "Data Quality"
    },
    {
      question: "Can I access historical ocean data for research?",
      answer: "Yes! Historical data is available through our CSV download feature. You can download oceanographic data for any year from 2012 onwards, including temperature profiles, current measurements, and meteorological parameters.",
      category: "Data Access"
    },
    {
      question: "What is the difference between RAMA, ARGO, and MOORED buoys?",
      answer: "RAMA buoys are surface moorings measuring air-sea interactions. ARGO are profiling floats that dive to 2000m depth measuring temperature/salinity profiles. MOORED buoys are fixed platforms with comprehensive meteorological and oceanographic sensors.",
      category: "Buoy Types"
    },
    {
      question: "How often is the ocean data updated?",
      answer: "Real-time data is updated every 10-60 minutes depending on the buoy type. RAMA buoys transmit hourly, ARGO floats surface every 10 days with profiles, and MOORED buoys provide continuous measurements with hourly transmission.",
      category: "Data Updates"
    },
    {
      question: "What should I do if a buoy shows 'No Data Available'?",
      answer: "This indicates the buoy may be undergoing maintenance, experiencing technical issues, or data transmission problems. Use the INCOIS Proxy feature to access the official INCOIS interface for the most current status and alternative data sources.",
      category: "Troubleshooting"
    }
  ];

  // Initialize chat with a welcome message
  useEffect(() => {
    setChatMessages([
      {
        id: 1,
        type: 'bot',
        message: "ðŸŒŠ Welcome to INCOIS Ocean Data Assistant! I can help you with questions about ocean buoys, real-time data, and oceanographic parameters. What would you like to know?",
        timestamp: new Date()
      }
    ]);
  }, []);

  // Handle chat message sending
  const sendChatMessage = () => {
    if (!currentMessage.trim()) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      message: currentMessage,
      timestamp: new Date()
    };

    setChatMessages(prev => [...prev, userMessage]);
    setChatLoading(true);

    // Simulate bot response with dummy data
    setTimeout(() => {
      // Find the most relevant answer based on keywords
      let bestMatch = dummyQAData[0]; // default
      let maxScore = 0;

      dummyQAData.forEach(qa => {
        const questionWords = qa.question.toLowerCase().split(' ');
        const messageWords = currentMessage.toLowerCase().split(' ');

        let score = 0;
        messageWords.forEach(word => {
          if (questionWords.some(qWord => qWord.includes(word) && word.length > 2)) {
            score++;
          }
        });

        if (score > maxScore) {
          maxScore = score;
          bestMatch = qa;
        }
      });

      const botMessage = {
        id: Date.now() + 1,
        type: 'bot',
        message: maxScore > 0 ? bestMatch.answer : "I understand you're asking about ocean data. For specific technical details, please use the INCOIS interface or contact our support team. Is there anything else about our buoy network I can help with?",
        timestamp: new Date(),
        category: maxScore > 0 ? bestMatch.category : 'General'
      };

      setChatMessages(prev => [...prev, botMessage]);
      setChatLoading(false);
    }, 1500);

    setCurrentMessage('');
  };

  // Quick question buttons
  const quickQuestions = [
    "How many active buoys?",
    "Sea temperature ranges?",
    "Data update frequency?",
    "Available parameters?"
  ];

  const handleQuickQuestion = (question) => {
    setCurrentMessage(question);
    setTimeout(() => sendChatMessage(), 100);
  };

  useEffect(() => {
    const currentYear = new Date().getFullYear();
    const years = [];
    for (let year = 2012; year <= currentYear; year++) {
      years.push(year);
    }
    setYearRange(years.reverse());
  }, []);

  // Helper function to normalize RAMA GeoJSON features to buoy format
  const normalizeRamaBuoys = (ramaGeoJSON) => {
    if (!ramaGeoJSON || !ramaGeoJSON.features) return [];

    return ramaGeoJSON.features.map((feature, index) => ({
      id: `RAMA_${index + 1}`,
      buoyId: feature.properties.buoyId || `RAMA_${index + 1}`,
      name: feature.properties.type 
        ? `${feature.properties.type} Buoy ${feature.properties.buoyId || index + 1}` 
        : `RAMA Buoy ${index + 1}`,
      lat: feature.geometry.coordinates[1],
      lon: feature.geometry.coordinates[0],
      coordinates: feature.geometry.coordinates,
      type: "RAMA",
      status: "active",
      reportingAgency: feature.properties.reportingAgency || "INCOIS",
      url: feature.properties.url || null,
      reportingStatus: feature.properties.Reporting || feature.properties.reporting || 'Not Reporting',
      properties: feature.properties,
      parameters: {
        sst: feature.properties.sst || null,
        seaSurfaceTemperature: feature.properties.sst || null,
        salinity: feature.properties.salinity || null,
        windSpeed: feature.properties.windspeed || null,
        windDirection: feature.properties.winddirection || null,
        pressure: feature.properties.pressure || null,
        waveHeight: feature.properties.waveheight || null,
      },
      dataSource: "INCOIS",
    }));
  };

  /**
   * ðŸŒŠ REAL INCOIS Data Loader - NO DUMMY DATA GENERATION
   */
  const loadRealINCOISData = async (buoyId, parameter) => {
    setLoadingChart(true);
    setChartError(null);
    setChartData(null);
    setDataTable([]);
    setDataSource('');

    try {
      console.log(`ðŸŒŠ Loading REAL INCOIS data: ${buoyId} â†’ ${parameter}...`);

      const response = await axios.get(`http://localhost:5000/api/moored/${buoyId}/${parameter}`);
      console.log(`ðŸ“Š Server response:`, response.data);

      if (response.data && response.data.success && response.data.isReal === true) {
        // REAL INCOIS DATA FOUND
        console.log(`âœ… REAL INCOIS data loaded: ${parameter}`);
        console.log(`ðŸ“ˆ Extraction method: ${response.data.extractionMethod}`);
        console.log(`ðŸ“Š Data points: ${response.data.extractedPoints}`);

        const realData = parseRealINCOISData(response.data.data, parameter);
        if (realData) {
          setChartData(realData.chartData);
          setDataTable(realData.tableData);
          setDataSource('REAL INCOIS Data');
        } else {
          throw new Error('Failed to parse real INCOIS data');
        }
      } else {
        // NO REAL DATA AVAILABLE
        console.log(`âŒ NO REAL DATA: Server response:`, response.data);
        setChartError(`No real INCOIS data available for ${parameter}. ${response.data.message || 'Try the proxy version instead.'}`);
        setDataSource('No Real Data Available');
      }
    } catch (error) {
      console.error(`âŒ Error loading ${parameter}:`, error);
      setChartError(`Error loading real data: ${error.response?.data?.message || error.message}`);
      setDataSource('Error');
    }

    setLoadingChart(false);
  };

  /**
   * Parse real INCOIS data into chart format
   */
  const parseRealINCOISData = (rawData, parameter) => {
    try {
      if (!rawData || typeof rawData !== 'string') {
        console.error('Invalid raw data format');
        return null;
      }

      const lines = rawData.split('\n').filter(line => line.trim() && !line.startsWith('#'));
      console.log(`ðŸ“„ Processing ${lines.length} data lines for ${parameter}`);

      const dataPoints = [];
      const tableData = [];

      lines.forEach((line, index) => {
        // Handle CSV format: timestamp,value or date,value
        const parts = line.split(',');
        if (parts.length >= 2) {
          const timestampStr = parts[0].trim();
          const valueStr = parts[1].trim();

          const timestamp = new Date(timestampStr);
          const value = parseFloat(valueStr);

          if (!isNaN(timestamp.getTime()) && !isNaN(value)) {
            // For chart
            dataPoints.push({ x: timestamp, y: value });

            // For table
            tableData.push({
              date: timestamp.toISOString().split('T')[0],
              time: timestamp.toTimeString().split(' ')[0],
              value: value,
              unit: getParameterUnit(parameter)
            });
          } else if (index < 5) {
            console.warn(`âš ï¸ Invalid data point at line ${index}: "${line}"`);
          }
        }
      });

      if (dataPoints.length === 0) {
        console.error('No valid data points found');
        return null;
      }

      const chartData = {
        datasets: [{
