        </div>
      </div>

      {/* Map */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6">
        <div className="bg-white/20 rounded-2xl shadow-xl">
          <MapContainer center={[12, 77]} zoom={5} style={{ height: "500px", width: "100%" }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution='&copy; OpenStreetMap' />
            {buoys.map((buoy, idx) => (
              <Marker
                key={idx}
                position={[buoy.lat || 0, buoy.lon || 0]}
                icon={getIconForBuoy(buoy)}
                eventHandlers={{ click: () => fetchBuoyDetails(buoy.id) }}
              >
                <Popup>
                  <div className="text-black">
                    <strong>{buoy.id}</strong><br/>
                    Network: {buoy.type}<br/>
                    Status: {buoy.status === 'active' ? 'ð¢ Active' : 'ð´ Inactive'}<br/>
                    SST: {getParameterValue(buoy, 'seaSurfaceTemperature')}Â°C<br/>
                    Salinity: {getParameterValue(buoy, 'salinity')} PSU<br/>
                    Air Temp: {getParameterValue(buoy, 'airTemperature')}Â°C<br/>
                    Data: {buoy.parameters ? 'â Available' : 'â No Data'}
                  </div>
                </Popup>
