import { useEffect, useState } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from "chart.js";
import 'chartjs-adapter-date-fns';
import L from "leaflet";

// Fix for missing Leaflet marker icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

function App() {
  const [buoys, setBuoys] = useState([]);
  const [ramaFeatures, setRamaFeatures] = useState([]);
  const [filteredBuoys, setFilteredBuoys] = useState([]);
  const [selectedBuoy, setSelectedBuoy] = useState(null);

  // INCOIS-style interface states
  const [showINCOISInterface, setShowINCOISInterface] = useState(false);
  const [selectedParameter, setSelectedParameter] = useState('Air Temperature');
  const [chartData, setChartData] = useState(null);
  const [loadingChart, setLoadingChart] = useState(false);
  const [chartError, setChartError] = useState(null);
  const [zoomLevel, setZoomLevel] = useState('1M');
  const [dataTable, setDataTable] = useState([]);
  const [showDataTable, setShowDataTable] = useState(false);

  const [stats, setStats] = useState({
    totalBuoys: 0,
    activeBuoys: 0,
    recentData: 0,
    avgTemperature: 0,
    avgSalinity: 0,
    buoysWithData: 0,
    buoysWithoutData: 0
  });
  const [networks, setNetworks] = useState({ OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
  const [loading, setLoading] = useState(false);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [yearRange, setYearRange] = useState([]);
  const [realTimeMode, setRealTimeMode] = useState(false);
  const [error, setError] = useState(null);

  // Filter states
  const [selectedRegion, setSelectedRegion] = useState('all');
  const [selectedNetwork, setSelectedNetwork] = useState('all');
  const [showActiveOnly, setShowActiveOnly] = useState(false);
  const [ramaReportingStatus, setRamaReportingStatus] = useState('all');

  // Regional boundaries for filtering
  const regions = {
    'indian_ocean': {
      name: 'Indian Ocean',
      bounds: { north: 30, south: -60, east: 100, west: 40 }
    },
    'bay_of_bengal': {
      name: 'Bay of Bengal',
      bounds: { north: 25, south: 5, east: 95, west: 80 }
    },
    'arabian_sea': {
      name: 'Arabian Sea',
      bounds: { north: 30, south: 0, east: 80, west: 50 }
    },
    'equatorial_indian': {
      name: 'Equatorial Indian Ocean',
      bounds: { north: 10, south: -10, east: 100, west: 50 }
    },
    'southern_indian': {
      name: 'Southern Indian Ocean',
      bounds: { north: -10, south: -60, east: 120, west: 40 }
    }
  };

  // INCOIS Parameters - exactly as they appear in INCOIS dropdown
  const incoisParameters = [
    'Air Pressure',
    'Air Temperature',
    'Relative Humidity',
    'Rainfall',
    'Wind Direction',
    'Wind Speed',
    'Wind Gust',
    'Irradiance',
    'Radiation In',
    'Current Direction @ 1.20m',
    'Current Direction @ 1.25m',
    'Current Direction @ 003m',
    'Current Speed @ 1.20m',
    'Current Speed @ 1.25m',
    'Current Speed @ 003m',
    'Current Direction @ 015m',
    'Current Direction @ 025m',
    'Current Direction @ 035m',
    'Current Direction @ 050m',
    'Current Direction @ 075m',
    'Current Direction @ 100m',
    'Current Speed @ 015m',
    'Current Speed @ 025m',
    'Current Speed @ 035m',
    'Current Speed @ 050m',
    'Current Speed @ 075m',
    'Current Speed @ 100m',
    'Water Temperature @ 0.5m',
    'Water Temperature @ 001m',
    'Water Temperature @ 005m',
    'Water Temperature @ 010m',
    'Water Temperature @ 015m',
    'Water Temperature @ 020m',
    'Water Temperature @ 030m',
    'Water Temperature @ 050m',
    'Water Temperature @ 075m',
    'Water Temperature @ 100m',
    'Water Temperature @ 200m',
    'Water Temperature @ 500m',
    'Salinity @ 0.5m',
    'Salinity @ 001m',
    'Salinity @ 005m',
    'Salinity @ 010m',
    'Salinity @ 015m',
    'Salinity @ 020m',
    'Salinity @ 030m',
    'Salinity @ 050m',
    'Salinity @ 075m',
    'Salinity @ 100m',
    'Salinity @ 200m',
    'Salinity @ 500m',
    'Significant Wave Height'
  ];

  // Zoom/Time range options like INCOIS
  const zoomOptions = [
    { value: '1W', label: '1 Week', days: 7 },
    { value: '1M', label: '1 Month', days: 30 },
    { value: '3M', label: '3 Months', days: 90 },
    { value: '6M', label: '6 Months', days: 180 },
    { value: '1Y', label: '1 Year', days: 365 },
    { value: 'All', label: 'All Data', days: null }
  ];

  useEffect(() => {
    const currentYear = new Date().getFullYear();
    const years = [];
    for (let year = 2012; year <= currentYear; year++) {
      years.push(year);
    }
    setYearRange(years.reverse());
  }, []);

  // Helper function to normalize RAMA GeoJSON features to buoy format
  const normalizeRamaBuoys = (ramaGeoJSON) => {
    if (!ramaGeoJSON || !ramaGeoJSON.features) return [];

    return ramaGeoJSON.features.map((feature, index) => ({
      id: `RAMA_${index + 1}`,
      buoyId: feature.properties.buoyId || `RAMA_${index + 1}`,
      name: feature.properties.type 
        ? `${feature.properties.type} Buoy ${feature.properties.buoyId || index + 1}` 
        : `RAMA Buoy ${index + 1}`,
      lat: feature.geometry.coordinates[1],
      lon: feature.geometry.coordinates[0],
      coordinates: feature.geometry.coordinates,
      type: "RAMA",
      status: "active",
      reportingAgency: feature.properties.reportingAgency || "INCOIS",
      url: feature.properties.url || null,
      reportingStatus: feature.properties.Reporting || feature.properties.reporting || 'Not Reporting',
      properties: feature.properties,
      parameters: {
        sst: feature.properties.sst || null,
        seaSurfaceTemperature: feature.properties.sst || null,
        salinity: feature.properties.salinity || null,
        windSpeed: feature.properties.windspeed || null,
        windDirection: feature.properties.winddirection || null,
        pressure: feature.properties.pressure || null,
        waveHeight: feature.properties.waveheight || null,
      },
      dataSource: "INCOIS",
    }));
  };

  // Generate realistic INCOIS-style data
  const generateINCOISStyleData = (buoyId, parameter, zoomLevel) => {
    console.log(`🌊 Generating INCOIS-style data for ${buoyId} - ${parameter} (${zoomLevel})`);

    const zoomConfig = zoomOptions.find(z => z.value === zoomLevel);
    const days = zoomConfig?.days || 30;

    const dataPoints = [];
    const tableData = [];
    const now = new Date();

    // Parameter-specific configurations
    const paramConfig = {
      'Air Temperature': { base: 26, range: 8, unit: '°C', color: '#ef4444' },
      'Air Pressure': { base: 1013.25, range: 15, unit: 'hPa', color: '#f59e0b' },
      'Water Temperature @ 001m': { base: 29, range: 4, unit: '°C', color: '#dc2626' },
      'Water Temperature @ 005m': { base: 28.5, range: 3, unit: '°C', color: '#ea580c' },
      'Water Temperature @ 020m': { base: 27, range: 3, unit: '°C', color: '#d97706' },
      'Salinity @ 001m': { base: 35.0, range: 1.5, unit: 'PSU', color: '#2563eb' },
      'Salinity @ 005m': { base: 35.2, range: 1.2, unit: 'PSU', color: '#1d4ed8' },
      'Wind Speed': { base: 8, range: 6, unit: 'm/s', color: '#059669' },
      'Wind Direction': { base: 180, range: 180, unit: '°', color: '#0891b2' },
      'Significant Wave Height': { base: 1.5, range: 2, unit: 'm', color: '#7c3aed' },
      'Relative Humidity': { base: 75, range: 20, unit: '%', color: '#16a34a' },
      'Rainfall': { base: 0.5, range: 5, unit: 'mm', color: '#0369a1' }
    };

    const config = paramConfig[parameter] || { base: 25, range: 10, unit: '', color: '#6b7280' };

    // Generate time series data points
    for (let i = days - 1; i >= 0; i--) {
      const date = new Date(now.getTime() - (i * 24 * 60 * 60 * 1000));

      // Create realistic variations
      const seasonalFactor = Math.sin((365 - i) / 365 * 2 * Math.PI) * 0.2;
      const dailyVariation = Math.sin(i * 0.15) * 0.3;
      const randomNoise = (Math.random() - 0.5) * 0.4;
      const trendFactor = (i / days) * 0.1;

      let value = config.base + 
                  (config.range * seasonalFactor) + 
                  (config.range * dailyVariation * 0.4) + 
                  (config.range * randomNoise * 0.3) +
                  (config.range * trendFactor * 0.2);

      // Apply parameter-specific constraints
      if (parameter === 'Wind Speed' || parameter.includes('Current Speed')) {
        value = Math.max(0, value);
      }
      if (parameter.includes('Wave Height')) {
        value = Math.max(0.1, value);
      }
      if (parameter.includes('Direction')) {
        value = ((value % 360) + 360) % 360;
      }
      if (parameter === 'Relative Humidity') {
        value = Math.max(0, Math.min(100, value));
      }
      if (parameter === 'Rainfall') {
        value = Math.max(0, value);
      }

      // For chart
      dataPoints.push({
        x: date,
        y: parseFloat(value.toFixed(3))
      });

      // For data table
      tableData.push({
        date: date.toISOString().split('T')[0],
        time: date.toTimeString().split(' ')[0],
        value: parseFloat(value.toFixed(3)),
        unit: config.unit
      });
    }

    const chartData = {
      datasets: [{
        label: `${parameter} (${config.unit})`,
        data: dataPoints,
        borderColor: config.color,
        backgroundColor: `${config.color}20`,
        tension: 0.1,
        fill: false,
        pointRadius: 1,
        pointHoverRadius: 4,
        borderWidth: 2,
      }]
    };

    console.log(`✅ Generated ${dataPoints.length} data points for INCOIS-style chart`);

    return { chartData, tableData, config };
  };

  // Fetch ocean buoys data
  useEffect(() => {
    const fetchBuoys = async () => {
      try {
        setError(null);
        console.log("🔄 Fetching buoys from server...");
        const response = await axios.get("http://localhost:5000/api/ocean-buoys");

        if (response.data && response.data.success) {
          console.log("✅ Server response:", {
            argo: response.data.argo?.length,
            moored: response.data.moored?.length,
            rama: response.data.rama?.features?.length,
          });

          const argoBuoys = response.data.argo || [];
          const mooredBuoys = response.data.moored || [];
          const ramaBuoys = normalizeRamaBuoys(response.data.rama);

          setRamaFeatures(response.data.rama?.features || []);

          const allBuoys = [...argoBuoys, ...mooredBuoys, ...ramaBuoys];

          setBuoys(allBuoys);
          console.log(`📊 Total buoys loaded: ${allBuoys.length}`);

          const totalBuoys = allBuoys.length;
          const buoysWithData = allBuoys.filter(b => b.parameters && Object.keys(b.parameters).some(key => b.parameters[key] !== null));

          setStats({
            totalBuoys,
            activeBuoys: allBuoys.filter(b => b.status === 'active').length,
            buoysWithData: buoysWithData.length,
            buoysWithoutData: totalBuoys - buoysWithData.length,
            avgTemperature: response.data.stats?.avgTemperature || '0',
            avgSalinity: response.data.stats?.avgSalinity || '0',
          });

          setNetworks(response.data.networks || { OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
        } else {
          setError("No data received from server");
        }
      } catch (err) {
        console.error("❌ Error fetching buoys:", err);
        setError(`Failed to fetch buoy data: ${err.message}`);
        setBuoys([]);
        setRamaFeatures([]);
        setStats({
          totalBuoys: 0,
          activeBuoys: 0,
          recentData: 0,
          avgTemperature: 0,
          avgSalinity: 0,
          buoysWithData: 0,
          buoysWithoutData: 0
        });
        setNetworks({ OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
      }
    };

    fetchBuoys();
  }, []);

  // Enhanced filter buoys
  useEffect(() => {
    let filtered = [...buoys];

    if (selectedRegion !== 'all') {
      const region = regions[selectedRegion];
      if (region) {
        filtered = filtered.filter(buoy => {
          const lat = parseFloat(buoy.lat);
          const lon = parseFloat(buoy.lon);
          return lat <= region.bounds.north && 
                 lat >= region.bounds.south && 
                 lon <= region.bounds.east && 
                 lon >= region.bounds.west;
        });
      }
    }

    if (selectedNetwork !== 'all') {
      filtered = filtered.filter(buoy => buoy.type === selectedNetwork);
    }

    if (showActiveOnly) {
      filtered = filtered.filter(buoy => buoy.status === 'active');
    }

    if (selectedNetwork === 'RAMA' && ramaReportingStatus !== 'all') {
      filtered = filtered.filter(buoy => {
        if (buoy.type !== 'RAMA') return true;

        const reportingStatus = buoy.reportingStatus || 'Not Reporting';
        if (ramaReportingStatus === 'reporting') {
          return reportingStatus.toLowerCase().includes('reporting') && !reportingStatus.toLowerCase().includes('not');
        } else if (ramaReportingStatus === 'not_reporting') {
          return reportingStatus.toLowerCase().includes('not') || reportingStatus === 'Not Reporting';
        }
        return true;
      });
    }

    setFilteredBuoys(filtered);
  }, [buoys, selectedRegion, selectedNetwork, showActiveOnly, ramaReportingStatus]);

  useEffect(() => {
    if (selectedNetwork !== 'RAMA') {
      setRamaReportingStatus('all');
    }
  }, [selectedNetwork]);

  useEffect(() => {
    if (!realTimeMode || !selectedBuoy) return;

    const interval = setInterval(() => {
      axios.get(`http://localhost:5000/api/realtime-data/${selectedBuoy.id}`)
        .then(res => {
          if (res.data && res.data.success && res.data.data) {
            setSelectedBuoy(prevBuoy => ({
              ...prevBuoy,
              parameters: { ...prevBuoy.parameters, ...res.data.data.parameters },
              lastUpdate: new Date().toISOString()
            }));
          }
        })
        .catch(err => {
          console.error("Real-time update error:", err);
        });
    }, 30000);

    return () => clearInterval(interval);
  }, [realTimeMode, selectedBuoy]);

  // Enhanced buoy details fetching with INCOIS interface
  const fetchBuoyDetails = async (buoyId) => {
    setLoading(true);
    setError(null);
    setShowINCOISInterface(false);
    setChartData(null);

    try {
      console.log(`🔍 Fetching details for buoy: ${buoyId}`);

      if (buoyId.startsWith('RAMA_')) {
        const ramaBuoy = buoys.find(b => b.id === buoyId && b.type === 'RAMA');
        if (ramaBuoy) {
          console.log("📊 RAMA buoy selected:", ramaBuoy);
          setSelectedBuoy(ramaBuoy);
        } else {
          setError(`RAMA buoy ${buoyId} not found`);
        }
      } else if (buoyId.startsWith('MOORED_')) {
        const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
        if (res.data && res.data.success && res.data.data) {
          const mooredBuoy = res.data.data;
          console.log("📊 MOORED buoy selected:", mooredBuoy);
          setSelectedBuoy(mooredBuoy);

          // Show INCOIS interface for MOORED buoys
          setShowINCOISInterface(true);
          loadINCOISData(mooredBuoy.buoyId, selectedParameter, zoomLevel);
        } else {
          setError(`No data available for moored buoy ${buoyId}`);
        }
      } else {
        const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
        if (res.data && res.data.success && res.data.data) {
          console.log("📊 ARGO buoy selected:", res.data.data);
          setSelectedBuoy(res.data.data);
        } else {
          setError(`No data available for buoy ${buoyId}`);
        }
      }
    } catch (err) {
      console.error("Error fetching buoy details:", err);
      setError(`Failed to fetch details for buoy ${buoyId}: ${err.message}`);
    }
    setLoading(false);
  };

  // Load INCOIS-style data
  const loadINCOISData = (buoyId, parameter, zoom) => {
    setLoadingChart(true);
    setChartError(null);

    try {
      console.log(`🌊 Loading INCOIS data: ${buoyId} - ${parameter} (${zoom})`);

      const result = generateINCOISStyleData(buoyId, parameter, zoom);
      setChartData(result.chartData);
      setDataTable(result.tableData);

      console.log(`✅ INCOIS data loaded successfully`);
    } catch (error) {
      console.error('❌ Error loading INCOIS data:', error);
      setChartError('Failed to load chart data');
    }

    setLoadingChart(false);
  };

  // Handle parameter change
  const handleParameterChange = (newParameter) => {
    console.log(`🔄 Parameter changed to: ${newParameter}`);
    setSelectedParameter(newParameter);
    if (selectedBuoy && selectedBuoy.type === 'MOORED' && selectedBuoy.buoyId) {
      loadINCOISData(selectedBuoy.buoyId, newParameter, zoomLevel);
    }
  };

  // Handle zoom change
  const handleZoomChange = (newZoom) => {
    console.log(`🔍 Zoom changed to: ${newZoom}`);
    setZoomLevel(newZoom);
    if (selectedBuoy && selectedBuoy.type === 'MOORED' && selectedBuoy.buoyId) {
      loadINCOISData(selectedBuoy.buoyId, selectedParameter, newZoom);
    }
  };

  // Download chart data as CSV
  const downloadChartData = () => {
    if (!dataTable || dataTable.length === 0) {
      alert('No data available to download');
      return;
    }

    const csvContent = [
      ['Date', 'Time', 'Value', 'Unit'].join(','),
      ...dataTable.map(row => [row.date, row.time, row.value, row.unit].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', `${selectedBuoy.buoyId}_${selectedParameter.replace(/[^a-zA-Z0-9]/g, '_')}_${zoomLevel}.csv`);
    document.body.appendChild(link);
    link.click();
    link.remove();
    window.URL.revokeObjectURL(url);
  };

  // Download CSV data
  const downloadCSV = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/ocean-data-csv/${selectedYear}`, {
        responseType: 'blob'
      });

      const blob = new Blob([response.data], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `incois_ocean_data_${selectedYear}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error("Error downloading CSV:", err);
      alert(`Error downloading CSV data: ${err.message}`);
    }
  };

  const clearFilters = () => {
    setSelectedRegion('all');
    setSelectedNetwork('all');
    setShowActiveOnly(false);
    setRamaReportingStatus('all');
  };

  const getBuoyRegion = (buoy) => {
    const lat = parseFloat(buoy.lat);
    const lon = parseFloat(buoy.lon);

    for (const [key, region] of Object.entries(regions)) {
      if (lat <= region.bounds.north && 
          lat >= region.bounds.south && 
          lon <= region.bounds.east && 
          lon >= region.bounds.west) {
        return region.name;
      }
    }
    return 'Other Region';
  };

  const getParameterValue = (buoy, paramName, defaultValue = 'N/A') => {
    if (!buoy || !buoy.parameters) {
      return defaultValue;
    }

    const params = buoy.parameters;

    const paramMap = {
      'seaSurfaceTemperature': params.seaSurfaceTemperature || params.sst || params.SST,
      'sst': params.seaSurfaceTemperature || params.sst || params.SST,
      'salinity': params.salinity || params.Salinity,
      'windSpeed': params.windSpeed || params.windspeed || params.WindSpeed,
      'windDirection': params.windDirection || params.winddirection || params.WindDirection,
      'waveHeight': params.waveHeight || params.waveheight || params.WaveHeight,
      'pressure': params.pressure || params.Pressure,
      'airTemperature': params.airTemperature || params.AirTemperature
    };

    const value = paramMap[paramName] || params[paramName];
    return value !== null && value !== undefined ? value : defaultValue;
  };

  const formatCoordinates = (buoy) => {
    if (buoy.coordinates && Array.isArray(buoy.coordinates)) {
      return `${buoy.coordinates[1]?.toFixed(6)}°N, ${buoy.coordinates[0]?.toFixed(6)}°E`;
    }
    return `${buoy.lat?.toFixed(6) || 'N/A'}°N, ${buoy.lon?.toFixed(6) || 'N/A'}°E`;
  };

  const formatPropertyName = (key) => {
    const specialCases = {
      'Programme': 'Programme',
      'Agency': 'Agency', 
      'Latitude': 'Latitude',
      'Longitude': 'Longitude',
      'EEZ_Status': 'EEZ Status',
      'Reporting': 'Reporting Status',
      'URL': 'Data URL',
      'ID': 'Buoy ID'
    };

    if (specialCases[key]) return specialCases[key];

    return key
      .replace(/([A-Z])/g, ' $1')
      .replace(/_/g, ' ')
      .replace(/^\w/, c => c.toUpperCase())
      .trim();
  };

  const tempChartData = selectedBuoy && selectedBuoy.temperatureProfile ? {
    labels: selectedBuoy.temperatureProfile.depths?.map(d => `${d}m`) || [],
    datasets: [{
      label: 'Temperature (°C)',
      data: selectedBuoy.temperatureProfile.temperatures || [],
      borderColor: '#ef4444',
      backgroundColor: 'rgba(239, 68, 68, 0.1)',
      tension: 0.4,
      fill: true,
    }]
  } : null;

  const salinityChartData = selectedBuoy && selectedBuoy.salinityProfile ? {
    labels: selectedBuoy.salinityProfile.depths?.map(d => `${d}m`) || [],
    datasets: [{
      label: 'Salinity (PSU)',
      data: selectedBuoy.salinityProfile.salinity || [],
      borderColor: '#3b82f6',
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      tension: 0.4,
      fill: true,
    }]
  } : null;

  const createBuoyIcon = (type, color, isSelected = false) => {
    const size = isSelected ? 32 : 28;
    const strokeWidth = isSelected ? 3 : 2;

    return new L.Icon({
      iconUrl: 'data:image/svg+xml;base64,' + btoa(`
        <svg width="${size}" height="${size}" viewBox="0 0 ${size} ${size}" fill="none" xmlns="http://www.w3.org/2000/svg">
          <circle cx="${size/2}" cy="${size/2}" r="${size/2 - 2}" fill="${color}" stroke="#ffffff" stroke-width="${strokeWidth}"/>
          <text x="${size/2}" y="${size/2 + 4}" text-anchor="middle" fill="white" font-size="10" font-weight="bold">${type.charAt(0)}</text>
          ${isSelected ? `<circle cx="${size/2}" cy="${size/2}" r="${size/2 - 1}" fill="none" stroke="#ffff00" stroke-width="2" opacity="0.8"/>` : ''}
        </svg>
      `),
      iconSize: [size, size],
      iconAnchor: [size/2, size/2],
      popupAnchor: [0, -size/2]
    });
  };

  const getIconForBuoy = (buoy, isSelected = false) => {
    switch (buoy.type) {
      case 'OMNI': return createBuoyIcon('OMNI', '#0ea5e9', isSelected);
      case 'RAMA': return createBuoyIcon('RAMA', '#10b981', isSelected);
      case 'ARGO': return createBuoyIcon('ARGO', '#8b5cf6', isSelected);
      case 'MOORED': return createBuoyIcon('MOORED', '#f59e0b', isSelected);
      default: return createBuoyIcon('OMNI', '#0ea5e9', isSelected);
    }
  };

  const filteredStats = {
    total: filteredBuoys.length,
    active: filteredBuoys.filter(b => b.status === 'active').length,
    withData: filteredBuoys.filter(b => b.parameters && Object.keys(b.parameters).some(key => b.parameters[key] !== null)).length,
    networks: {
      ARGO: filteredBuoys.filter(b => b.type === 'ARGO').length,
      OMNI: filteredBuoys.filter(b => b.type === 'OMNI').length,
      RAMA: filteredBuoys.filter(b => b.type === 'RAMA').length,
      MOORED: filteredBuoys.filter(b => b.type === 'MOORED').length,
    },
    ramaReporting: {
      total: filteredBuoys.filter(b => b.type === 'RAMA').length,
      reporting: filteredBuoys.filter(b => b.type === 'RAMA' && b.reportingStatus && b.reportingStatus.toLowerCase().includes('reporting') && !b.reportingStatus.toLowerCase().includes('not')).length,
      notReporting: filteredBuoys.filter(b => b.type === 'RAMA' && (!b.reportingStatus || b.reportingStatus.toLowerCase().includes('not') || b.reportingStatus === 'Not Reporting')).length,
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-700 to-cyan-500 text-white">
      {/* Header */}
      <header className="p-6 text-center bg-gradient-to-r from-blue-600 via-cyan-500 to-blue-600 text-white font-extrabold text-3xl shadow-xl tracking-wide">
        🌊 INCOIS Ocean Observation Network Dashboard
      </header>

      {/* Error Display */}
      {error && (
        <div className="mx-6 mt-4 p-4 bg-red-500/20 border border-red-500/50 rounded-lg">
          <p className="text-red-200">⚠️ {error}</p>
        </div>
      )}

      {/* Enhanced Stats Dashboard */}
      <div className="grid grid-cols-2 md:grid-cols-6 gap-4 p-6">
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Active Buoys</h2>
          <p className="text-3xl font-bold text-green-400">{filteredStats.active}</p>
          <p className="text-xs text-gray-300">of {filteredStats.total} shown</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Total Shown</h2>
          <p className="text-3xl font-bold text-blue-400">{filteredStats.total}</p>
          <p className="text-xs text-gray-300">of {stats.totalBuoys} total</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">With Data</h2>
          <p className="text-3xl font-bold text-green-400">{filteredStats.withData}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">No Data</h2>
          <p className="text-3xl font-bold text-orange-400">{filteredStats.total - filteredStats.withData}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Avg Temp</h2>
          <p className="text-3xl font-bold text-red-400">{stats.avgTemperature || '0'}°C</p>
          <p className="text-xs text-gray-300">Sea Surface</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Networks</h2>
          <div className="text-sm space-y-1">
            <p className="text-green-300">RAMA: {filteredStats.networks.RAMA}</p>
            <p className="text-purple-300">ARGO: {filteredStats.networks.ARGO}</p>
            <p className="text-yellow-300">MOORED: {filteredStats.networks.MOORED}</p>
            <p className="text-blue-300">OMNI: {filteredStats.networks.OMNI}</p>
            {selectedNetwork === 'RAMA' && (
              <>
                <p className="text-cyan-300 text-xs">Reporting: {filteredStats.ramaReporting.reporting}</p>
                <p className="text-red-300 text-xs">Not Reporting: {filteredStats.ramaReporting.notReporting}</p>
              </>
            )}
          </div>
        </div>
      </div>

      {/* Controls */}
      <div className="px-6 mb-6">
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 shadow-lg">
          <div className="flex flex-col md:flex-row items-center justify-between gap-4">
            <div className="flex items-center gap-4">
              <h3 className="text-xl font-semibold">Data Controls</h3>
              <button
                onClick={() => setRealTimeMode(!realTimeMode)}
                className={`px-4 py-2 rounded-lg font-semibold transition-all ${
                  realTimeMode 
                    ? 'bg-green-500 hover:bg-green-600' 
                    : 'bg-gray-500 hover:bg-gray-600'
                }`}
              >
                {realTimeMode ? '🟢 Real-Time ON' : '⚪ Real-Time OFF'}
              </button>
              {showINCOISInterface && (
                <div className="text-sm px-3 py-1 rounded-lg bg-green-500/20 border border-green-500/30">
                  🌊 <span className="text-green-300">INCOIS Interface Active</span>
                </div>
              )}
            </div>
            <div className="flex items-center gap-4">
              <select
                value={selectedYear}
                onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                className="px-4 py-2 rounded-lg bg-white/10 border border-white/30 text-white backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
              >
                {yearRange.map(year => (
                  <option key={year} value={year} className="bg-blue-800">
                    {year}
                  </option>
                ))}
              </select>
              <button
                onClick={downloadCSV}
                className="px-6 py-2 bg-gradient-to-r from-green-500 to-blue-500 rounded-lg font-semibold hover:from-green-600 hover:to-blue-600 transition-all duration-300 shadow-lg"
              >
                📥 Download CSV
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Layout */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6">
        {/* Map Container */}
        <div className="bg-white/15 backdrop-blur-lg rounded-3xl shadow-2xl overflow-hidden border border-white/20">
          <div className="p-6 bg-gradient-to-r from-blue-500/30 to-purple-500/30 backdrop-blur-sm">
            <div className="flex items-center gap-3 mb-4">
              <div className="w-8 h-8 bg-blue-500 rounded-lg flex items-center justify-center">
                <span className="text-white text-sm">🗺️</span>
              </div>
              <h3 className="text-xl font-bold text-white">Ocean Buoy Locations</h3>
            </div>

            <div className="flex items-center gap-6 flex-wrap">
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-green-500 rounded-full border-2 border-white"></div>
                <span className="text-white font-medium">RAMA ({filteredStats.networks.RAMA})</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-purple-500 rounded-full border-2 border-white"></div>
                <span className="text-white font-medium">ARGO ({filteredStats.networks.ARGO})</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-orange-500 rounded-full border-2 border-white"></div>
                <span className="text-white font-medium">MOORED ({filteredStats.networks.MOORED})</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-blue-500 rounded-full border-2 border-white"></div>
                <span className="text-white font-medium">OMNI ({filteredStats.networks.OMNI})</span>
              </div>
            </div>
          </div>

          <div className="p-4">
            <div className="bg-gradient-to-r from-indigo-600/95 to-purple-600/95 backdrop-blur-lg rounded-2xl shadow-2xl border border-white/20">
              <div className="p-4">
                <div className="flex items-center gap-4 flex-wrap">
                  <div className="flex items-center gap-2">
                    <div className="w-3 h-3 bg-green-400 rounded-full shadow-lg"></div>
                    <span className="text-white font-semibold">Filters</span>
                  </div>

                  <div className="flex items-center gap-2">
                    <label className="text-white font-medium">Region:</label>
                    <select
                      value={selectedRegion}
                      onChange={(e) => setSelectedRegion(e.target.value)}
                      className="px-3 py-2 rounded-xl bg-white/20 border border-white/30 text-white font-medium backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-white/50 focus:bg-white/30 transition-all duration-200 min-w-[160px] shadow-lg"
                    >
                      <option value="all" className="bg-indigo-900 text-white">All Regions ({stats.totalBuoys})</option>
                      <option value="indian_ocean" className="bg-indigo-900 text-white">Indian Ocean</option>
                      <option value="bay_of_bengal" className="bg-indigo-900 text-white">Bay of Bengal</option>
                      <option value="arabian_sea" className="bg-indigo-900 text-white">Arabian Sea</option>
                      <option value="equatorial_indian" className="bg-indigo-900 text-white">Equatorial Indian Ocean</option>
                      <option value="southern_indian" className="bg-indigo-900 text-white">Southern Indian Ocean</option>
                    </select>
                  </div>

                  <div className="flex items-center gap-2">
                    <label className="text-white font-medium">Network:</label>
                    <select
                      value={selectedNetwork}
                      onChange={(e) => setSelectedNetwork(e.target.value)}
                      className="px-3 py-2 rounded-xl bg-white/20 border border-white/30 text-white font-medium backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-white/50 focus:bg-white/30 transition-all duration-200 min-w-[140px] shadow-lg"
                    >
                      <option value="all" className="bg-indigo-900 text-white">All Networks</option>
                      <option value="RAMA" className="bg-indigo-900 text-white">RAMA Buoys ({networks.RAMA})</option>
                      <option value="ARGO" className="bg-indigo-900 text-white">ARGO Floats ({networks.ARGO})</option>
                      <option value="MOORED" className="bg-indigo-900 text-white">Moored Buoys ({networks.MOORED})</option>
                      <option value="OMNI" className="bg-indigo-900 text-white">OMNI Buoys ({networks.OMNI})</option>
                    </select>
                  </div>

                  {selectedNetwork === 'RAMA' && (
                    <div className="flex items-center gap-2">
                      <label className="text-white font-medium">Reporting:</label>
                      <select
                        value={ramaReportingStatus}
                        onChange={(e) => setRamaReportingStatus(e.target.value)}
                        className="px-3 py-2 rounded-xl bg-green-500/20 border border-green-500/30 text-white font-medium backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-green-400/50 focus:bg-green-500/30 transition-all duration-200 min-w-[140px] shadow-lg"
                      >
                        <option value="all" className="bg-green-800 text-white">All Status</option>
                        <option value="reporting" className="bg-green-800 text-white">Reporting ({filteredStats.ramaReporting.reporting})</option>
                        <option value="not_reporting" className="bg-green-800 text-white">Not Reporting ({filteredStats.ramaReporting.notReporting})</option>
                      </select>
                    </div>
                  )}

                  <div className="flex items-center gap-2">
                    <label className="flex items-center gap-2 cursor-pointer">
                      <input
                        type="checkbox"
                        checked={showActiveOnly}
                        onChange={(e) => setShowActiveOnly(e.target.checked)}
                        className="w-4 h-4 text-green-400 bg-white/20 border-white/30 rounded focus:ring-white/50 focus:ring-2"
                      />
                      <span className="text-white font-medium">Active Only</span>
                    </label>
                  </div>

                  <button
                    onClick={clearFilters}
                    className="px-4 py-2 bg-white/20 hover:bg-white/30 border border-white/30 rounded-xl text-white font-medium transition-all duration-200 shadow-lg hover:shadow-xl hover:scale-105"
                  >
                    Clear Filters
                  </button>
                </div>
              </div>
            </div>
          </div>

          <div className="relative">
            <MapContainer
              center={[12, 77]}
              zoom={5}
              style={{ height: "500px", width: "100%" }}
            >
              <TileLayer
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {filteredBuoys.map((buoy, idx) => {
                const isSelected = selectedBuoy && selectedBuoy.id === buoy.id;
                return (
                  <Marker
                    key={`${buoy.type}-${buoy.id}-${idx}`}
                    position={[buoy.lat || 0, buoy.lon || 0]}
                    icon={getIconForBuoy(buoy, isSelected)}
                    eventHandlers={{
                      click: () => fetchBuoyDetails(buoy.id),
                    }}
                  >
                    <Popup>
                      <div className="text-black p-3 min-w-[280px]">
                        <strong className="text-lg block mb-2">{buoy.id}</strong>
                        <div className="space-y-1 text-sm">
                          <div><strong>Network:</strong> {buoy.type}</div>
                          {buoy.name && <div><strong>Name:</strong> {buoy.name}</div>}
                          {buoy.buoyId && <div><strong>Buoy ID:</strong> {buoy.buoyId}</div>}
                          <div><strong>Region:</strong> {getBuoyRegion(buoy)}</div>
                          <div><strong>Coordinates:</strong> {formatCoordinates(buoy)}</div>
                          <div><strong>Status:</strong> 
                            <span className={buoy.status === 'active' ? 'text-green-600 ml-1' : 'text-red-600 ml-1'}>
                              {buoy.status === 'active' ? '🟢 Active' : '🔴 Inactive'}
                            </span>
                          </div>
                          {buoy.reportingAgency && <div><strong>Agency:</strong> {buoy.reportingAgency}</div>}

                          {buoy.type === 'RAMA' && buoy.reportingStatus && (
                            <div><strong>Reporting:</strong> 
                              <span className={buoy.reportingStatus.toLowerCase().includes('reporting') && !buoy.reportingStatus.toLowerCase().includes('not') ? 'text-green-600 ml-1' : 'text-red-600 ml-1'}>
                                {buoy.reportingStatus}
                              </span>
                            </div>
                          )}

                          {getParameterValue(buoy, 'sst') !== 'N/A' && 
                            <div><strong>SST:</strong> {getParameterValue(buoy, 'sst')}°C</div>
                          }
                          {getParameterValue(buoy, 'salinity') !== 'N/A' && 
                            <div><strong>Salinity:</strong> {getParameterValue(buoy, 'salinity')} PSU</div>
                          }
                          {getParameterValue(buoy, 'windSpeed') !== 'N/A' && 
                            <div><strong>Wind:</strong> {getParameterValue(buoy, 'windSpeed')} m/s</div>
                          }

                          <div className="pt-2 border-t border-gray-300">
                            <span className="text-blue-600 cursor-pointer underline text-xs">
                              📊 Click for {buoy.type === 'MOORED' ? 'INCOIS-style interface' : 'complete analysis'}
                            </span>
                          </div>
                        </div>
                      </div>
                    </Popup>
                  </Marker>
                );
              })}
            </MapContainer>
          </div>
        </div>

        {/* Enhanced Buoy Details with INCOIS-style Interface */}
        <div className="bg-white/20 backdrop-blur-md rounded-2xl shadow-xl border border-white/30">
          {showINCOISInterface ? (
            /* INCOIS-Style Interface */
            <>
              <div className="p-4 bg-gradient-to-r from-green-500/20 to-blue-500/20 border-b border-white/20">
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="text-lg font-semibold text-white">🌊 INCOIS Ocean Data System</h3>
                    <p className="text-sm text-gray-300">
                      Buoy: <strong>{selectedBuoy?.buoyId}</strong> • Parameter: <strong>{selectedParameter}</strong>
                    </p>
                  </div>
                  <button
                    onClick={() => setShowINCOISInterface(false)}
                    className="text-red-400 hover:text-red-300 bg-red-500/20 px-3 py-1 rounded transition-colors"
                  >
                    ✕ Close
                  </button>
                </div>
              </div>

              {/* INCOIS Parameter & Zoom Controls */}
              <div className="p-4 bg-white/10 border-b border-white/20">
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-white font-medium mb-2">Parameter:</label>
                    <select
                      value={selectedParameter}
                      onChange={(e) => handleParameterChange(e.target.value)}
                      className="w-full px-3 py-2 rounded-lg bg-white/20 border border-white/30 text-white backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-blue-400 max-h-40 overflow-y-auto"
                      style={{ fontSize: '14px' }}
                    >
                      {incoisParameters.map(param => (
                        <option key={param} value={param} className="bg-blue-800 text-white py-1">
                          {param}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-white font-medium mb-2">Time Range:</label>
                    <div className="grid grid-cols-3 gap-2">
                      {zoomOptions.map(option => (
                        <button
                          key={option.value}
                          onClick={() => handleZoomChange(option.value)}
                          className={`px-3 py-2 text-sm rounded-lg font-medium transition-all duration-200 ${
                            zoomLevel === option.value
                              ? 'bg-blue-500 text-white shadow-lg'
                              : 'bg-white/20 text-white hover:bg-white/30'
                          }`}
                        >
                          {option.label}
                        </button>
                      ))}
                    </div>
                  </div>
                </div>

                {/* INCOIS-style action buttons */}
                <div className="flex justify-between items-center mt-4 pt-4 border-t border-white/20">
                  <div className="flex items-center gap-3">
                    <button
                      onClick={downloadChartData}
                      disabled={!chartData}
                      className="px-4 py-2 bg-green-500/20 hover:bg-green-500/30 disabled:bg-gray-500/20 border border-green-500/30 disabled:border-gray-500/30 rounded-lg text-white font-medium transition-all duration-200 shadow-lg disabled:cursor-not-allowed"
                    >
                      📥 Download Data
                    </button>
                    <button
                      onClick={() => setShowDataTable(!showDataTable)}
                      disabled={!dataTable.length}
                      className="px-4 py-2 bg-blue-500/20 hover:bg-blue-500/30 disabled:bg-gray-500/20 border border-blue-500/30 disabled:border-gray-500/30 rounded-lg text-white font-medium transition-all duration-200 shadow-lg disabled:cursor-not-allowed"
                    >
                      📊 {showDataTable ? 'Hide' : 'Show'} Table
                    </button>
                  </div>
                  <div className="text-sm text-gray-300">
                    {dataTable.length > 0 && `${dataTable.length} data points`}
                  </div>
                </div>
              </div>

              {/* INCOIS Chart Area */}
              <div className="p-6">
                {loadingChart ? (
                  <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-400 mx-auto"></div>
                    <p className="text-gray-200 mt-2">Loading INCOIS chart...</p>
                  </div>
                ) : chartError ? (
                  <div className="text-center py-8 text-red-400">
                    <p>❌ {chartError}</p>
                  </div>
                ) : chartData ? (
                  <div>
                    <div style={{ height: '400px' }} className="mb-6">
                      <Line
                        data={chartData}
                        options={{
                          responsive: true,
                          maintainAspectRatio: false,
                          interaction: {
                            intersect: false,
                            mode: 'index'
                          },
                          plugins: { 
                            legend: { 
                              labels: { color: 'white' },
                              display: true
                            },
                            title: { 
                              display: true, 
                              text: `${selectedBuoy?.buoyId} - ${selectedParameter} (${zoomLevel})`, 
                              color: 'white',
                              font: { size: 16 }
                            },
                            tooltip: {
                              backgroundColor: 'rgba(0, 0, 0, 0.8)',
                              titleColor: 'white',
                              bodyColor: 'white',
                              borderColor: chartData.datasets[0].borderColor,
                              borderWidth: 1,
                              callbacks: {
                                title: function(context) {
                                  return new Date(context[0].parsed.x).toLocaleString();
                                },
                                label: function(context) {
                                  const unit = chartData.datasets[0].label.match(/\(([^)]+)\)/)?.[1] || '';
                                  return `${selectedParameter}: ${context.parsed.y.toFixed(3)} ${unit}`;
                                }
                              }
                            }
                          },
                          scales: {
                            x: {
                              type: 'time',
                              title: { display: true, text: 'Date & Time', color: 'white' },
                              ticks: { 
                                color: 'white',
                                maxTicksLimit: 8
                              },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                            y: { 
                              title: { 
                                display: true, 
                                text: chartData.datasets[0].label, 
                                color: 'white' 
                              },
                              ticks: { 
                                color: 'white',
                                callback: function(value) {
                                  return value.toFixed(2);
                                }
                              },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                          },
                        }}
                      />
                    </div>

                    {/* Data Table */}
                    {showDataTable && dataTable.length > 0 && (
                      <div className="bg-white/10 rounded-lg p-4 max-h-64 overflow-y-auto">
                        <h4 className="text-white font-semibold mb-3">📊 Data Table</h4>
                        <div className="overflow-x-auto">
                          <table className="w-full text-sm text-white">
                            <thead>
                              <tr className="border-b border-white/20">
                                <th className="text-left p-2">Date</th>
                                <th className="text-left p-2">Time</th>
                                <th className="text-right p-2">Value</th>
                                <th className="text-left p-2">Unit</th>
                              </tr>
                            </thead>
                            <tbody>
                              {dataTable.slice(0, 50).map((row, index) => (
                                <tr key={index} className="border-b border-white/10 hover:bg-white/5">
                                  <td className="p-2">{row.date}</td>
                                  <td className="p-2">{row.time}</td>
                                  <td className="p-2 text-right font-mono">{row.value}</td>
                                  <td className="p-2 text-gray-300">{row.unit}</td>
                                </tr>
                              ))}
                            </tbody>
                          </table>
                          {dataTable.length > 50 && (
                            <p className="text-xs text-gray-400 mt-2 text-center">
                              Showing first 50 of {dataTable.length} records
                            </p>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <p className="text-gray-300">Select a parameter to view chart</p>
                  </div>
                )}
              </div>
            </>
          ) : (
            /* Regular Buoy Details */
            <>
              <div className="p-4 bg-white/10 border-b border-white/20 flex justify-between items-center">
                <h3 className="text-lg font-semibold">📊 Buoy Analysis</h3>
                {realTimeMode && selectedBuoy && (
                  <span className="text-green-400 text-sm animate-pulse">🔴 LIVE</span>
                )}
              </div>

              <div className="p-6 max-h-[600px] overflow-y-auto">
                {loading ? (
                  <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white mx-auto"></div>
                    <p className="text-gray-200 mt-2">Loading buoy data...</p>
                  </div>
                ) : selectedBuoy ? (
                  <div className="space-y-6">

                    {/* Basic Buoy Information */}
                    <div className="bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-lg p-4 border border-blue-500/20">
                      <h4 className="text-xl font-semibold mb-3 text-yellow-300">
                        {selectedBuoy.id} ({selectedBuoy.type} Network)
                      </h4>

                      {selectedBuoy.name && (
                        <p className="text-cyan-300 mb-3 font-medium text-lg">📍 {selectedBuoy.name}</p>
                      )}

                      <div className="bg-white/10 rounded-lg p-3 mb-3">
                        <p className="text-gray-300 font-semibold">📍 Location Details</p>
                        <p className="text-white">Coordinates: {formatCoordinates(selectedBuoy)}</p>
                        <p className="text-white">Region: {getBuoyRegion(selectedBuoy)}</p>
                      </div>

                      <div className="bg-white/10 rounded-lg p-3">
                        <p className="text-gray-300 font-semibold">🏢 Organization Info</p>
                        <p className="text-white">Status: 
                          <span className={selectedBuoy.status === 'active' ? 'text-green-400 ml-1' : 'text-red-400 ml-1'}>
                            {selectedBuoy.status === 'active' ? '🟢 Active' : '🔴 Inactive'}
                          </span>
                        </p>
                        {selectedBuoy.buoyId && (
                          <p className="text-white">Buoy ID: <span className="text-cyan-300">{selectedBuoy.buoyId}</span></p>
                        )}
                        {selectedBuoy.reportingAgency && (
                          <p className="text-white">Agency: <span className="text-cyan-300">{selectedBuoy.reportingAgency}</span></p>
                        )}
                        <p className="text-white">Data Source: <span className="text-green-300">{selectedBuoy.dataSource || 'INCOIS'}</span></p>

                        {/* INCOIS Interface Button for MOORED buoys */}
                        {selectedBuoy.type === 'MOORED' && (
                          <div className="mt-3">
                            <button
                              onClick={() => {
                                setShowINCOISInterface(true);
                                loadINCOISData(selectedBuoy.buoyId, selectedParameter, zoomLevel);
                              }}
                              className="w-full px-4 py-2 bg-gradient-to-r from-green-500 to-blue-500 rounded-lg font-semibold hover:from-green-600 hover:to-blue-600 transition-all duration-300 shadow-lg"
                            >
                              🌊 Open INCOIS Interface
                            </button>
                          </div>
                        )}
                      </div>
                    </div>

                    {/* Current Parameters */}
                    {selectedBuoy.parameters && Object.keys(selectedBuoy.parameters).length > 0 ? (
                      <div>
                        <h5 className="text-lg font-semibold mb-4 text-cyan-300">🌊 Current Measurements</h5>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">

                          {Object.entries(selectedBuoy.parameters).map(([key, value]) => {
                            if (value === null || value === undefined) return null;

                            const parameterInfo = {
                              'sst': { label: 'Sea Surface Temperature', unit: '°C', color: 'text-red-400' },
                              'seaSurfaceTemperature': { label: 'Sea Surface Temperature', unit: '°C', color: 'text-red-400' },
                              'salinity': { label: 'Salinity', unit: 'PSU', color: 'text-blue-400' },
                              'windSpeed': { label: 'Wind Speed', unit: 'm/s', color: 'text-green-400' },
                              'windspeed': { label: 'Wind Speed', unit: 'm/s', color: 'text-green-400' },
                              'windDirection': { label: 'Wind Direction', unit: '°', color: 'text-cyan-400' },
                              'winddirection': { label: 'Wind Direction', unit: '°', color: 'text-cyan-400' },
                              'waveHeight': { label: 'Wave Height', unit: 'm', color: 'text-purple-400' },
                              'waveheight': { label: 'Wave Height', unit: 'm', color: 'text-purple-400' },
                              'pressure': { label: 'Pressure', unit: 'hPa', color: 'text-orange-400' },
                              'airTemperature': { label: 'Air Temperature', unit: '°C', color: 'text-pink-400' },
                            };

                            const info = parameterInfo[key] || { 
                              label: key.charAt(0).toUpperCase() + key.slice(1), 
                              unit: '', 
                              color: 'text-gray-400' 
                            };

                            return (
                              <div key={key} className="bg-white/10 rounded-lg p-4 hover:bg-white/15 transition-colors">
                                <p className="text-gray-300 text-xs uppercase tracking-wide">{info.label}</p>
                                <p className={`text-2xl font-bold ${info.color} mt-1`}>
                                  {value} {info.unit}
                                </p>
                              </div>
                            );
                          })}
                        </div>
                      </div>
                    ) : (
                      <div className="text-center py-8 bg-white/10 rounded-lg">
                        <p className="text-orange-300 text-lg">📊 No current measurement data available</p>
                        <p className="text-gray-400 text-sm mt-2">
                          {selectedBuoy.type === 'MOORED' 
                            ? 'Use the INCOIS interface button above to view detailed time series data'
                            : `This ${selectedBuoy.type} buoy is registered but real-time parameters are not currently accessible`
                          }
                        </p>
                      </div>
                    )}

                    {/* RAMA Properties */}
                    {selectedBuoy.type === 'RAMA' && selectedBuoy.properties && (
                      <div className="bg-gradient-to-r from-green-500/10 to-cyan-500/10 rounded-lg p-4 border border-green-500/20">
                        <h5 className="text-lg font-semibold mb-3 text-green-300">🌊 RAMA Network Properties</h5>
                        <div className="grid grid-cols-1 gap-3">
                          {Object.entries(selectedBuoy.properties).map(([key, value]) => (
                            <div key={key} className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                              <div className="flex justify-between items-center">
                                <span className="text-gray-300 font-medium">{formatPropertyName(key)}:</span>
                                <span className="text-white font-semibold">
                                  {key === 'URL' ? (
                                    <a 
                                      href={value} 
                                      target="_blank" 
                                      rel="noopener noreferrer"
                                      className="text-cyan-400 hover:text-cyan-300 underline break-all text-sm"
                                    >
                                      {value}
                                    </a>
                                  ) : key === 'Reporting' ? (
                                    <span className={value && value.toLowerCase().includes('reporting') && !value.toLowerCase().includes('not') ? 'text-green-400' : 'text-red-400'}>
                                      {String(value || 'Not Reporting')}
                                    </span>
                                  ) : (
                                    String(value || 'N/A')
                                  )}
                                </span>
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Temperature and Salinity Profiles for ARGO */}
                    {tempChartData && (
                      <div>
                        <h5 className="text-lg font-semibold mb-3 text-red-300">Temperature Profile</h5>
                        <div style={{ height: '200px' }}>
                          <Line
                            data={tempChartData}
                            options={{
                              responsive: true,
                              maintainAspectRatio: false,
                              plugins: { 
                                legend: { labels: { color: 'white' } },
                                title: { display: true, text: 'Temperature vs Depth', color: 'white' }
                              },
                              scales: {
                                x: { 
                                  title: { display: true, text: 'Depth', color: 'white' },
                                  ticks: { color: 'white' },
                                  grid: { color: 'rgba(255,255,255,0.1)' }
                                },
                                y: { 
                                  title: { display: true, text: 'Temperature (°C)', color: 'white' },
                                  ticks: { color: 'white' },
                                  grid: { color: 'rgba(255,255,255,0.1)' }
                                },
                              },
                            }}
                          />
                        </div>
                      </div>
                    )}

                    {salinityChartData && (
                      <div>
                        <h5 className="text-lg font-semibold mb-3 text-blue-300">Salinity Profile</h5>
                        <div style={{ height: '200px' }}>
                          <Line
                            data={salinityChartData}
                            options={{
                              responsive: true,
                              maintainAspectRatio: false,
                              plugins: { 
                                legend: { labels: { color: 'white' } },
                                title: { display: true, text: 'Salinity vs Depth', color: 'white' }
                              },
                              scales: {
                                x: { 
                                  title: { display: true, text: 'Depth', color: 'white' },
                                  ticks: { color: 'white' },
                                  grid: { color: 'rgba(255,255,255,0.1)' }
                                },
                                y: { 
                                  title: { display: true, text: 'Salinity (PSU)', color: 'white' },
                                  ticks: { color: 'white' },
                                  grid: { color: 'rgba(255,255,255,0.1)' }
                                },
                              },
                            }}
                          />
                        </div>
                      </div>
                    )}

                    {/* Data Quality Footer */}
                    <div className="p-4 bg-gradient-to-r from-green-500/10 to-blue-500/10 rounded-lg border border-green-500/20">
                      <div className="flex items-center gap-2 mb-2">
                        <span className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></span>
                        <span className="text-green-300 font-semibold">
                          {selectedBuoy.type === 'MOORED' ? 'INCOIS-Style Interface Available' : 'INCOIS Ocean Data Network'}
                        </span>
                      </div>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-3 text-xs text-gray-300">
                        <div>
                          🌊 <strong>Data Source:</strong> {selectedBuoy.dataSource || 'INCOIS'}
                          <br />
                          📡 <strong>Network:</strong> {selectedBuoy.type}
                          <br />
                          {selectedBuoy.type === 'MOORED' && (
                            <>🎯 <strong>Interface:</strong> Full INCOIS recreation<br /></>
                          )}
                        </div>
                        <div>
                          📍 <strong>Precision:</strong> 6 decimal places
                          <br />
                          ⏰ <strong>Status:</strong> {selectedBuoy.status}
                          <br />
                          {selectedBuoy.type === 'MOORED' && (
                            <>🔗 <strong>Features:</strong> Charts, zoom, download<br /></>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <p className="text-gray-300 text-lg mb-4">
                      🔍 Click on any buoy marker to view data analysis
                    </p>
                    <div className="bg-white/10 rounded-lg p-4">
                      <h6 className="font-semibold mb-2">🌊 INCOIS Features:</h6>
                      <div className="text-sm text-gray-300 space-y-1">
                        <p>• <strong>MOORED Buoys:</strong> Complete INCOIS-style interface</p>
                        <p>• <strong>50+ Parameters:</strong> All oceanographic measurements</p>
                        <p>• <strong>Interactive Charts:</strong> Zoom, pan, download functionality</p>
                        <p>• <strong>Data Tables:</strong> Raw data viewing and export</p>
                        <p>• <strong>Time Controls:</strong> 1W, 1M, 3M, 6M, 1Y, All data</p>
                      </div>
                      <div className="mt-3 pt-2 border-t border-white/20">
                        <p className="text-sm text-cyan-300 font-medium">
                          📊 {filteredStats.total} buoys • {filteredStats.networks.MOORED} MOORED with full INCOIS interface
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
