// BuoyMap.js
import { useEffect, useState } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix for missing Leaflet marker icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

function BuoyMap() {
  const [buoys, setBuoys] = useState([]);
  const [filteredBuoys, setFilteredBuoys] = useState([]);
  const [selectedBuoy, setSelectedBuoy] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Filter states
  const [selectedRegion, setSelectedRegion] = useState('all');
  const [selectedTimeRange, setSelectedTimeRange] = useState('all');
  const [selectedDepthRange, setSelectedDepthRange] = useState('all');

  // INCOIS interface states for MOORED buoys
  const [showINCOISInterface, setShowINCOISInterface] = useState(false);
  const [selectedParameter, setSelectedParameter] = useState('Air Temperature');
  const [incoisProxyUrl, setIncoisProxyUrl] = useState('');
  const [loadingProxy, setLoadingProxy] = useState(false);
  const [proxyError, setProxyError] = useState(null);

  // Regional boundaries for filtering
  const regions = {
    'indian_ocean': {
      name: 'Indian Ocean',
      bounds: { north: 30, south: -60, east: 100, west: 40 }
    },
    'bay_of_bengal': {
      name: 'Bay of Bengal',
      bounds: { north: 25, south: 5, east: 95, west: 80 }
    },
    'arabian_sea': {
      name: 'Arabian Sea',
      bounds: { north: 30, south: 0, east: 80, west: 50 }
    },
    'equatorial_indian': {
      name: 'Equatorial Indian Ocean',
      bounds: { north: 10, south: -10, east: 100, west: 50 }
    },
    'southern_indian': {
      name: 'Southern Indian Ocean',
      bounds: { north: -10, south: -60, east: 120, west: 40 }
    }
  };

  // Time range options
  const timeRangeOptions = [
    { value: 'all', label: 'All Time' },
    { value: '24h', label: 'Last 24 Hours' },
    { value: '7d', label: 'Last 7 Days' },
    { value: '30d', label: 'Last 30 Days' },
    { value: '1y', label: 'Last Year' }
  ];

  // Depth range options
  const depthRangeOptions = [
    { value: 'all', label: 'All Depths' },
    { value: 'surface', label: 'Surface (0-10m)' },
    { value: 'shallow', label: 'Shallow (10-50m)' },
    { value: 'mid', label: 'Mid Water (50-200m)' },
    { value: 'deep', label: 'Deep (200m+)' }
  ];

  // INCOIS Parameters
  const incoisParameters = [
    'Air Pressure', 'Air Temperature', 'Relative Humidity', 'Rainfall',
    'Wind Direction', 'Wind Speed', 'Wind Gust', 'Irradiance', 'Radiation In',
    'Current Direction @ 1.20m', 'Current Speed @ 1.20m',
    'Water Temperature @ 0.5m', 'Water Temperature @ 001m',
    'Salinity @ 0.5m', 'Salinity @ 001m',
    'Significant Wave Height'
  ];

  // Helper function to normalize RAMA GeoJSON features to buoy format
  const normalizeRamaBuoys = (ramaGeoJSON) => {
    if (!ramaGeoJSON || !ramaGeoJSON.features) return [];
    
    return ramaGeoJSON.features.map((feature, index) => ({
      id: `RAMA_${index + 1}`,
      buoyId: feature.properties.buoyId || `RAMA_${index + 1}`,
      name: feature.properties.type 
        ? `${feature.properties.type} Buoy ${feature.properties.buoyId || index + 1}` 
        : `RAMA Buoy ${index + 1}`,
      lat: feature.geometry.coordinates[1],
      lon: feature.geometry.coordinates[0],
      coordinates: feature.geometry.coordinates,
      type: "RAMA",
      status: "active",
      reportingAgency: feature.properties.reportingAgency || "INCOIS",
      url: feature.properties.url || null,
      reportingStatus: feature.properties.Reporting || feature.properties.reporting || 'Not Reporting',
      properties: feature.properties,
      parameters: {
        sst: feature.properties.sst || null,
        seaSurfaceTemperature: feature.properties.sst || null,
        salinity: feature.properties.salinity || null,
        windSpeed: feature.properties.windspeed || null,
        windDirection: feature.properties.winddirection || null,
        pressure: feature.properties.pressure || null,
        waveHeight: feature.properties.waveheight || null,
      },
      dataSource: "INCOIS",
    }));
  };

  // Fetch ocean buoys data
  useEffect(() => {
    const fetchBuoys = async () => {
      try {
        setError(null);
        setLoading(true);
        console.log("🔄 Fetching buoys from server...");
        const response = await axios.get("http://localhost:5000/api/ocean-buoys");
        
        if (response.data && response.data.success) {
          const argoBuoys = response.data.argo || [];
          const mooredBuoys = response.data.moored || [];
          const ramaBuoys = normalizeRamaBuoys(response.data.rama);
          
          const allBuoys = [...argoBuoys, ...mooredBuoys, ...ramaBuoys];
          setBuoys(allBuoys);
          console.log(`📊 Total buoys loaded: ${allBuoys.length}`);
        } else {
          setError("No data received from server");
        }
      } catch (err) {
        console.error("❌ Error fetching buoys:", err);
        setError(`Failed to fetch buoy data: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchBuoys();
  }, []);

  // Filter buoys based on selected criteria
  useEffect(() => {
    let filtered = [...buoys];

    if (selectedRegion !== 'all') {
      const region = regions[selectedRegion];
      if (region) {
        filtered = filtered.filter(buoy => {
          const lat = parseFloat(buoy.lat);
          const lon = parseFloat(buoy.lon);
          return lat <= region.bounds.north && 
                 lat >= region.bounds.south && 
                 lon <= region.bounds.east && 
                 lon >= region.bounds.west;
        });
      }
    }

    setFilteredBuoys(filtered);
  }, [buoys, selectedRegion, selectedTimeRange, selectedDepthRange]);

  // Handle buoy selection
  const handleBuoyClick = async (buoyId) => {
    setLoading(true);
    setError(null);
    setShowINCOISInterface(false);
    
    try {
      console.log(`🔍 Fetching details for buoy: ${buoyId}`);
      
      if (buoyId.startsWith('RAMA_')) {
        const ramaBuoy = buoys.find(b => b.id === buoyId && b.type === 'RAMA');
        if (ramaBuoy) {
          setSelectedBuoy(ramaBuoy);
        } else {
          setError(`RAMA buoy ${buoyId} not found`);
        }
      } else if (buoyId.startsWith('MOORED_')) {
        const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
        if (res.data && res.data.success && res.data.data) {
          const mooredBuoy = res.data.data;
          setSelectedBuoy(mooredBuoy);
          setShowINCOISInterface(true);
          await loadINCOISProxy(mooredBuoy.buoyId, selectedParameter);
        } else {
          setError(`No data available for moored buoy ${buoyId}`);
        }
      } else {
        const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
        if (res.data && res.data.success && res.data.data) {
          setSelectedBuoy(res.data.data);
        } else {
          setError(`No data available for buoy ${buoyId}`);
        }
      }
    } catch (err) {
      console.error("Error fetching buoy details:", err);
      setError(`Failed to fetch details for buoy ${buoyId}: ${err.message}`);
    }
    setLoading(false);
  };

  // Load INCOIS Proxy
  const loadINCOISProxy = async (buoyId, parameter) => {
    setLoadingProxy(true);
    setProxyError(null);
    
    try {
      const response = await axios.get(`http://localhost:5000/api/incois-url/${buoyId}/${parameter}`);
      
      if (response.data && response.data.success) {
        const proxyUrl = response.data.proxyUrl;
        const fullProxyUrl = `http://localhost:5000${proxyUrl}`;
        setIncoisProxyUrl(fullProxyUrl);
      } else {
        setProxyError(`Failed to generate INCOIS proxy URL`);
      }
    } catch (error) {
      setProxyError(`Error loading INCOIS proxy: ${error.message}`);
    }
    
    setLoadingProxy(false);
  };

  // Apply filters
  const handleApplyFilters = () => {
    console.log('🔍 Applying filters:', {
      region: selectedRegion,
      timeRange: selectedTimeRange,
      depthRange: selectedDepthRange
    });
    // Filters are already applied via useEffect, but you can add additional logic here
  };

  // Create buoy icons
  const createBuoyIcon = (type, color, isSelected = false) => {
    const size = isSelected ? 32 : 28;
    const strokeWidth = isSelected ? 3 : 2;
    
    return new L.Icon({
      iconUrl: 'data:image/svg+xml;base64,' + btoa(`
        <svg width="${size}" height="${size}" viewBox="0 0 ${size} ${size}" fill="none" xmlns="http://www.w3.org/2000/svg">
          <circle cx="${size/2}" cy="${size/2}" r="${size/2 - 2}" fill="${color}" stroke="#ffffff" stroke-width="${strokeWidth}"/>
          <text x="${size/2}" y="${size/2 + 4}" text-anchor="middle" fill="white" font-size="10" font-weight="bold">${type.charAt(0)}</text>
          ${isSelected ? `<circle cx="${size/2}" cy="${size/2}" r="${size/2 - 1}" fill="none" stroke="#ffff00" stroke-width="2" opacity="0.8"/>` : ''}
        </svg>
      `),
      iconSize: [size, size],
      iconAnchor: [size/2, size/2],
      popupAnchor: [0, -size/2]
    });
  };

  const getIconForBuoy = (buoy, isSelected = false) => {
    switch (buoy.type) {
      case 'OMNI': return createBuoyIcon('OMNI', '#0ea5e9', isSelected);
      case 'RAMA': return createBuoyIcon('RAMA', '#10b981', isSelected);
      case 'ARGO': return createBuoyIcon('ARGO', '#8b5cf6', isSelected);
      case 'MOORED': return createBuoyIcon('MOORED', '#f59e0b', isSelected);
      default: return createBuoyIcon('OMNI', '#0ea5e9', isSelected);
    }
  };

  const filteredStats = {
    total: filteredBuoys.length,
    active: filteredBuoys.filter(b => b.status === 'active').length,
    withData: filteredBuoys.filter(b => b.parameters && Object.keys(b.parameters).some(key => b.parameters[key] !== null)).length,
    networks: {
      ARGO: filteredBuoys.filter(b => b.type === 'ARGO').length,
      OMNI: filteredBuoys.filter(b => b.type === 'OMNI').length,
      RAMA: filteredBuoys.filter(b => b.type === 'RAMA').length,
      MOORED: filteredBuoys.filter(b => b.type === 'MOORED').length,
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        
        {/* Error Display */}
        {error && (
          <div className="mb-6 p-4 bg-red-500/20 border border-red-500/50 rounded-lg">
            <p className="text-red-200">⚠️ {error}</p>
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          
          {/* Data Filters Sidebar */}
          <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6 h-fit">
            <div className="flex items-center mb-4">
              <svg className="w-5 h-5 text-cyan-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.414A1 1 0 013 6.707V4z"/>
              </svg>
              <h2 className="text-lg font-semibold text-white">Data Filters</h2>
            </div>

            <div className="space-y-4">
              {/* Region Filter */}
              <div>
                <label className="flex items-center text-sm font-medium text-slate-300 mb-2">
                  <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"/>
                  </svg>
                  Region
                </label>
                <select 
                  value={selectedRegion}
                  onChange={(e) => setSelectedRegion(e.target.value)}
                  className="w-full bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-sm text-white focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
                >
                  <option value="all">Select region</option>
                  <option value="indian_ocean">Indian Ocean</option>
                  <option value="bay_of_bengal">Bay of Bengal</option>
                  <option value="arabian_sea">Arabian Sea</option>
                  <option value="equatorial_indian">Equatorial Indian Ocean</option>
                  <option value="southern_indian">Southern Indian Ocean</option>
                </select>
              </div>

              {/* Time Range Filter */}
              <div>
                <label className="flex items-center text-sm font-medium text-slate-300 mb-2">
                  <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"/>
                  </svg>
                  Time Range
                </label>
                <select 
                  value={selectedTimeRange}
                  onChange={(e) => setSelectedTimeRange(e.target.value)}
                  className="w-full bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-sm text-white focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
                >
                  {timeRangeOptions.map(option => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
              </div>

              {/* Depth Range Filter */}
              <div>
                <label className="flex items-center text-sm font-medium text-slate-300 mb-2">
                  <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 14l-7 7m0 0l-7-7m7 7V3"/>
                  </svg>
                  Depth Range
                </label>
                <select 
                  value={selectedDepthRange}
                  onChange={(e) => setSelectedDepthRange(e.target.value)}
                  className="w-full bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-sm text-white focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
                >
                  {depthRangeOptions.map(option => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                  ))}
                </select>
              </div>

              <button 
                onClick={handleApplyFilters}
                className="w-full bg-cyan-600 hover:bg-cyan-700 text-white rounded-lg px-4 py-2 text-sm font-medium transition-colors"
              >
                Apply Filters
              </button>
            </div>
          </div>

          {/* Map and Network Summary */}
          <div className="lg:col-span-3 space-y-6">
            
            {/* Ocean Buoy Network Map */}
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 overflow-hidden">
              <div className="p-4 border-b border-slate-700/50">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <svg className="w-5 h-5 text-cyan-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"/>
                    </svg>
                    <h3 className="text-lg font-semibold text-white">Ocean Buoy Network</h3>
                  </div>
                  <div className="flex space-x-4">
                    <div className="flex items-center text-xs text-green-400">
                      <div className="w-2 h-2 bg-green-400 rounded-full mr-1"></div>
                      RAMA ({filteredStats.networks.RAMA})
                    </div>
                    <div className="flex items-center text-xs text-purple-400">
                      <div className="w-2 h-2 bg-purple-400 rounded-full mr-1"></div>
                      ARGO ({filteredStats.networks.ARGO})
                    </div>
                    <div className="flex items-center text-xs text-orange-400">
                      <div className="w-2 h-2 bg-orange-400 rounded-full mr-1"></div>
                      MOORED ({filteredStats.networks.MOORED})
                    </div>
                  </div>
                </div>
              </div>
              
              <div className="relative h-96">
                {loading ? (
                  <div className="absolute inset-0 flex items-center justify-center bg-slate-700/50">
                    <div className="text-center">
                      <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-cyan-400 mx-auto"></div>
                      <p className="text-slate-300 mt-2">Loading buoy data...</p>
                    </div>
                  </div>
                ) : (
                  <MapContainer
                    center={[12, 77]}
                    zoom={5}
                    style={{ height: "100%", width: "100%" }}
                    className="rounded-b-2xl"
                  >
                    <TileLayer
                      attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
                      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {filteredBuoys.map((buoy, idx) => {
                      const isSelected = selectedBuoy && selectedBuoy.id === buoy.id;
                      return (
                        <Marker
                          key={`${buoy.type}-${buoy.id}-${idx}`}
                          position={[buoy.lat || 0, buoy.lon || 0]}
                          icon={getIconForBuoy(buoy, isSelected)}
                          eventHandlers={{
                            click: () => handleBuoyClick(buoy.id),
                          }}
                        >
                          <Popup>
                            <div className="text-black p-3 min-w-[240px]">
                              <strong className="text-lg block mb-2">{buoy.id}</strong>
                              <div className="space-y-1 text-sm">
                                <div><strong>Network:</strong> {buoy.type}</div>
                                <div><strong>Status:</strong> 
                                  <span className={buoy.status === 'active' ? 'text-green-600 ml-1' : 'text-red-600 ml-1'}>
                                    {buoy.status === 'active' ? '🟢 Active' : '🔴 Inactive'}
                                  </span>
                                </div>
                                <div><strong>Coordinates:</strong> {buoy.lat?.toFixed(4)}°N, {buoy.lon?.toFixed(4)}°E</div>
                                {buoy.type === 'MOORED' && (
                                  <div className="pt-2 border-t border-gray-300">
                                    <span className="text-blue-600 cursor-pointer underline text-xs">
                                      📊 Click for REAL INCOIS data interface!
                                    </span>
                                  </div>
                                )}
                              </div>
                            </div>
                          </Popup>
                        </Marker>
                      );
                    })}
                  </MapContainer>
                )}
              </div>
            </div>

            {/* Network Summary */}
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6">
              <h3 className="text-lg font-semibold text-white mb-4 flex items-center">
                <svg className="w-5 h-5 text-cyan-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"/>
                </svg>
                Network Summary
              </h3>
              
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-white mb-1">{filteredStats.total}</div>
                  <div className="text-sm text-slate-400">Total Buoys</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-400 mb-1">{filteredStats.active}</div>
                  <div className="text-sm text-slate-400">Active</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-cyan-400 mb-1">{filteredStats.withData}</div>
                  <div className="text-sm text-slate-400">With Data</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-orange-400 mb-1">{filteredStats.total - filteredStats.withData}</div>
                  <div className="text-sm text-slate-400">No Data</div>
                </div>
              </div>
              
              <hr className="border-slate-700 my-4" />
              
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <div className="text-xl font-bold text-green-400 mb-1">{filteredStats.networks.RAMA}</div>
                  <div className="text-xs text-green-400">RAMA</div>
                </div>
                <div className="text-center">
                  <div className="text-xl font-bold text-purple-400 mb-1">{filteredStats.networks.ARGO}</div>
                  <div className="text-xs text-purple-400">ARGO</div>
                </div>
                <div className="text-center">
                  <div className="text-xl font-bold text-orange-400 mb-1">{filteredStats.networks.MOORED}</div>
                  <div className="text-xs text-orange-400">MOORED</div>
                </div>
                <div className="text-center">
                  <div className="text-xl font-bold text-blue-400 mb-1">{filteredStats.networks.OMNI}</div>
                  <div className="text-xs text-blue-400">OMNI</div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* INCOIS Interface - when MOORED buoy is selected */}
        {selectedBuoy && selectedBuoy.type === 'MOORED' && showINCOISInterface && (
          <div className="mt-6">
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50">
              <div className="p-4 border-b border-slate-700/50">
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="text-lg font-semibold text-white flex items-center">
                      <svg className="w-5 h-5 text-green-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"/>
                      </svg>
                      REAL INCOIS Ocean Data System
                    </h3>
                    <p className="text-sm text-slate-400">
                      Buoy: <strong>{selectedBuoy?.buoyId}</strong> • Parameter: <strong>{selectedParameter}</strong>
                    </p>
                  </div>
                  <button
                    onClick={() => setShowINCOISInterface(false)}
                    className="text-slate-400 hover:text-white transition-colors"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"/>
                    </svg>
                  </button>
                </div>
              </div>

              {/* Parameter Controls */}
              <div className="p-4 border-b border-slate-700/50">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 items-end">
                  <div>
                    <label className="block text-sm font-medium text-slate-300 mb-2">Parameter:</label>
                    <select
                      value={selectedParameter}
                      onChange={(e) => setSelectedParameter(e.target.value)}
                      className="w-full bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-sm text-white focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
                    >
                      {incoisParameters.map(param => (
                        <option key={param} value={param}>{param}</option>
                      ))}
                    </select>
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-slate-300 mb-2">Time Range:</label>
                    <select className="w-full bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-sm text-white focus:ring-2 focus:ring-cyan-400 focus:border-transparent">
                      <option value="1W">1 Week</option>
                      <option value="1M">1 Month</option>
                      <option value="3M">3 Months</option>
                      <option value="6M">6 Months</option>
                      <option value="1Y">1 Year</option>
                    </select>
                  </div>

                  <div className="flex justify-end">
                    <button
                      onClick={() => loadINCOISProxy(selectedBuoy.buoyId, selectedParameter)}
                      disabled={loadingProxy}
                      className="bg-cyan-600 hover:bg-cyan-700 disabled:bg-slate-600 disabled:cursor-not-allowed text-white rounded-lg px-6 py-2 text-sm font-medium transition-colors"
                    >
                      {loadingProxy ? 'Loading...' : 'Submit'}
                    </button>
                  </div>
                </div>
              </div>

              {/* INCOIS Proxy Display */}
              <div className="p-6">
                {loadingProxy ? (
                  <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-cyan-400 mx-auto"></div>
                    <p className="text-slate-300 mt-2">Loading REAL INCOIS interface...</p>
                  </div>
                ) : proxyError ? (
                  <div className="text-center py-8">
                    <div className="bg-red-500/10 border border-red-500/20 rounded-lg p-6">
                      <p className="text-red-400 text-lg">❌ {proxyError}</p>
                      <button
                        onClick={() => loadINCOISProxy(selectedBuoy.buoyId, selectedParameter)}
                        className="mt-4 bg-green-600 hover:bg-green-700 text-white rounded-lg px-4 py-2 text-sm font-medium transition-colors"
                      >
                        🔄 Retry
                      </button>
                    </div>
                  </div>
                ) : incoisProxyUrl ? (
                  <div>
                    <div className="mb-4 p-3 bg-green-500/10 border border-green-500/20 rounded-lg">
                      <h4 className="text-green-400 font-semibold">🌊 REAL INCOIS Interface Active</h4>
                      <p className="text-sm text-slate-300">
                        Direct access to INCOIS system • Parameter: <strong>{selectedParameter}</strong>
                      </p>
                    </div>
                    
                    <div className="bg-white rounded-lg overflow-hidden" style={{ height: '600px' }}>
                      <iframe
                        key={`${incoisProxyUrl}-${Date.now()}`}
                        src={incoisProxyUrl}
                        width="100%"
                        height="100%"
                        title={`INCOIS Data for ${selectedBuoy.buoyId} - ${selectedParameter}`}
                        frameBorder="0"
                        className="w-full h-full"
                        allow="fullscreen"
                      />
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <div className="bg-cyan-500/10 border border-cyan-500/20 rounded-lg p-6">
                      <h4 className="text-cyan-400 font-semibold mb-3">🌊 Ready to Load INCOIS Data</h4>
                      <p className="text-slate-300 mb-4">
                        Select a parameter above and click <strong>"Submit"</strong> to load the real INCOIS interface
                      </p>
                      <button
                        onClick={() => loadINCOISProxy(selectedBuoy.buoyId, selectedParameter)}
                        className="bg-cyan-600 hover:bg-cyan-700 text-white rounded-lg px-6 py-3 text-sm font-semibold transition-colors"
                      >
                        🚀 Submit
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default BuoyMap;
