import { useEffect, useState } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import L from "leaflet";

// Fix for missing Leaflet marker icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [floats, setFloats] = useState([]);
  const [selectedFloat, setSelectedFloat] = useState(null);
  const [stats, setStats] = useState({
    totalFloats: 0,
    activeFloats: 0,
    recentData: 0,
    avgTemperature: 0
  });
  const [loading, setLoading] = useState(false);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [yearRange, setYearRange] = useState([]);

  useEffect(() => {
    // Generate year range from 2001 to current year
    const currentYear = new Date().getFullYear();
    const years = [];
    for (let year = 2001; year <= currentYear; year++) {
      years.push(year);
    }
    setYearRange(years.reverse());
  }, []);

  // Fetch Argo floats list
  useEffect(() => {
    axios.get("http://localhost:5000/api/argo-floats").then((res) => {
      if (res.data.success) {
        setFloats(res.data.floats);
        setStats(res.data.stats);
      }
    }).catch(err => {
      console.error("Error fetching floats:", err);
    });
  }, []);

  // Fetch float details by ID
  const fetchFloatDetails = async (floatId) => {
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:5000/api/argo-float/${floatId}`);
      if (res.data.success) {
        setSelectedFloat(res.data.data);
      }
    } catch (err) {
      console.error("Error fetching float details:", err);
    }
    setLoading(false);
  };

  // Download CSV data for selected year
  const downloadCSV = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/argo-data-csv/${selectedYear}`, {
        responseType: 'blob'
      });

      const blob = new Blob([response.data], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `argo_data_${selectedYear}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error("Error downloading CSV:", err);
      alert("Error downloading CSV data");
    }
  };

  // Create temperature chart
  const tempChartData = selectedFloat && selectedFloat.temperatureProfile ? {
    labels: selectedFloat.temperatureProfile.depths,
    datasets: [{
      label: 'Temperature (¬∞C)',
      data: selectedFloat.temperatureProfile.temperatures,
      borderColor: '#ef4444',
      backgroundColor: 'rgba(239, 68, 68, 0.1)',
      tension: 0.4,
      fill: true,
    }]
  } : null;

  // Create salinity chart
  const salinityChartData = selectedFloat && selectedFloat.salinityProfile ? {
    labels: selectedFloat.salinityProfile.depths,
    datasets: [{
      label: 'Salinity (PSU)',
      data: selectedFloat.salinityProfile.salinity,
      borderColor: '#3b82f6',
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      tension: 0.4,
      fill: true,
    }]
  } : null;

  // Create custom Argo float icon
  const argoIcon = new L.Icon({
    iconUrl: 'data:image/svg+xml;base64,' + btoa(`
      <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <circle cx="12" cy="12" r="8" fill="#0ea5e9" stroke="#ffffff" stroke-width="2"/>
        <circle cx="12" cy="12" r="3" fill="#ffffff"/>
      </svg>
    `),
    iconSize: [24, 24],
    iconAnchor: [12, 12],
    popupAnchor: [0, -12]
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-700 to-cyan-500 text-white">
      {/* Header */}
      <header className="p-6 text-center bg-gradient-to-r from-blue-600 via-cyan-500 to-blue-600 text-white font-extrabold text-3xl shadow-xl tracking-wide">
        üåä INCOIS Argo Float Monitoring System
      </header>

      {/* Stats Dashboard */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-6 p-6">
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Active Floats</h2>
          <p className="text-3xl font-bold text-green-400">{stats.activeFloats}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Total Records</h2>
          <p className="text-3xl font-bold text-blue-400">{stats.totalFloats}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Recent Data</h2>
          <p className="text-3xl font-bold text-yellow-400">{stats.recentData}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Avg Temperature</h2>
          <p className="text-3xl font-bold text-orange-400">{stats.avgTemperature}¬∞C</p>
        </div>
      </div>

      {/* Year Selection and CSV Download */}
      <div className="px-6 mb-6">
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 shadow-lg">
          <h3 className="text-xl font-semibold mb-4 text-center">Download Historical Data</h3>
          <div className="flex flex-col md:flex-row items-center justify-center gap-4">
            <select
              value={selectedYear}
              onChange={(e) => setSelectedYear(parseInt(e.target.value))}
              className="px-4 py-2 rounded-lg bg-white/10 border border-white/30 text-white backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
            >
              {yearRange.map(year => (
                <option key={year} value={year} className="bg-blue-800">
                  {year}
                </option>
              ))}
            </select>
            <button
              onClick={downloadCSV}
              className="px-6 py-2 bg-gradient-to-r from-green-500 to-blue-500 rounded-lg font-semibold hover:from-green-600 hover:to-blue-600 transition-all duration-300 shadow-lg"
            >
              üì• Download CSV
            </button>
          </div>
        </div>
      </div>

      {/* Main Layout */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6">
        {/* Map */}
        <div className="bg-white/20 backdrop-blur-md rounded-2xl shadow-xl overflow-hidden border border-white/30">
          <div className="p-4 bg-white/10 border-b border-white/20">
            <h3 className="text-lg font-semibold">üó∫Ô∏è Argo Float Locations</h3>
          </div>
          <MapContainer
            center={[10, 75]}
            zoom={4}
            style={{ height: "500px", width: "100%" }}
          >
            <TileLayer
              attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {floats.map((float, idx) => (
              <Marker
                key={idx}
                position={[float.lat || 10, float.lon || 75]}
                icon={argoIcon}
                eventHandlers={{
                  click: () => fetchFloatDetails(float.id),
                }}
              >
                <Popup>
                  <div className="text-black">
                    <strong>{float.name}</strong><br/>
                    ID: {float.id}<br/>
                    Status: {float.status === 'active' ? 'üü¢ Active' : 'üî¥ Inactive'}
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>

        {/* Float Details */}
        <div className="bg-white/20 backdrop-blur-md rounded-2xl shadow-xl border border-white/30">
          <div className="p-4 bg-white/10 border-b border-white/20">
            <h3 className="text-lg font-semibold">üìä Float Data Analysis</h3>
          </div>
          <div className="p-6">
            {loading ? (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white mx-auto"></div>
                <p className="text-gray-200 mt-2">Loading float details...</p>
              </div>
            ) : selectedFloat ? (
              <div className="space-y-6">
                {/* Float Info */}
                <div>
                  <h4 className="text-xl font-semibold mb-2 text-yellow-300">
                    {selectedFloat.name}
                  </h4>
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <p><span className="text-gray-300">Float ID:</span> {selectedFloat.id}</p>
                    <p><span className="text-gray-300">Status:</span> 
                      <span className={selectedFloat.status === 'active' ? 'text-green-400' : 'text-red-400'}>
                        {selectedFloat.status === 'active' ? ' üü¢ Active' : ' üî¥ Inactive'}
                      </span>
                    </p>
                    <p><span className="text-gray-300">Latitude:</span> {selectedFloat.lat?.toFixed(4) || 'N/A'}¬∞</p>
                    <p><span className="text-gray-300">Longitude:</span> {selectedFloat.lon?.toFixed(4) || 'N/A'}¬∞</p>
                    <p><span className="text-gray-300">Last Update:</span> {selectedFloat.lastUpdate || 'N/A'}</p>
                    <p><span className="text-gray-300">Cycles:</span> {selectedFloat.cycles || 0}</p>
                  </div>
                </div>

                {/* Temperature Profile */}
                {tempChartData && (
                  <div>
                    <h5 className="text-lg font-semibold mb-3 text-red-300">Temperature Profile</h5>
                    <div style={{ height: '200px' }}>
                      <Line
                        data={tempChartData}
                        options={{
                          responsive: true,
                          maintainAspectRatio: false,
                          plugins: { 
                            legend: { labels: { color: 'white' } },
                            title: { display: true, text: 'Temperature vs Depth', color: 'white' }
                          },
                          scales: {
                            x: { 
                              title: { display: true, text: 'Depth (m)', color: 'white' },
                              ticks: { color: 'white' },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                            y: { 
                              title: { display: true, text: 'Temperature (¬∞C)', color: 'white' },
                              ticks: { color: 'white' },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                          },
                        }}
                      />
                    </div>
                  </div>
                )}

                {/* Salinity Profile */}
                {salinityChartData && (
                  <div>
                    <h5 className="text-lg font-semibold mb-3 text-blue-300">Salinity Profile</h5>
                    <div style={{ height: '200px' }}>
                      <Line
                        data={salinityChartData}
                        options={{
                          responsive: true,
                          maintainAspectRatio: false,
                          plugins: { 
                            legend: { labels: { color: 'white' } },
                            title: { display: true, text: 'Salinity vs Depth', color: 'white' }
                          },
                          scales: {
                            x: { 
                              title: { display: true, text: 'Depth (m)', color: 'white' },
                              ticks: { color: 'white' },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                            y: { 
                              title: { display: true, text: 'Salinity (PSU)', color: 'white' },
                              ticks: { color: 'white' },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                          },
                        }}
                      />
                    </div>
                  </div>
                )}

                {/* Additional Parameters */}
                {selectedFloat.additionalParams && (
                  <div>
                    <h5 className="text-lg font-semibold mb-3 text-green-300">Additional Parameters</h5>
                    <div className="grid grid-cols-2 gap-2 text-sm">
                      {Object.entries(selectedFloat.additionalParams).map(([key, value]) => (
                        <p key={key}>
                          <span className="text-gray-300 capitalize">{key.replace(/_/g, ' ')}:</span> {value}
                        </p>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <p className="text-gray-300 text-center py-8">
                üîç Click on an Argo float marker to view detailed oceanographic data
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
