      // Enhanced API call with proper error handling
      const response = await axios.get(`http://localhost:5000/api/incois-url/${proxyBuoyId}/${encodeURIComponent(parameter)}`, {
        timeout: 10000, // 10 second timeout
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      
      if (response.data && response.data.success && response.data.proxyUrl) {
        const proxyUrl = response.data.proxyUrl;
        const fullProxyUrl = `http://localhost:5000${proxyUrl}`;
        setIncoisProxyUrl(fullProxyUrl);
        console.log(`âœ… INCOIS proxy loaded: ${fullProxyUrl}`);
        
        // Set a timeout to check if iframe loads
        setTimeout(() => {
          if (!iframeLoaded) {
