      } else {
        throw new Error(`Invalid response from proxy server: ${JSON.stringify(response.data)}`);
      }
    } catch (error) {
      console.error("âŒ Error loading INCOIS proxy:", error);
      let errorMessage = `Error loading INCOIS proxy: ${error.message}`;
      
      if (error.code === 'ECONNABORTED') {
        errorMessage = 'Request timeout - INCOIS server may be slow to respond';
      } else if (error.response?.status === 404) {
        errorMessage = `Parameter "${parameter}" not found for buoy ${getProxyBuoyId(buoy)}`;
      } else if (error.response?.status >= 500) {
        errorMessage = 'INCOIS server error - please try again later';
      }
      
      setProxyError(errorMessage);
