import { useEffect, useState } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import L from "leaflet";

// Fix for missing Leaflet marker icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [buoys, setBuoys] = useState([]);
  const [selectedBuoy, setSelectedBuoy] = useState(null);
  const [stats, setStats] = useState({
    totalBuoys: 0,
    activeBuoys: 0,
    recentData: 0,
    avgTemperature: 0,
    buoysWithData: 0,
    buoysWithoutData: 0
  });
  const [networks, setNetworks] = useState({ OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
  const [loading, setLoading] = useState(false);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [yearRange, setYearRange] = useState([]);
  const [realTimeMode, setRealTimeMode] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const currentYear = new Date().getFullYear();
    const years = [];
    for (let year = 2012; year <= currentYear; year++) {
      years.push(year);
    }
    setYearRange(years.reverse());
  }, []);

  // Fetch ocean buoys data with error handling
  useEffect(() => {
    const fetchBuoys = async () => {
      try {
        setError(null);
        const response = await axios.get("http://localhost:5000/api/ocean-buoys");

        if (response.data && response.data.success) {
          setBuoys(response.data.buoys || []);
          setStats(response.data.stats || {
            totalBuoys: 0,
            activeBuoys: 0,
            recentData: 0,
            avgTemperature: 0,
            buoysWithData: 0,
            buoysWithoutData: 0
          });
          setNetworks(response.data.networks || { OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
        } else {
          setError("No data received from server");
        }
      } catch (err) {
        console.error("Error fetching buoys:", err);
        setError(`Failed to fetch buoy data: ${err.message}`);
        // Set default empty state
        setBuoys([]);
        setStats({
          totalBuoys: 0,
          activeBuoys: 0,
          recentData: 0,
          avgTemperature: 0,
          buoysWithData: 0,
          buoysWithoutData: 0
        });
        setNetworks({ OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
      }
    };

    fetchBuoys();
  }, []);

  // Real-time data updates with null safety
  useEffect(() => {
    if (!realTimeMode || !selectedBuoy) return;

    const interval = setInterval(() => {
      axios.get(`http://localhost:5000/api/realtime-data/${selectedBuoy.id}`)
        .then(res => {
          if (res.data && res.data.success && res.data.data) {
            setSelectedBuoy(prevBuoy => ({
              ...prevBuoy,
              parameters: res.data.data.parameters || null,
              lastUpdate: new Date().toISOString()
            }));
          }
        })
        .catch(err => {
          console.error("Real-time update error:", err);
          // Don't break the UI on real-time errors
        });
    }, 30000);

    return () => clearInterval(interval);
  }, [realTimeMode, selectedBuoy]);

  // Fetch buoy details with null safety
  const fetchBuoyDetails = async (buoyId) => {
    setLoading(true);
    setError(null);

    try {
      const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
      if (res.data && res.data.success && res.data.data) {
        setSelectedBuoy(res.data.data);
      } else {
        setError(`No data available for buoy ${buoyId}`);
      }
    } catch (err) {
      console.error("Error fetching buoy details:", err);
      setError(`Failed to fetch details for buoy ${buoyId}: ${err.message}`);
    }
    setLoading(false);
  };

  // Download CSV data
  const downloadCSV = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/ocean-data-csv/${selectedYear}`, {
        responseType: 'blob'
      });

      const blob = new Blob([response.data], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `incois_ocean_data_${selectedYear}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error("Error downloading CSV:", err);
      alert(`Error downloading CSV data: ${err.message}`);
    }
  };

  // SAFE parameter access function
  const getParameterValue = (buoy, paramName, defaultValue = 'N/A') => {
    if (!buoy || !buoy.parameters || buoy.parameters === null) {
      return defaultValue;
    }
    return buoy.parameters[paramName] || defaultValue;
  };

  // Create temperature profile chart with null safety
  const tempChartData = selectedBuoy && selectedBuoy.temperatureProfile ? {
    labels: selectedBuoy.temperatureProfile.depths?.map(d => `${d}m`) || [],
    datasets: [{
      label: 'Temperature (Â°C)',
      data: selectedBuoy.temperatureProfile.temperatures || [],
      borderColor: '#ef4444',
      backgroundColor: 'rgba(239, 68, 68, 0.1)',
      tension: 0.4,
      fill: true,
    }]
  } : null;

  // Create salinity profile chart with null safety
  const salinityChartData = selectedBuoy && selectedBuoy.salinityProfile ? {
    labels: selectedBuoy.salinityProfile.depths?.map(d => `${d}m`) || [],
    datasets: [{
      label: 'Salinity (PSU)',
      data: selectedBuoy.salinityProfile.salinity || [],
      borderColor: '#3b82f6',
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      tension: 0.4,
      fill: true,
    }]
  } : null;

  // Create custom buoy icons
  const createBuoyIcon = (type, color) => {
    return new L.Icon({
      iconUrl: 'data:image/svg+xml;base64,' + btoa(`
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <circle cx="12" cy="12" r="8" fill="${color}" stroke="#ffffff" stroke-width="2"/>
          <text x="12" y="16" text-anchor="middle" fill="white" font-size="8" font-weight="bold">${type.charAt(0)}</text>
        </svg>
      `),
      iconSize: [24, 24],
      iconAnchor: [12, 12],
      popupAnchor: [0, -12]
    });
  };

  const omniIcon = createBuoyIcon('OMNI', '#0ea5e9');
  const ramaIcon = createBuoyIcon('RAMA', '#10b981');
  const argoIcon = createBuoyIcon('ARGO', '#8b5cf6');
  const mooredIcon = createBuoyIcon('MOORED', '#f59e0b');

  const getIconForBuoy = (buoy) => {
    switch (buoy.type) {
      case 'OMNI': return omniIcon;
      case 'RAMA': return ramaIcon;
      case 'ARGO': return argoIcon;
      case 'MOORED': return mooredIcon;
      default: return omniIcon;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-700 to-cyan-500 text-white">
      {/* Header */}
      <header className="p-6 text-center bg-gradient-to-r from-blue-600 via-cyan-500 to-blue-600 text-white font-extrabold text-3xl shadow-xl tracking-wide">
        ðŸŒŠ INCOIS Ocean Observation Network Dashboard
      </header>

      {/* Error Display */}
      {error && (
        <div className="mx-6 mt-4 p-4 bg-red-500/20 border border-red-500/50 rounded-lg">
          <p className="text-red-200">âš ï¸ {error}</p>
        </div>
      )}

      {/* Stats Dashboard with null safety */}
      <div className="grid grid-cols-2 md:grid-cols-6 gap-4 p-6">
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Active Buoys</h2>
          <p className="text-3xl font-bold text-green-400">{stats.activeBuoys || 0}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Total Buoys</h2>
          <p className="text-3xl font-bold text-blue-400">{stats.totalBuoys || 0}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">With Data</h2>
          <p className="text-3xl font-bold text-green-400">{stats.buoysWithData || 0}</p>
        </div>
