/**
 * 3. Generate CSV data for specific year
 */
app.get("/api/ocean-data-csv/:year", async (req, res) => {
  const year = parseInt(req.params.year);
  try {
    const allBuoys = await cachedFetch("ocean_buoys", async () => {
      const omniData = OMNI_BUOY_LOCATIONS.map(buoy => ({
        ...generateOceanographicData(buoy.id, buoy.lat, buoy.lon),
        type: 'OMNI'
      }));
      const ramaData = RAMA_BUOY_LOCATIONS.map(buoy => ({
        ...generateOceanographicData(buoy.id, buoy.lat, buoy.lon),
        type: 'RAMA'
      }));
      return [...omniData, ...ramaData];
    });

    // Generate CSV content
    let csvContent = "Buoy_ID,Network,Latitude,Longitude,Status,Last_Update,SST,Salinity,Wind_Speed,Wind_Dir,Air_Temp,Humidity,Pressure,Wave_Height,Current_Speed,Current_Dir\n";

    allBuoys.forEach(buoy => {
      csvContent += [
        buoy.id,
        buoy.type,
        buoy.lat.toFixed(4),
        buoy.lon.toFixed(4),
        buoy.status,
        buoy.lastUpdate.split('T')[0],
        buoy.parameters.seaSurfaceTemperature,
        buoy.parameters.salinity,
        buoy.parameters.windSpeed,
        buoy.parameters.windDirection,
        buoy.parameters.airTemperature,
        buoy.parameters.humidity,
        buoy.parameters.pressure,
        buoy.parameters.waveHeight,
        buoy.parameters.currentSpeed,
        buoy.parameters.currentDirection
      ].join(',') + '\n';
    });

    res.setHeader('Content-Type', 'text/csv');
    res.setHeader('Content-Disposition', `attachment; filename="incois_ocean_data_${year}.csv"`);
    res.send(csvContent);

  } catch (err) {
    console.error("Error generating CSV:", err.message);
    res.status(500).json({
      success: false,
      message: "Failed to generate CSV",
      error: err.message,
    });
  }
});

