/**
 * 5. Historical data trends
 */
app.get("/api/historical-trends/:buoyId/:days", async (req, res) => {
  const { buoyId, days } = req.params;
  const numDays = parseInt(days) || 7;

  try {
    const baseData = OMNI_BUOY_LOCATIONS.find(b => b.id === buoyId) || 
                     RAMA_BUOY_LOCATIONS.find(b => b.id === buoyId);

    if (!baseData) {
      return res.status(404).json({ success: false, message: "Buoy not found" });
    }

    const trends = [];
    for (let i = numDays; i >= 0; i--) {
      const date = new Date(Date.now() - i * 24 * 60 * 60 * 1000);
      const data = generateOceanographicData(buoyId, baseData.lat, baseData.lon);
      trends.push({
        date: date.toISOString().split('T')[0],
        temperature: data.parameters.seaSurfaceTemperature,
        salinity: data.parameters.salinity,
        windSpeed: data.parameters.windSpeed,
        waveHeight: data.parameters.waveHeight
      });
    }

    res.json({ success: true, trends: trends });
  } catch (err) {
    console.error("Error fetching historical trends:", err.message);
    res.status(500).json({ success: false, message: err.message });
  }
});

