import { useEffect, useState, useMemo } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { Line, Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Fix missing Leaflet icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [buoys, setBuoys] = useState([]);
  const [filteredBuoys, setFilteredBuoys] = useState([]);
  const [selectedBuoy, setSelectedBuoy] = useState(null);
  const [stats, setStats] = useState({
    totalBuoys: 0,
    activeBuoys: 0,
    recentData: 0,
    avgTemperature: 0,
    avgSalinity: 0,
    buoysWithData: 0,
    buoysWithoutData: 0
  });
  const [networks, setNetworks] = useState({ OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
  const [loading, setLoading] = useState(false);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [yearRange, setYearRange] = useState([]);
  const [realTimeMode, setRealTimeMode] = useState(false);
  const [error, setError] = useState(null);

  // Filter states
  const [selectedRegion, setSelectedRegion] = useState('all');
  const [selectedNetwork, setSelectedNetwork] = useState('all');
  const [showActiveOnly, setShowActiveOnly] = useState(false);

  const regions = {
    indian_ocean: { name: 'Indian Ocean', bounds: { north: 30, south: -60, east: 100, west: 40 } },
    bay_of_bengal: { name: 'Bay of Bengal', bounds: { north: 25, south: 5, east: 95, west: 80 } },
    arabian_sea: { name: 'Arabian Sea', bounds: { north: 30, south: 0, east: 80, west: 50 } },
    equatorial_indian: { name: 'Equatorial Indian Ocean', bounds: { north: 10, south: -10, east: 100, west: 50 } },
    southern_indian: { name: 'Southern Indian Ocean', bounds: { north: -10, south: -60, east: 120, west: 40 } }
  };

  // Generate year range
  useEffect(() => {
    const years = [];
    for (let year = 2012; year <= new Date().getFullYear(); year++) years.push(year);
    setYearRange(years.reverse());
  }, []);

  // Fetch buoys data
  useEffect(() => {
    const fetchBuoys = async () => {
      try {
        setError(null);
        const response = await axios.get("http://localhost:5000/api/ocean-buoys");
        if (response.data?.success) {
          setBuoys(response.data.buoys || []);
          setStats(response.data.stats || stats);
          setNetworks(response.data.networks || networks);
        } else {
          setError("No data received from server");
        }
      } catch (err) {
        console.error(err);
        setError(`Failed to fetch buoy data: ${err.message}`);
      }
    };
    fetchBuoys();
  }, []);

  // Filter buoys
  useEffect(() => {
    let filtered = [...buoys];

    if (selectedRegion !== 'all') {
      const region = regions[selectedRegion];
      filtered = filtered.filter(b => b.lat <= region.bounds.north && b.lat >= region.bounds.south && b.lon <= region.bounds.east && b.lon >= region.bounds.west);
    }
    if (selectedNetwork !== 'all') filtered = filtered.filter(b => b.type === selectedNetwork);
    if (showActiveOnly) filtered = filtered.filter(b => b.status === 'active');

    setFilteredBuoys(filtered);
  }, [buoys, selectedRegion, selectedNetwork, showActiveOnly]);

  // Filtered stats
  const filteredStats = useMemo(() => ({
    total: filteredBuoys.length,
    active: filteredBuoys.filter(b => b.status === 'active').length,
    withData: filteredBuoys.filter(b => b.parameters).length,
    networks: {
      ARGO: filteredBuoys.filter(b => b.type === 'ARGO').length,
      OMNI: filteredBuoys.filter(b => b.type === 'OMNI').length,
      RAMA: filteredBuoys.filter(b => b.type === 'RAMA').length,
      MOORED: filteredBuoys.filter(b => b.type === 'MOORED').length,
    }
  }), [filteredBuoys]);

  // Fetch single buoy details
  const fetchBuoyDetails = async (buoyId) => {
    setLoading(true);
    setError(null);
    try {
      const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
      if (res.data?.success) setSelectedBuoy(res.data.data);
      else setError(`No data for buoy ${buoyId}`);
    } catch (err) {
      setError(`Failed to fetch buoy details: ${err.message}`);
    }
    setLoading(false);
  };

  // CSV download
  const downloadCSV = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/ocean-data-csv/${selectedYear}`, { responseType: 'blob' });
      const blob = new Blob([res.data], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `incois_ocean_data_${selectedYear}.csv`;
      link.click();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      alert(`CSV download failed: ${err.message}`);
    }
  };

  // Clear filters
  const clearFilters = () => {
    setSelectedRegion('all');
    setSelectedNetwork('all');
    setShowActiveOnly(false);
  };

  // Parameter safe access
  const getParameterValue = (buoy, key, defaultValue = 'N/A') => {
    return buoy?.parameters?.[key] ?? defaultValue;
  };

  // Format coordinates
  const formatCoordinates = (buoy) => `${buoy.lat?.toFixed(4) || 'N/A'}Â°N, ${buoy.lon?.toFixed(4) || 'N/A'}Â°E`;

  // Buoy Icon
  const createBuoyIcon = (type, color, isSelected = false) => {
    const size = isSelected ? 32 : 28;
    return new L.Icon({
      iconUrl: 'data:image/svg+xml;base64,' + btoa(`
        <svg width="${size}" height="${size}" viewBox="0 0 ${size} ${size}" xmlns="http://www.w3.org/2000/svg">
          <circle cx="${size/2}" cy="${size/2}" r="${size/2 - 2}" fill="${color}" stroke="#fff" stroke-width="2"/>
          <text x="${size/2}" y="${size/2 + 4}" text-anchor="middle" fill="white" font-size="10" font-weight="bold">${type.charAt(0)}</text>
          ${isSelected ? `<circle cx="${size/2}" cy="${size/2}" r="${size/2 - 1}" fill="none" stroke="#ffff00" stroke-width="2"/>` : ''}
        </svg>
      `),
      iconSize: [size, size],
      iconAnchor: [size/2, size/2],
      popupAnchor: [0, -size/2]
    });
  };

  const getIconForBuoy = (buoy, isSelected = false) => {
    const colors = { OMNI: '#0ea5e9', RAMA: '#10b981', ARGO: '#8b5cf6', MOORED: '#f59e0b' };
    return createBuoyIcon(buoy.type, colors[buoy.type] || '#0ea5e9', isSelected);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-700 to-cyan-500 text-white">
      <header className="p-6 text-center font-extrabold text-3xl shadow-xl tracking-wide">ðŸŒŠ INCOIS Ocean Observation Dashboard</header>

      {/* Error */}
      {error && <div className="mx-6 mt-4 p-4 bg-red-500/20 rounded-lg">{error}</div>}

      {/* Stats & Controls */}
      {/* ... same as your current JSX ... */}

      {/* Map */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6">
        <div className="bg-white/15 backdrop-blur-lg rounded-3xl shadow-2xl overflow-hidden border border-white/20">
          <MapContainer center={[12, 77]} zoom={5} style={{ height: "500px", width: "100%" }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="&copy; OpenStreetMap" />
            {filteredBuoys.map((b, idx) => (
              <Marker
                key={b.id + idx}
                position={[b.lat || 0, b.lon || 0]}
                icon={getIconForBuoy(b, selectedBuoy?.id === b.id)}
                eventHandlers={{ click: () => fetchBuoyDetails(b.id) }}
              >
                <Popup>
                  <div className="text-black p-3 min-w-[280px]">
                    <strong>{b.id}</strong>
                    <div>Network: {b.type}</div>
                    <div>Status: {b.status}</div>
                    <div>Coordinates: {formatCoordinates(b)}</div>
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>

        {/* Buoy Analysis */}
        <div className="bg-white/20 backdrop-blur-md rounded-2xl shadow-xl border border-white/30">
          {/* Detailed buoy info */}
        </div>
      </div>
    </div>
  );
}

export default App;
