import { useEffect, useState } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { Line, Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import L from "leaflet";

// Fix for missing Leaflet marker icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [buoys, setBuoys] = useState([]);
  const [filteredBuoys, setFilteredBuoys] = useState([]);
  const [selectedBuoy, setSelectedBuoy] = useState(null);
  const [stats, setStats] = useState({
    totalBuoys: 0,
    activeBuoys: 0,
    recentData: 0,
    avgTemperature: 0,
    avgSalinity: 0,
    buoysWithData: 0,
    buoysWithoutData: 0
  });
  const [networks, setNetworks] = useState({ OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
  const [loading, setLoading] = useState(false);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [yearRange, setYearRange] = useState([]);
  const [realTimeMode, setRealTimeMode] = useState(false);
  const [error, setError] = useState(null);

  // Filter states
  const [selectedRegion, setSelectedRegion] = useState('all');
  const [selectedNetwork, setSelectedNetwork] = useState('all');
  const [showActiveOnly, setShowActiveOnly] = useState(false);

  // Regional boundaries for filtering
  const regions = {
    'indian_ocean': {
      name: 'Indian Ocean',
      bounds: { north: 30, south: -60, east: 100, west: 40 }
    },
    'bay_of_bengal': {
      name: 'Bay of Bengal',
      bounds: { north: 25, south: 5, east: 95, west: 80 }
    },
    'arabian_sea': {
      name: 'Arabian Sea',
      bounds: { north: 30, south: 0, east: 80, west: 50 }
    },
    'equatorial_indian': {
      name: 'Equatorial Indian Ocean',
      bounds: { north: 10, south: -10, east: 100, west: 50 }
    },
    'southern_indian': {
      name: 'Southern Indian Ocean',
      bounds: { north: -10, south: -60, east: 120, west: 40 }
    }
  };

  useEffect(() => {
    const currentYear = new Date().getFullYear();
    const years = [];
    for (let year = 2012; year <= currentYear; year++) {
      years.push(year);
    }
    setYearRange(years.reverse());
  }, []);

  // Fetch ocean buoys data with error handling
  useEffect(() => {
    const fetchBuoys = async () => {
      try {
        setError(null);
        console.log("🔄 Fetching buoys from INCOIS server...");
        const response = await axios.get("http://localhost:5000/api/ocean-buoys");

        if (response.data && response.data.success) {
          console.log("✅ Server response:", response.data);
          console.log(`📊 Total buoys received: ${response.data.buoys?.length || 0}`);
          console.log(`📈 Network breakdown:`, response.data.networks);

          setBuoys(response.data.buoys || []);
          setStats(response.data.stats || {
            totalBuoys: 0,
            activeBuoys: 0,
            recentData: 0,
            avgTemperature: 0,
            avgSalinity: 0,
            buoysWithData: 0,
            buoysWithoutData: 0
          });
          setNetworks(response.data.networks || { OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
        } else {
          setError("No data received from server");
        }
      } catch (err) {
        console.error("❌ Error fetching buoys:", err);
        setError(`Failed to fetch buoy data: ${err.message}`);
        setBuoys([]);
        setStats({
          totalBuoys: 0,
          activeBuoys: 0,
          recentData: 0,
          avgTemperature: 0,
          avgSalinity: 0,
          buoysWithData: 0,
          buoysWithoutData: 0
        });
        setNetworks({ OMNI: 0, RAMA: 0, ARGO: 0, MOORED: 0 });
      }
    };

    fetchBuoys();
  }, []);

  // Filter buoys based on selected criteria
  useEffect(() => {
    let filtered = [...buoys];

    // Apply regional filter
    if (selectedRegion !== 'all') {
      const region = regions[selectedRegion];
      if (region) {
        filtered = filtered.filter(buoy => {
          const lat = parseFloat(buoy.lat);
          const lon = parseFloat(buoy.lon);
          return lat <= region.bounds.north && 
                 lat >= region.bounds.south && 
                 lon <= region.bounds.east && 
                 lon >= region.bounds.west;
        });
      }
    }

    // Apply network filter
    if (selectedNetwork !== 'all') {
      filtered = filtered.filter(buoy => buoy.type === selectedNetwork);
    }

    // Apply status filter
    if (showActiveOnly) {
      filtered = filtered.filter(buoy => buoy.status === 'active');
    }

    setFilteredBuoys(filtered);
  }, [buoys, selectedRegion, selectedNetwork, showActiveOnly]);

  // Real-time data updates with null safety
  useEffect(() => {
    if (!realTimeMode || !selectedBuoy) return;

    const interval = setInterval(() => {
      axios.get(`http://localhost:5000/api/realtime-data/${selectedBuoy.id}`)
        .then(res => {
          if (res.data && res.data.success && res.data.data) {
            setSelectedBuoy(prevBuoy => ({
              ...prevBuoy,
              parameters: { ...prevBuoy.parameters, ...res.data.data.parameters },
              lastUpdate: new Date().toISOString()
            }));
          }
        })
        .catch(err => {
          console.error("Real-time update error:", err);
        });
    }, 30000);

    return () => clearInterval(interval);
  }, [realTimeMode, selectedBuoy]);

  // Fetch buoy details when marker is clicked
  const fetchBuoyDetails = async (buoyId) => {
    setLoading(true);
    setError(null);

    try {
      console.log(`🔍 Fetching details for buoy: ${buoyId}`);
      const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
      if (res.data && res.data.success && res.data.data) {
        console.log("📊 Buoy details:", res.data.data);
        setSelectedBuoy(res.data.data);
      } else {
        setError(`No data available for buoy ${buoyId}`);
      }
    } catch (err) {
      console.error("Error fetching buoy details:", err);
      setError(`Failed to fetch details for buoy ${buoyId}: ${err.message}`);
    }
    setLoading(false);
  };

  // Download CSV data
  const downloadCSV = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/ocean-data-csv/${selectedYear}`, {
        responseType: 'blob'
      });

      const blob = new Blob([response.data], { type: 'text/csv' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `incois_ocean_data_${selectedYear}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error("Error downloading CSV:", err);
      alert(`Error downloading CSV data: ${err.message}`);
    }
  };

  // Clear all filters
  const clearFilters = () => {
    setSelectedRegion('all');
    setSelectedNetwork('all');
    setShowActiveOnly(false);
  };

  // Get region name for a buoy based on coordinates
  const getBuoyRegion = (buoy) => {
    const lat = parseFloat(buoy.lat);
    const lon = parseFloat(buoy.lon);

    for (const [key, region] of Object.entries(regions)) {
      if (lat <= region.bounds.north && 
          lat >= region.bounds.south && 
          lon <= region.bounds.east && 
          lon >= region.bounds.west) {
        return region.name;
      }
    }
    return 'Other Region';
  };

  // SAFE parameter access function - Enhanced for all parameter variations
  const getParameterValue = (buoy, paramName, defaultValue = 'N/A') => {
    if (!buoy || !buoy.parameters) {
      return defaultValue;
    }

    const params = buoy.parameters;

    // Handle different parameter name variations from server
    const paramMap = {
      'seaSurfaceTemperature': params.seaSurfaceTemperature || params.sst || params.SST,
      'sst': params.seaSurfaceTemperature || params.sst || params.SST,
      'salinity': params.salinity || params.Salinity,
      'windSpeed': params.windSpeed || params.windspeed || params.WindSpeed,
      'windDirection': params.windDirection || params.winddirection || params.WindDirection,
      'waveHeight': params.waveHeight || params.waveheight || params.WaveHeight,
      'pressure': params.pressure || params.Pressure,
      'airTemperature': params.airTemperature || params.AirTemperature
    };

    const value = paramMap[paramName] || params[paramName];
    return value !== null && value !== undefined ? value : defaultValue;
  };

  // Format coordinates for display
  const formatCoordinates = (buoy) => {
    if (buoy.coordinates && Array.isArray(buoy.coordinates)) {
      return `${buoy.coordinates[1]?.toFixed(4)}°N, ${buoy.coordinates[0]?.toFixed(4)}°E`;
    }
    return `${buoy.lat?.toFixed(4) || 'N/A'}°N, ${buoy.lon?.toFixed(4) || 'N/A'}°E`;
  };

  // Create temperature profile chart with null safety
  const tempChartData = selectedBuoy && selectedBuoy.temperatureProfile ? {
    labels: selectedBuoy.temperatureProfile.depths?.map(d => `${d}m`) || [],
    datasets: [{
      label: 'Temperature (°C)',
      data: selectedBuoy.temperatureProfile.temperatures || [],
      borderColor: '#ef4444',
      backgroundColor: 'rgba(239, 68, 68, 0.1)',
      tension: 0.4,
      fill: true,
    }]
  } : null;

  // Create salinity profile chart with null safety
  const salinityChartData = selectedBuoy && selectedBuoy.salinityProfile ? {
    labels: selectedBuoy.salinityProfile.depths?.map(d => `${d}m`) || [],
    datasets: [{
      label: 'Salinity (PSU)',
      data: selectedBuoy.salinityProfile.salinity || [],
      borderColor: '#3b82f6',
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      tension: 0.4,
      fill: true,
    }]
  } : null;

  // Create custom buoy icons with better visibility
  const createBuoyIcon = (type, color, isSelected = false) => {
    const size = isSelected ? 32 : 28;
    const strokeWidth = isSelected ? 3 : 2;

    return new L.Icon({
      iconUrl: 'data:image/svg+xml;base64,' + btoa(`
        <svg width="${size}" height="${size}" viewBox="0 0 ${size} ${size}" fill="none" xmlns="http://www.w3.org/2000/svg">
          <circle cx="${size/2}" cy="${size/2}" r="${size/2 - 2}" fill="${color}" stroke="#ffffff" stroke-width="${strokeWidth}"/>
          <text x="${size/2}" y="${size/2 + 4}" text-anchor="middle" fill="white" font-size="10" font-weight="bold">${type.charAt(0)}</text>
          ${isSelected ? `<circle cx="${size/2}" cy="${size/2}" r="${size/2 - 1}" fill="none" stroke="#ffff00" stroke-width="2" opacity="0.8"/>` : ''}
        </svg>
      `),
      iconSize: [size, size],
      iconAnchor: [size/2, size/2],
      popupAnchor: [0, -size/2]
    });
  };

  const omniIcon = createBuoyIcon('OMNI', '#0ea5e9');
  const ramaIcon = createBuoyIcon('RAMA', '#10b981');
  const argoIcon = createBuoyIcon('ARGO', '#8b5cf6');
  const mooredIcon = createBuoyIcon('MOORED', '#f59e0b');

  const getIconForBuoy = (buoy, isSelected = false) => {
    switch (buoy.type) {
      case 'OMNI': return createBuoyIcon('OMNI', '#0ea5e9', isSelected);
      case 'RAMA': return createBuoyIcon('RAMA', '#10b981', isSelected);
      case 'ARGO': return createBuoyIcon('ARGO', '#8b5cf6', isSelected);
      case 'MOORED': return createBuoyIcon('MOORED', '#f59e0b', isSelected);
      default: return createBuoyIcon('OMNI', '#0ea5e9', isSelected);
    }
  };

  // Calculate filtered stats
  const filteredStats = {
    total: filteredBuoys.length,
    active: filteredBuoys.filter(b => b.status === 'active').length,
    withData: filteredBuoys.filter(b => b.parameters !== null && b.parameters !== undefined).length,
    networks: {
      ARGO: filteredBuoys.filter(b => b.type === 'ARGO').length,
      OMNI: filteredBuoys.filter(b => b.type === 'OMNI').length,
      RAMA: filteredBuoys.filter(b => b.type === 'RAMA').length,
      MOORED: filteredBuoys.filter(b => b.type === 'MOORED').length,
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-700 to-cyan-500 text-white">
      {/* Header */}
      <header className="p-6 text-center bg-gradient-to-r from-blue-600 via-cyan-500 to-blue-600 text-white font-extrabold text-3xl shadow-xl tracking-wide">
        🌊 INCOIS Ocean Observation Network Dashboard
      </header>

      {/* Error Display */}
      {error && (
        <div className="mx-6 mt-4 p-4 bg-red-500/20 border border-red-500/50 rounded-lg">
          <p className="text-red-200">⚠️ {error}</p>
        </div>
      )}

      {/* Enhanced Stats Dashboard */}
      <div className="grid grid-cols-2 md:grid-cols-6 gap-4 p-6">
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Active Buoys</h2>
          <p className="text-3xl font-bold text-green-400">{filteredStats.active}</p>
          <p className="text-xs text-gray-300">of {filteredStats.total} shown</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Total Shown</h2>
          <p className="text-3xl font-bold text-blue-400">{filteredStats.total}</p>
          <p className="text-xs text-gray-300">of {stats.totalBuoys} total</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">With Data</h2>
          <p className="text-3xl font-bold text-green-400">{filteredStats.withData}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">No Data</h2>
          <p className="text-3xl font-bold text-orange-400">{filteredStats.total - filteredStats.withData}</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Avg Temp</h2>
          <p className="text-3xl font-bold text-red-400">{stats.avgTemperature || '0'}°C</p>
          <p className="text-xs text-gray-300">Sea Surface</p>
        </div>
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 text-center shadow-lg hover:scale-105 transition-transform">
          <h2 className="text-lg text-gray-100">Networks</h2>
          <div className="text-sm space-y-1">
            <p className="text-green-300">RAMA: {filteredStats.networks.RAMA}</p>
            <p className="text-purple-300">ARGO: {filteredStats.networks.ARGO}</p>
            <p className="text-yellow-300">MOORED: {filteredStats.networks.MOORED}</p>
            <p className="text-blue-300">OMNI: {filteredStats.networks.OMNI}</p>
          </div>
        </div>
      </div>

      {/* Controls - Only Real-Time and CSV */}
      <div className="px-6 mb-6">
        <div className="bg-white/20 backdrop-blur-md rounded-2xl p-6 shadow-lg">
          <div className="flex flex-col md:flex-row items-center justify-between gap-4">
            <div className="flex items-center gap-4">
              <h3 className="text-xl font-semibold">Data Controls</h3>
              <button
                onClick={() => setRealTimeMode(!realTimeMode)}
                className={`px-4 py-2 rounded-lg font-semibold transition-all ${
                  realTimeMode 
                    ? 'bg-green-500 hover:bg-green-600' 
                    : 'bg-gray-500 hover:bg-gray-600'
                }`}
              >
                {realTimeMode ? '🟢 Real-Time ON' : '⚪ Real-Time OFF'}
              </button>
              <div className="text-sm text-gray-300">
                📊 Avg Salinity: <span className="text-cyan-300 font-semibold">{stats.avgSalinity || '0'} PSU</span>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <select
                value={selectedYear}
                onChange={(e) => setSelectedYear(parseInt(e.target.value))}
                className="px-4 py-2 rounded-lg bg-white/10 border border-white/30 text-white backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
              >
                {yearRange.map(year => (
                  <option key={year} value={year} className="bg-blue-800">
                    {year}
                  </option>
                ))}
              </select>
              <button
                onClick={downloadCSV}
                className="px-6 py-2 bg-gradient-to-r from-green-500 to-blue-500 rounded-lg font-semibold hover:from-green-600 hover:to-blue-600 transition-all duration-300 shadow-lg"
              >
                📥 Download CSV
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Main Layout */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6">
        {/* Map with Header, Network Legends, and Filter Bar */}
        <div className="bg-white/15 backdrop-blur-lg rounded-3xl shadow-2xl overflow-hidden border border-white/20">
          {/* Map Header with Network Legends */}
          <div className="p-6 bg-gradient-to-r from-blue-500/30 to-purple-500/30 backdrop-blur-sm">
            <div className="flex items-center gap-3 mb-4">
              <div className="w-8 h-8 bg-blue-500 rounded-lg flex items-center justify-center">
                <span className="text-white text-sm">🗺️</span>
              </div>
              <h3 className="text-xl font-bold text-white">Ocean Buoy Locations</h3>
            </div>

            {/* Network Legend - Horizontal Layout */}
            <div className="flex items-center gap-6 flex-wrap">
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-green-500 rounded-full border-2 border-white"></div>
                <span className="text-white font-medium">RAMA ({filteredStats.networks.RAMA})</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-purple-500 rounded-full border-2 border-white"></div>
                <span className="text-white font-medium">ARGO ({filteredStats.networks.ARGO})</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-orange-500 rounded-full border-2 border-white"></div>
                <span className="text-white font-medium">MOORED ({filteredStats.networks.MOORED})</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-blue-500 rounded-full border-2 border-white"></div>
                <span className="text-white font-medium">OMNI ({filteredStats.networks.OMNI})</span>
              </div>
            </div>
          </div>

          {/* Filter Bar - Now Below Header, Above Map */}
          <div className="p-4">
            <div className="bg-gradient-to-r from-indigo-600/95 to-purple-600/95 backdrop-blur-lg rounded-2xl shadow-2xl border border-white/20">
              <div className="p-4">
                <div className="flex items-center gap-4 flex-wrap">
                  {/* Filter Icon and Label */}
                  <div className="flex items-center gap-2">
                    <div className="w-3 h-3 bg-green-400 rounded-full shadow-lg"></div>
                    <span className="text-white font-semibold">Filters</span>
                  </div>

                  {/* Region Filter */}
                  <div className="flex items-center gap-2">
                    <label className="text-white font-medium">Region:</label>
                    <select
                      value={selectedRegion}
                      onChange={(e) => setSelectedRegion(e.target.value)}
                      className="px-3 py-2 rounded-xl bg-white/20 border border-white/30 text-white font-medium backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-white/50 focus:bg-white/30 transition-all duration-200 min-w-[160px] shadow-lg"
                    >
                      <option value="all" className="bg-indigo-900 text-white">All Regions ({stats.totalBuoys})</option>
                      <option value="indian_ocean" className="bg-indigo-900 text-white">Indian Ocean</option>
                      <option value="bay_of_bengal" className="bg-indigo-900 text-white">Bay of Bengal</option>
                      <option value="arabian_sea" className="bg-indigo-900 text-white">Arabian Sea</option>
                      <option value="equatorial_indian" className="bg-indigo-900 text-white">Equatorial Indian Ocean</option>
                      <option value="southern_indian" className="bg-indigo-900 text-white">Southern Indian Ocean</option>
                    </select>
                  </div>

                  {/* Network Filter */}
                  <div className="flex items-center gap-2">
                    <label className="text-white font-medium">Network:</label>
                    <select
                      value={selectedNetwork}
                      onChange={(e) => setSelectedNetwork(e.target.value)}
                      className="px-3 py-2 rounded-xl bg-white/20 border border-white/30 text-white font-medium backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-white/50 focus:bg-white/30 transition-all duration-200 min-w-[140px] shadow-lg"
                    >
                      <option value="all" className="bg-indigo-900 text-white">All Networks</option>
                      <option value="RAMA" className="bg-indigo-900 text-white">RAMA Buoys ({networks.RAMA})</option>
                      <option value="ARGO" className="bg-indigo-900 text-white">ARGO Floats ({networks.ARGO})</option>
                      <option value="MOORED" className="bg-indigo-900 text-white">Moored Buoys ({networks.MOORED})</option>
                      <option value="OMNI" className="bg-indigo-900 text-white">OMNI Buoys ({networks.OMNI})</option>
                    </select>
                  </div>

                  {/* Active Only Checkbox */}
                  <div className="flex items-center gap-2">
                    <label className="flex items-center gap-2 cursor-pointer">
                      <input
                        type="checkbox"
                        checked={showActiveOnly}
                        onChange={(e) => setShowActiveOnly(e.target.checked)}
                        className="w-4 h-4 text-green-400 bg-white/20 border-white/30 rounded focus:ring-white/50 focus:ring-2"
                      />
                      <span className="text-white font-medium">Active Only</span>
                    </label>
                  </div>

                  {/* Clear Filters Button */}
                  <button
                    onClick={clearFilters}
                    className="px-4 py-2 bg-white/20 hover:bg-white/30 border border-white/30 rounded-xl text-white font-medium transition-all duration-200 shadow-lg hover:shadow-xl hover:scale-105"
                  >
                    Clear Filters
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* Map Container */}
          <div className="relative">
            <MapContainer
              center={[12, 77]}
              zoom={5}
              style={{ height: "500px", width: "100%" }}
            >
              <TileLayer
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {filteredBuoys.map((buoy, idx) => {
                const isSelected = selectedBuoy && selectedBuoy.id === buoy.id;
                return (
                  <Marker
                    key={`${buoy.type}-${buoy.id}-${idx}`}
                    position={[buoy.lat || 0, buoy.lon || 0]}
                    icon={getIconForBuoy(buoy, isSelected)}
                    eventHandlers={{
                      click: () => fetchBuoyDetails(buoy.id),
                    }}
                  >
                    <Popup>
                      <div className="text-black p-3 min-w-[280px]">
                        <strong className="text-lg block mb-2">{buoy.id}</strong>
                        <div className="space-y-1 text-sm">
                          <div><strong>Network:</strong> {buoy.type}</div>
                          {buoy.name && <div><strong>Name:</strong> {buoy.name}</div>}
                          {buoy.buoyId && <div><strong>Buoy ID:</strong> {buoy.buoyId}</div>}
                          <div><strong>Region:</strong> {getBuoyRegion(buoy)}</div>
                          <div><strong>Coordinates:</strong> {formatCoordinates(buoy)}</div>
                          <div><strong>Status:</strong> 
                            <span className={buoy.status === 'active' ? 'text-green-600 ml-1' : 'text-red-600 ml-1'}>
                              {buoy.status === 'active' ? '🟢 Active' : '🔴 Inactive'}
                            </span>
                          </div>
                          {buoy.reportingAgency && <div><strong>Agency:</strong> {buoy.reportingAgency}</div>}

                          {/* Parameters */}
                          {getParameterValue(buoy, 'sst') !== 'N/A' && 
                            <div><strong>SST:</strong> {getParameterValue(buoy, 'sst')}°C</div>
                          }
                          {getParameterValue(buoy, 'salinity') !== 'N/A' && 
                            <div><strong>Salinity:</strong> {getParameterValue(buoy, 'salinity')} PSU</div>
                          }
                          {getParameterValue(buoy, 'windSpeed') !== 'N/A' && 
                            <div><strong>Wind:</strong> {getParameterValue(buoy, 'windSpeed')} m/s</div>
                          }
                          {getParameterValue(buoy, 'pressure') !== 'N/A' && 
                            <div><strong>Pressure:</strong> {getParameterValue(buoy, 'pressure')} hPa</div>
                          }

                          <div className="pt-2 border-t border-gray-300">
                            <span className="text-blue-600 cursor-pointer underline text-xs">
                              📊 Click marker for detailed analysis
                            </span>
                          </div>
                        </div>
                      </div>
                    </Popup>
                  </Marker>
                );
              })}
            </MapContainer>
          </div>
        </div>

        {/* Enhanced Buoy Details Analysis */}
        <div className="bg-white/20 backdrop-blur-md rounded-2xl shadow-xl border border-white/30">
          <div className="p-4 bg-white/10 border-b border-white/20 flex justify-between items-center">
            <h3 className="text-lg font-semibold">📊 Buoy Data Analysis</h3>
            {realTimeMode && selectedBuoy && (
              <span className="text-green-400 text-sm animate-pulse">🔴 LIVE</span>
            )}
          </div>
          <div className="p-6 max-h-[600px] overflow-y-auto">
            {loading ? (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white mx-auto"></div>
                <p className="text-gray-200 mt-2">Loading buoy details...</p>
              </div>
            ) : selectedBuoy ? (
              <div className="space-y-6">
                {/* Enhanced Buoy Info */}
                <div>
                  <h4 className="text-xl font-semibold mb-3 text-yellow-300">
                    {selectedBuoy.id} ({selectedBuoy.type} Network)
                  </h4>
                  {selectedBuoy.name && (
                    <p className="text-cyan-300 mb-2 font-medium">{selectedBuoy.name}</p>
                  )}
                  <div className="grid grid-cols-1 gap-3 text-sm">
                    <p><span className="text-gray-300">Coordinates:</span> {formatCoordinates(selectedBuoy)}</p>
                    <p><span className="text-gray-300">Region:</span> {getBuoyRegion(selectedBuoy)}</p>
                    <p><span className="text-gray-300">Status:</span> 
                      <span className={selectedBuoy.status === 'active' ? 'text-green-400' : 'text-red-400'}>
                        {selectedBuoy.status === 'active' ? ' 🟢 Active' : ' 🔴 Inactive'}
                      </span>
                    </p>
                    {selectedBuoy.buoyId && (
                      <p><span className="text-gray-300">Buoy ID:</span> {selectedBuoy.buoyId}</p>
                    )}
                    {selectedBuoy.reportingAgency && (
                      <p><span className="text-gray-300">Reporting Agency:</span> {selectedBuoy.reportingAgency}</p>
                    )}
                    <p><span className="text-gray-300">Data Source:</span> {selectedBuoy.dataSource || 'INCOIS'}</p>
                    {selectedBuoy.url && (
                      <p><span className="text-gray-300">Data URL:</span> 
                        <a href={selectedBuoy.url} target="_blank" rel="noopener noreferrer" className="text-cyan-400 hover:text-cyan-300 ml-1 underline">
                          🔗 View Source
                        </a>
                      </p>
                    )}
                  </div>
                </div>

                {/* Dynamic Current Parameters */}
                {selectedBuoy.parameters && (
                  <div>
                    <h5 className="text-lg font-semibold mb-3 text-cyan-300">Real-Time Measurements</h5>
                    <div className="grid grid-cols-2 gap-3 text-sm">

                      {/* Sea Surface Temperature */}
                      {getParameterValue(selectedBuoy, 'sst') !== 'N/A' && (
                        <div className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                          <p className="text-gray-300 text-xs uppercase tracking-wide">Sea Surface Temperature</p>
                          <p className="text-2xl font-bold text-red-400 mt-1">
                            {getParameterValue(selectedBuoy, 'sst')}°C
                          </p>
                        </div>
                      )}

                      {/* Salinity */}
                      {getParameterValue(selectedBuoy, 'salinity') !== 'N/A' && (
                        <div className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                          <p className="text-gray-300 text-xs uppercase tracking-wide">Salinity</p>
                          <p className="text-2xl font-bold text-blue-400 mt-1">
                            {getParameterValue(selectedBuoy, 'salinity')} PSU
                          </p>
                        </div>
                      )}

                      {/* Wind Speed */}
                      {getParameterValue(selectedBuoy, 'windSpeed') !== 'N/A' && (
                        <div className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                          <p className="text-gray-300 text-xs uppercase tracking-wide">Wind Speed</p>
                          <p className="text-2xl font-bold text-green-400 mt-1">
                            {getParameterValue(selectedBuoy, 'windSpeed')} m/s
                          </p>
                        </div>
                      )}

                      {/* Wind Direction */}
                      {getParameterValue(selectedBuoy, 'windDirection') !== 'N/A' && (
                        <div className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                          <p className="text-gray-300 text-xs uppercase tracking-wide">Wind Direction</p>
                          <p className="text-2xl font-bold text-cyan-400 mt-1">
                            {getParameterValue(selectedBuoy, 'windDirection')}°
                          </p>
                        </div>
                      )}

                      {/* Wave Height */}
                      {getParameterValue(selectedBuoy, 'waveHeight') !== 'N/A' && (
                        <div className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                          <p className="text-gray-300 text-xs uppercase tracking-wide">Wave Height</p>
                          <p className="text-2xl font-bold text-purple-400 mt-1">
                            {getParameterValue(selectedBuoy, 'waveHeight')} m
                          </p>
                        </div>
                      )}

                      {/* Pressure */}
                      {getParameterValue(selectedBuoy, 'pressure') !== 'N/A' && (
                        <div className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                          <p className="text-gray-300 text-xs uppercase tracking-wide">Pressure</p>
                          <p className="text-2xl font-bold text-orange-400 mt-1">
                            {getParameterValue(selectedBuoy, 'pressure')} hPa
                          </p>
                        </div>
                      )}

                      {/* Air Temperature */}
                      {getParameterValue(selectedBuoy, 'airTemperature') !== 'N/A' && (
                        <div className="bg-white/10 rounded-lg p-3 hover:bg-white/15 transition-colors">
                          <p className="text-gray-300 text-xs uppercase tracking-wide">Air Temperature</p>
                          <p className="text-2xl font-bold text-pink-400 mt-1">
                            {getParameterValue(selectedBuoy, 'airTemperature')}°C
                          </p>
                        </div>
                      )}
                    </div>

                    {/* Enhanced Data Quality Indicator */}
                    <div className="mt-4 p-4 bg-gradient-to-r from-green-500/10 to-blue-500/10 rounded-lg border border-green-500/20">
                      <div className="flex items-center gap-2 mb-2">
                        <span className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></span>
                        <span className="text-green-300 font-semibold">Live INCOIS Data</span>
                      </div>
                      <p className="text-xs text-gray-300">
                        🌊 <strong>Source:</strong> INCOIS Ocean Observation Network
                        {selectedBuoy.lastUpdate && ` • Last Updated: ${new Date(selectedBuoy.lastUpdate).toLocaleString()}`}
                        <br />
                        📡 <strong>Network Type:</strong> {selectedBuoy.type}
                        {selectedBuoy.reportingAgency && ` • Agency: ${selectedBuoy.reportingAgency}`}
                      </p>
                    </div>
                  </div>
                )}

                {/* No Data Message */}
                {!selectedBuoy.parameters && (
                  <div className="text-center py-8 bg-white/10 rounded-lg">
                    <p className="text-orange-300 text-lg">📊 No real-time data available</p>
                    <p className="text-gray-400 text-sm mt-2">
                      This buoy location is available but real-time parameters are not currently accessible from INCOIS
                    </p>
                    {selectedBuoy.url && (
                      <div className="mt-3">
                        <a 
                          href={selectedBuoy.url} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="text-cyan-400 hover:text-cyan-300 underline"
                        >
                          🔗 View Original Data Source
                        </a>
                      </div>
                    )}
                  </div>
                )}

                {/* Temperature Profile */}
                {tempChartData && (
                  <div>
                    <h5 className="text-lg font-semibold mb-3 text-red-300">Temperature Profile</h5>
                    <div style={{ height: '200px' }}>
                      <Line
                        data={tempChartData}
                        options={{
                          responsive: true,
                          maintainAspectRatio: false,
                          plugins: { 
                            legend: { labels: { color: 'white' } },
                            title: { display: true, text: 'Temperature vs Depth', color: 'white' }
                          },
                          scales: {
                            x: { 
                              title: { display: true, text: 'Depth', color: 'white' },
                              ticks: { color: 'white' },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                            y: { 
                              title: { display: true, text: 'Temperature (°C)', color: 'white' },
                              ticks: { color: 'white' },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                          },
                        }}
                      />
                    </div>
                  </div>
                )}

                {/* Salinity Profile */}
                {salinityChartData && (
                  <div>
                    <h5 className="text-lg font-semibold mb-3 text-blue-300">Salinity Profile</h5>
                    <div style={{ height: '200px' }}>
                      <Line
                        data={salinityChartData}
                        options={{
                          responsive: true,
                          maintainAspectRatio: false,
                          plugins: { 
                            legend: { labels: { color: 'white' } },
                            title: { display: true, text: 'Salinity vs Depth', color: 'white' }
                          },
                          scales: {
                            x: { 
                              title: { display: true, text: 'Depth', color: 'white' },
                              ticks: { color: 'white' },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                            y: { 
                              title: { display: true, text: 'Salinity (PSU)', color: 'white' },
                              ticks: { color: 'white' },
                              grid: { color: 'rgba(255,255,255,0.1)' }
                            },
                          },
                        }}
                      />
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <div className="text-center py-8">
                <p className="text-gray-300 text-lg mb-4">
                  🔍 Click on any buoy marker to view detailed oceanographic data
                </p>
                <div className="bg-white/10 rounded-lg p-4">
                  <h6 className="font-semibold mb-2">Current Filter Summary:</h6>
                  <p className="text-sm text-gray-300">
                    Region: {selectedRegion === 'all' ? 'All Regions' : regions[selectedRegion]?.name}
                  </p>
                  <p className="text-sm text-gray-300">
                    Network: {selectedNetwork === 'all' ? 'All Networks' : selectedNetwork}
                  </p>
                  <p className="text-sm text-gray-300">
                    Status: {showActiveOnly ? 'Active Only' : 'All Status'}
                  </p>
                  <div className="mt-3 pt-2 border-t border-white/20">
                    <p className="text-sm text-cyan-300 font-medium">
                      📊 Displaying {filteredStats.total} buoys with live INCOIS data
                    </p>
                    <p className="text-xs text-gray-400 mt-1">
                      Including RAMA, ARGO, and MOORED networks
                    </p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
