// BuoyMap.js - Fixed Unicode Escape Error
import { useEffect, useState } from "react";
import axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from "chart.js";
import 'chartjs-adapter-date-fns';
import L from "leaflet";

// Fix for missing Leaflet marker icons
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

function BuoyMap() {
  const [buoys, setBuoys] = useState([]);
  const [filteredBuoys, setFilteredBuoys] = useState([]);
  const [selectedBuoy, setSelectedBuoy] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Filter states
  const [selectedRegion, setSelectedRegion] = useState('all');
  const [selectedNetwork, setSelectedNetwork] = useState('all');
  const [ramaReportingFilter, setRamaReportingFilter] = useState('all');

  // INCOIS interface states
  const [showINCOISInterface, setShowINCOISInterface] = useState(false);
  const [selectedParameter, setSelectedParameter] = useState('Air Temperature');
  const [selectedTimeRange, setSelectedTimeRange] = useState('1M');
  const [incoisProxyUrl, setIncoisProxyUrl] = useState('');
  const [loadingProxy, setLoadingProxy] = useState(false);
  const [proxyError, setProxyError] = useState(null);
  const [iframeLoaded, setIframeLoaded] = useState(false);
  const [dataSource, setDataSource] = useState('');

  // Chat Interface States
  const [showChatInterface, setShowChatInterface] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [chatLoading, setChatLoading] = useState(false);

  // Regional boundaries for filtering
  const regions = {
    'indian_ocean': {
      name: 'Indian Ocean',
      bounds: { north: 30, south: -60, east: 100, west: 40 }
    },
    'bay_of_bengal': {
      name: 'Bay of Bengal',
      bounds: { north: 25, south: 5, east: 95, west: 80 }
    },
    'arabian_sea': {
      name: 'Arabian Sea',
      bounds: { north: 30, south: 0, east: 80, west: 50 }
    },
    'equatorial_indian': {
      name: 'Equatorial Indian Ocean',
      bounds: { north: 10, south: -10, east: 100, west: 50 }
    },
    'southern_indian': {
      name: 'Southern Indian Ocean',
      bounds: { north: -10, south: -60, east: 120, west: 40 }
    }
  };

  // INCOIS Parameters
  const incoisParameters = [
    'Air Pressure',
    'Air Temperature',
    'Relative Humidity',
    'Rainfall',
    'Wind Direction',
    'Wind Speed',
    'Wind Gust',
    'Irradiance',
    'Radiation In',
    'Current Direction @ 1.20m',
    'Current Direction @ 1.25m',
    'Current Direction @ 003m',
    'Current Speed @ 1.20m',
    'Current Speed @ 1.25m',
    'Current Speed @ 003m',
    'Water Temperature @ 0.5m',
    'Water Temperature @ 001m',
    'Water Temperature @ 005m',
    'Salinity @ 0.5m',
    'Salinity @ 001m',
    'Salinity @ 005m',
    'Significant Wave Height'
  ];

  // Time Range Options
  const timeRangeOptions = [
    { value: '1W', label: '1 Week', days: 7 },
    { value: '1M', label: '1 Month', days: 30 },
    { value: '3M', label: '3 Months', days: 90 },
    { value: '6M', label: '6 Months', days: 180 },
    { value: '1Y', label: '1 Year', days: 365 },
    { value: 'All', label: 'All Data', days: null }
  ];

  // Helper function to normalize RAMA GeoJSON features to buoy format
  const normalizeRamaBuoys = (ramaGeoJSON) => {
    if (!ramaGeoJSON || !ramaGeoJSON.features) return [];
    
    return ramaGeoJSON.features.map((feature, index) => ({
      id: `RAMA_${index + 1}`,
      buoyId: feature.properties.buoyId || `RAMA_${index + 1}`,
      name: feature.properties.type 
        ? `${feature.properties.type} Buoy ${feature.properties.buoyId || index + 1}` 
        : `RAMA Buoy ${index + 1}`,
      lat: feature.geometry.coordinates[1],
      lon: feature.geometry.coordinates[0],
      coordinates: feature.geometry.coordinates,
      type: "RAMA",
      status: "active",
      reportingAgency: feature.properties.reportingAgency || "INCOIS",
      url: feature.properties.url || null,
      reportingStatus: feature.properties.Reporting || feature.properties.reporting || 'Not Reporting',
      properties: feature.properties,
      parameters: {
        sst: feature.properties.sst || null,
        seaSurfaceTemperature: feature.properties.sst || null,
        salinity: feature.properties.salinity || null,
        windSpeed: feature.properties.windspeed || null,
        windDirection: feature.properties.winddirection || null,
        pressure: feature.properties.pressure || null,
        waveHeight: feature.properties.waveheight || null,
      },
      dataSource: "INCOIS",
    }));
  };

  // INCOIS Proxy Loading
  const loadINCOISProxy = async (buoyId, parameter) => {
    setLoadingProxy(true);
    setProxyError(null);
    
    try {
      console.log(`🌊 Loading INCOIS proxy URL for ${buoyId} → ${parameter}`);
      
      const response = await axios.get(`http://localhost:5000/api/incois-url/${buoyId}/${parameter}`);
      
      if (response.data && response.data.success) {
        const proxyUrl = response.data.proxyUrl;
        const fullProxyUrl = `http://localhost:5000${proxyUrl}`;
        
        setIncoisProxyUrl(fullProxyUrl);
        setDataSource('REAL INCOIS Proxy');
        console.log(`✅ INCOIS proxy URL loaded: ${proxyUrl}`);
      } else {
        const errorMsg = `Failed to generate INCOIS proxy URL: ${response.data.message || 'Unknown error'}`;
        console.error(errorMsg);
        setProxyError(errorMsg);
        setDataSource('Error Loading Proxy');
      }
    } catch (error) {
      const errorMsg = `Error loading INCOIS proxy: ${error.message}`;
      console.error('❌', errorMsg);
      setProxyError(errorMsg);
      setDataSource('Error');
    }
    
    setLoadingProxy(false);
  };

  // Chat Functions
  const initializeChatWithBuoy = (buoy) => {
    const buoyInfo = `Analyzing buoy ${formatBuoyId(buoy)} (${buoy.type} network) located at ${buoy.lat?.toFixed(6)}°N, ${buoy.lon?.toFixed(6)}°E in the ${getRegionFromCoordinates(buoy.lat, buoy.lon)}.`;
    
    const initialMessages = [
      {
        id: 1,
        type: 'system',
        content: `🌊 Buoy Analysis Assistant Activated`,
        timestamp: new Date().toLocaleTimeString()
      },
      {
        id: 2,
        type: 'assistant',
        content: `Hello! I'm here to help analyze buoy ${formatBuoyId(buoy)} from the ${buoy.type} network. ${buoyInfo} What would you like to know about this buoy's data, location, or oceanographic measurements?`,
        timestamp: new Date().toLocaleTimeString()
      }
    ];
    
    setChatMessages(initialMessages);
    setShowChatInterface(true);
  };

  const sendChatMessage = async () => {
    if (!userInput.trim() || !selectedBuoy) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: userInput,
      timestamp: new Date().toLocaleTimeString()
    };

    setChatMessages(prev => [...prev, userMessage]);
    setUserInput('');
    setChatLoading(true);

    // Simulate AI response
    setTimeout(() => {
      const responses = [
        `Based on buoy ${formatBuoyId(selectedBuoy)}'s data, this ${selectedBuoy.type} station is located in the ${getRegionFromCoordinates(selectedBuoy.lat, selectedBuoy.lon)}. ${selectedBuoy.type === 'MOORED' ? 'This MOORED buoy provides real-time INCOIS data including temperature, salinity, and wave measurements.' : `This ${selectedBuoy.type} buoy is part of the ${selectedBuoy.type} network monitoring oceanic conditions.`}`,
        
        `The buoy's coordinates are ${selectedBuoy.lat?.toFixed(6)}°N, ${selectedBuoy.lon?.toFixed(6)}°E. ${selectedBuoy.type === 'RAMA' ? `This RAMA buoy's reporting status is: ${selectedBuoy.reportingStatus}.` : ''} ${selectedBuoy.type === 'MOORED' ? 'You can view live INCOIS charts by clicking the "Show Charts" button above.' : ''}`,
        
        `${formatBuoyId(selectedBuoy)} is currently ${selectedBuoy.status} and provides oceanographic data for research and monitoring. ${selectedBuoy.type === 'ARGO' ? 'ARGO floats measure temperature and salinity profiles as they drift with ocean currents.' : ''} ${selectedBuoy.type === 'OMNI' ? 'OMNI buoys provide multi-parameter oceanographic measurements.' : ''}`,
        
        `This buoy is operated by ${selectedBuoy.dataSource || 'INCOIS'} and is part of the global ocean observing system. The data helps scientists understand ocean conditions, climate patterns, and marine environments in the Indian Ocean region.`
      ];

      const randomResponse = responses[Math.floor(Math.random() * responses.length)];
      
      const assistantMessage = {
        id: Date.now() + 1,
        type: 'assistant',
        content: randomResponse,
        timestamp: new Date().toLocaleTimeString()
      };

      setChatMessages(prev => [...prev, assistantMessage]);
      setChatLoading(false);
    }, 1500);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendChatMessage();
    }
  };

  // Helper function to get region name from coordinates
  const getRegionFromCoordinates = (lat, lon) => {
    if (lat > 15 && lon < 75) return "Northern Arabian Sea";
    if (lat > 10 && lon < 75) return "Western Arabian Sea";
    if (lat > 10 && lon > 85) return "Bay of Bengal";
    if (lat <= 10 && lon > 85) return "Eastern Bay of Bengal";
    if (lat < 0) return "Southern Indian Ocean";
    return "Central Indian Ocean";
  };

  // Helper function to format buoy ID for display
  const formatBuoyId = (buoy) => {
    if (buoy.buoyId && buoy.buoyId !== buoy.id) {
      return buoy.buoyId;
    }
    return buoy.id;
  };

  // Helper function to get appropriate buoy ID for INCOIS proxy
  const getProxyBuoyId = (buoy) => {
    if (buoy.type === 'MOORED') {
      return buoy.buoyId || buoy.id;
    }
    
    const representativeBuoys = {
      'ARGO': 'AD10', 
      'RAMA': 'BD11', 
      'OMNI': 'AD10'  
    };
    
    return representativeBuoys[buoy.type] || 'AD10';
  };

  // Helper function to get buoys in a specific region
  const getBuoysInRegion = (regionKey) => {
    if (regionKey === 'all') return buoys.length;
    
    const region = regions[regionKey];
    if (!region) return 0;
    
    return buoys.filter(buoy => {
      const lat = parseFloat(buoy.lat);
      const lon = parseFloat(buoy.lon);
      return lat <= region.bounds.north && 
             lat >= region.bounds.south && 
             lon <= region.bounds.east && 
             lon >= region.bounds.west;
    }).length;
  };

  // Helper function to get buoys by network type
  const getBuoysByNetwork = (networkType) => {
    if (networkType === 'all') return buoys.length;
    return buoys.filter(buoy => buoy.type === networkType).length;
  };

  // Helper function to get RAMA buoys by reporting status
  const getRamaBuoysByReporting = (reportingStatus) => {
    const ramaBuoys = buoys.filter(buoy => buoy.type === 'RAMA');
    
    if (reportingStatus === 'all') return ramaBuoys.length;
    
    if (reportingStatus === 'reporting') {
      return ramaBuoys.filter(buoy => {
        const status = buoy.reportingStatus || 'Not Reporting';
        return status.toLowerCase().includes('reporting') && !status.toLowerCase().includes('not');
      }).length;
    }
    
    if (reportingStatus === 'not_reporting') {
      return ramaBuoys.filter(buoy => {
        const status = buoy.reportingStatus || 'Not Reporting';
        return status.toLowerCase().includes('not') || status === 'Not Reporting';
      }).length;
    }
    
    return 0;
  };

  // Generate region options with counts
  const getRegionOptions = () => [
    { value: 'all', label: `All Regions (${getBuoysInRegion('all')})` },
    { value: 'indian_ocean', label: `Indian Ocean (${getBuoysInRegion('indian_ocean')})` },
    { value: 'bay_of_bengal', label: `Bay of Bengal (${getBuoysInRegion('bay_of_bengal')})` },
    { value: 'arabian_sea', label: `Arabian Sea (${getBuoysInRegion('arabian_sea')})` },
    { value: 'equatorial_indian', label: `Equatorial Indian Ocean (${getBuoysInRegion('equatorial_indian')})` },
    { value: 'southern_indian', label: `Southern Indian Ocean (${getBuoysInRegion('southern_indian')})` }
  ];

  // Generate network options with counts
  const getNetworkOptions = () => [
    { value: 'all', label: `All Networks (${getBuoysByNetwork('all')})` },
    { value: 'RAMA', label: `RAMA (${getBuoysByNetwork('RAMA')})` },
    { value: 'ARGO', label: `ARGO (${getBuoysByNetwork('ARGO')})` },
    { value: 'MOORED', label: `MOORED (${getBuoysByNetwork('MOORED')})` },
    { value: 'OMNI', label: `OMNI (${getBuoysByNetwork('OMNI')})` }
  ];

  // Generate RAMA reporting options with counts
  const getRamaReportingOptions = () => [
    { value: 'all', label: `All Status (${getRamaBuoysByReporting('all')})` },
    { value: 'reporting', label: `Reporting (${getRamaBuoysByReporting('reporting')})` },
    { value: 'not_reporting', label: `Not Reporting (${getRamaBuoysByReporting('not_reporting')})` }
  ];

  // Fetch ocean buoys data
  useEffect(() => {
    const fetchBuoys = async () => {
      try {
        setError(null);
        setLoading(true);
        console.log("🔄 Fetching buoys from server...");
        const response = await axios.get("http://localhost:5000/api/ocean-buoys");
        
        if (response.data && response.data.success) {
          const argoBuoys = response.data.argo || [];
          const mooredBuoys = response.data.moored || [];
          const ramaBuoys = normalizeRamaBuoys(response.data.rama);
          
          const allBuoys = [...argoBuoys, ...mooredBuoys, ...ramaBuoys];
          setBuoys(allBuoys);
          console.log(`📊 Total buoys loaded: ${allBuoys.length}`);
        } else {
          setError("No data received from server");
        }
      } catch (err) {
        console.error("❌ Error fetching buoys:", err);
        setError(`Failed to fetch buoy data: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchBuoys();
  }, []);

  // Filter buoys based on selected criteria
  useEffect(() => {
    let filtered = [...buoys];

    // Region filter
    if (selectedRegion !== 'all') {
      const region = regions[selectedRegion];
      if (region) {
        filtered = filtered.filter(buoy => {
          const lat = parseFloat(buoy.lat);
          const lon = parseFloat(buoy.lon);
          return lat <= region.bounds.north && 
                 lat >= region.bounds.south && 
                 lon <= region.bounds.east && 
                 lon >= region.bounds.west;
        });
      }
    }

    // Network filter
    if (selectedNetwork !== 'all') {
      filtered = filtered.filter(buoy => buoy.type === selectedNetwork);
    }

    // RAMA Reporting filter
    if (selectedNetwork === 'RAMA' && ramaReportingFilter !== 'all') {
      filtered = filtered.filter(buoy => {
        if (buoy.type !== 'RAMA') return false;
        
        const reportingStatus = buoy.reportingStatus || 'Not Reporting';
        if (ramaReportingFilter === 'reporting') {
          return reportingStatus.toLowerCase().includes('reporting') && !reportingStatus.toLowerCase().includes('not');
        } else if (ramaReportingFilter === 'not_reporting') {
          return reportingStatus.toLowerCase().includes('not') || reportingStatus === 'Not Reporting';
        }
        return true;
      });
    }

    setFilteredBuoys(filtered);
  }, [buoys, selectedRegion, selectedNetwork, ramaReportingFilter]);

  // Reset RAMA filter when network changes
  useEffect(() => {
    if (selectedNetwork !== 'RAMA') {
      setRamaReportingFilter('all');
    }
  }, [selectedNetwork]);

  // Enhanced buoy details fetching
  const handleBuoyClick = async (buoyId) => {
    setLoading(true);
    setError(null);
    setShowINCOISInterface(false);
    setIncoisProxyUrl('');
    setDataSource('');
    setProxyError(null);
    
    try {
      console.log(`🔍 Fetching details for buoy: ${buoyId}`);
      
      if (buoyId.startsWith('RAMA_')) {
        const ramaBuoy = buoys.find(b => b.id === buoyId && b.type === 'RAMA');
        if (ramaBuoy) {
          console.log("📊 RAMA buoy selected:", ramaBuoy);
          setSelectedBuoy(ramaBuoy);
          initializeChatWithBuoy(ramaBuoy);
        } else {
          setError(`RAMA buoy ${buoyId} not found`);
        }
      } else if (buoyId.startsWith('MOORED_')) {
        const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
        if (res.data && res.data.success && res.data.data) {
          const mooredBuoy = res.data.data;
          console.log("📊 MOORED buoy selected:", mooredBuoy);
          setSelectedBuoy(mooredBuoy);
          setShowINCOISInterface(true);
          await loadINCOISProxy(mooredBuoy.buoyId, selectedParameter);
          initializeChatWithBuoy(mooredBuoy);
        } else {
          setError(`No data available for moored buoy ${buoyId}`);
        }
      } else {
        const res = await axios.get(`http://localhost:5000/api/ocean-buoy/${buoyId}`);
        if (res.data && res.data.success && res.data.data) {
          console.log("📊 ARGO buoy selected:", res.data.data);
          setSelectedBuoy(res.data.data);
          initializeChatWithBuoy(res.data.data);
        } else {
          setError(`No data available for buoy ${buoyId}`);
        }
      }
    } catch (err) {
      console.error("Error fetching buoy details:", err);
      setError(`Failed to fetch details for buoy ${buoyId}: ${err.message}`);
    }
    setLoading(false);
  };

  // Handle parameter change and auto-update proxy
  const handleParameterChange = (newParameter) => {
    console.log(`🔄 Parameter changed to: ${newParameter}`);
    setSelectedParameter(newParameter);
    
    if (selectedBuoy && incoisProxyUrl) {
      console.log(`🔄 Auto-updating proxy for new parameter: ${newParameter}`);
      loadINCOISProxy(selectedBuoy.buoyId, newParameter);
    }
  };

  // Handle time range change
  const handleTimeRangeChange = (newTimeRange) => {
    console.log(`🔍 Time range changed to: ${newTimeRange}`);
    setSelectedTimeRange(newTimeRange);
  };

  // Submit button handler
  const handleSubmit = () => {
    if (selectedBuoy && selectedParameter) {
      loadINCOISProxy(selectedBuoy.buoyId, selectedParameter);
    }
  };

  // Apply filters
  const handleApplyFilters = () => {
    console.log('🔍 Applying filters:', {
      region: selectedRegion,
      network: selectedNetwork,
      ramaReporting: ramaReportingFilter
    });
  };

  // FIXED: Create buoy icons without Unicode escape sequences
  const createBuoyIcon = (type, color, isSelected = false) => {
    const size = isSelected ? 32 : 28;
    const strokeWidth = isSelected ? 3 : 2;
    
    // Create SVG without problematic Unicode escape sequences
    const svgContent = `<svg width="${size}" height="${size}" viewBox="0 0 ${size} ${size}" fill="none" xmlns="http://www.w3.org/2000/svg">
      <circle cx="${size/2}" cy="${size/2}" r="${size/2 - 2}" fill="${color}" stroke="#ffffff" stroke-width="${strokeWidth}"/>
      <text x="${size/2}" y="${size/2 + 4}" text-anchor="middle" fill="white" font-size="10" font-weight="bold">${type.charAt(0)}</text>
      ${isSelected ? `<circle cx="${size/2}" cy="${size/2}" r="${size/2 - 1}" fill="none" stroke="#ffff00" stroke-width="2" opacity="0.8"/>` : ''}
    </svg>`;
    
    return new L.Icon({
      iconUrl: 'data:image/svg+xml;base64,' + btoa(svgContent),
      iconSize: [size, size],
      iconAnchor: [size/2, size/2],
      popupAnchor: [0, -size/2]
    });
  };

  const getIconForBuoy = (buoy, isSelected = false) => {
    switch (buoy.type) {
      case 'OMNI': return createBuoyIcon('OMNI', '#0ea5e9', isSelected);
      case 'RAMA': return createBuoyIcon('RAMA', '#10b981', isSelected);
      case 'ARGO': return createBuoyIcon('ARGO', '#8b5cf6', isSelected);
      case 'MOORED': return createBuoyIcon('MOORED', '#f59e0b', isSelected);
      default: return createBuoyIcon('OMNI', '#0ea5e9', isSelected);
    }
  };

  // TOTAL STATS
  const totalStats = {
    total: buoys.length,
    active: buoys.filter(b => b.status === 'active').length,
    withData: buoys.filter(b => b.parameters && Object.keys(b.parameters).some(key => b.parameters[key] !== null)).length,
    networks: {
      ARGO: buoys.filter(b => b.type === 'ARGO').length,
      OMNI: buoys.filter(b => b.type === 'OMNI').length,
      RAMA: buoys.filter(b => b.type === 'RAMA').length,
      MOORED: buoys.filter(b => b.type === 'MOORED').length,
    }
  };

  // FILTERED STATS
  const filteredStats = {
    networks: {
      ARGO: filteredBuoys.filter(b => b.type === 'ARGO').length,
      OMNI: filteredBuoys.filter(b => b.type === 'OMNI').length,
      RAMA: filteredBuoys.filter(b => b.type === 'RAMA').length,
      MOORED: filteredBuoys.filter(b => b.type === 'MOORED').length,
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        
        {/* Error Display */}
        {error && (
          <div className="mb-6 p-4 bg-red-500/20 border border-red-500/50 rounded-lg">
            <p className="text-red-200">⚠️ {error}</p>
          </div>
        )}

        {/* Network Summary Section - MOVED TO TOP */}
        <div className="mb-6 bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 p-6">
          <h3 className="text-xl font-semibold text-white mb-4 flex items-center">
            <svg className="w-6 h-6 text-cyan-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"/>
            </svg>
            Ocean Buoy Network Summary
          </h3>
          
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            {/* Total Stats */}
            <div className="text-center">
              <div className="text-3xl font-bold text-white mb-2">{totalStats.total}</div>
              <div className="text-sm text-slate-400">Total Buoys</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-green-400 mb-2">{totalStats.active}</div>
              <div className="text-sm text-slate-400">Active</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-cyan-400 mb-2">{totalStats.withData}</div>
              <div className="text-sm text-slate-400">With Data</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-orange-400 mb-2">{totalStats.total - totalStats.withData}</div>
              <div className="text-sm text-slate-400">No Data</div>
            </div>
          </div>
          
          <hr className="border-slate-700 my-4" />
          
          {/* Network Breakdown */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center bg-slate-700/30 rounded-lg p-3">
              <div className="flex items-center justify-center mb-2">
                <div className="w-4 h-4 bg-green-400 rounded-full mr-2"></div>
                <span className="text-sm font-semibold text-slate-300">RAMA</span>
              </div>
              <div className="text-xl font-bold text-white">{totalStats.networks.RAMA}</div>
            </div>
            <div className="text-center bg-slate-700/30 rounded-lg p-3">
              <div className="flex items-center justify-center mb-2">
                <div className="w-4 h-4 bg-purple-400 rounded-full mr-2"></div>
                <span className="text-sm font-semibold text-slate-300">ARGO</span>
              </div>
              <div className="text-xl font-bold text-white">{totalStats.networks.ARGO}</div>
            </div>
            <div className="text-center bg-slate-700/30 rounded-lg p-3">
              <div className="flex items-center justify-center mb-2">
                <div className="w-4 h-4 bg-orange-400 rounded-full mr-2"></div>
                <span className="text-sm font-semibold text-slate-300">MOORED</span>
              </div>
              <div className="text-xl font-bold text-white">{totalStats.networks.MOORED}</div>
            </div>
            <div className="text-center bg-slate-700/30 rounded-lg p-3">
              <div className="flex items-center justify-center mb-2">
                <div className="w-4 h-4 bg-blue-400 rounded-full mr-2"></div>
                <span className="text-sm font-semibold text-slate-300">OMNI</span>
              </div>
              <div className="text-xl font-bold text-white">{totalStats.networks.OMNI}</div>
            </div>
          </div>
        </div>

        {/* Two Column Layout - INCREASED SIZES */}
        <div className="grid grid-cols-1 lg:grid-cols-5 gap-6">
          
          {/* LEFT: Ocean Buoy Network Map - INCREASED FROM 3 TO 4 COLUMNS */}
          <div className="lg:col-span-4">
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 overflow-hidden">
              
              {/* Header with Map Title */}
              <div className="p-4 border-b border-slate-700/50">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <svg className="w-5 h-5 text-cyan-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"/>
                    </svg>
                    <h3 className="text-lg font-semibold text-white">Ocean Buoy Network Map</h3>
                  </div>
                  <div className="flex space-x-4">
                    <div className="flex items-center text-xs text-green-400">
                      <div className="w-2 h-2 bg-green-400 rounded-full mr-1"></div>
                      RAMA ({filteredStats.networks.RAMA})
                    </div>
                    <div className="flex items-center text-xs text-purple-400">
                      <div className="w-2 h-2 bg-purple-400 rounded-full mr-1"></div>
                      ARGO ({filteredStats.networks.ARGO})
                    </div>
                    <div className="flex items-center text-xs text-orange-400">
                      <div className="w-2 h-2 bg-orange-400 rounded-full mr-1"></div>
                      MOORED ({filteredStats.networks.MOORED})
                    </div>
                  </div>
                </div>
              </div>

              {/* Data Filters */}
              <div className="p-4 bg-slate-700/30 border-b border-slate-700/50">
                <div className="flex items-center mb-3">
                  <svg className="w-4 h-4 text-cyan-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.414A1 1 0 013 6.707V4z"/>
                  </svg>
                  <span className="text-sm font-semibold text-white">Data Filters</span>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                  {/* Region Filter */}
                  <div>
                    <label className="flex items-center text-xs font-medium text-slate-300 mb-1">
                      <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"/>
                      </svg>
                      Region
                    </label>
                    <select 
                      value={selectedRegion}
                      onChange={(e) => setSelectedRegion(e.target.value)}
                      className="w-full bg-slate-600 border border-slate-500 rounded-lg px-3 py-2 text-xs text-white focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
                    >
                      {getRegionOptions().map(option => (
                        <option key={option.value} value={option.value} className="bg-slate-700">
                          {option.label}
                        </option>
                      ))}
                    </select>
                  </div>

                  {/* Network Filter */}
                  <div>
                    <label className="flex items-center text-xs font-medium text-slate-300 mb-1">
                      <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 11H5m14-7H3m16 14H5"/>
                      </svg>
                      Network
                    </label>
                    <select 
                      value={selectedNetwork}
                      onChange={(e) => setSelectedNetwork(e.target.value)}
                      className="w-full bg-slate-600 border border-slate-500 rounded-lg px-3 py-2 text-xs text-white focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
                    >
                      {getNetworkOptions().map(option => (
                        <option key={option.value} value={option.value} className="bg-slate-700">
                          {option.label}
                        </option>
                      ))}
                    </select>
                  </div>

                  {/* RAMA Reporting Filter */}
                  {selectedNetwork === 'RAMA' && (
                    <div>
                      <label className="flex items-center text-xs font-medium text-slate-300 mb-1">
                        <svg className="w-3 h-3 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"/>
                        </svg>
                        Reporting Status
                      </label>
                      <select 
                        value={ramaReportingFilter}
                        onChange={(e) => setRamaReportingFilter(e.target.value)}
                        className="w-full bg-green-500/20 border border-green-500/30 rounded-lg px-3 py-2 text-xs text-white focus:ring-2 focus:ring-green-400 focus:border-transparent"
                      >
                        {getRamaReportingOptions().map(option => (
                          <option key={option.value} value={option.value} className="bg-green-800 text-white">
                            {option.label}
                          </option>
                        ))}
                      </select>
                    </div>
                  )}

                  {/* Apply Filters Button */}
                  <div className="flex items-end">
                    <button 
                      onClick={handleApplyFilters}
                      className="w-full bg-cyan-600 hover:bg-cyan-700 text-white rounded-lg px-3 py-2 text-xs font-medium transition-colors"
                    >
                      Apply Filters
                    </button>
                  </div>
                </div>
              </div>
              
              {/* Map Container - INCREASED HEIGHT */}
              <div className="relative h-[700px]">
                <MapContainer
                  center={[12, 77]}
                  zoom={5}
                  style={{ height: "100%", width: "100%" }}
                  className="rounded-b-2xl"
                >
                  <TileLayer
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a>'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                  />
                  {filteredBuoys.map((buoy, idx) => {
                    const isSelected = selectedBuoy && selectedBuoy.id === buoy.id;
                    return (
                      <Marker
                        key={`${buoy.type}-${buoy.id}-${idx}`}
                        position={[buoy.lat || 0, buoy.lon || 0]}
                        icon={getIconForBuoy(buoy, isSelected)}
                        eventHandlers={{
                          click: () => handleBuoyClick(buoy.id),
                        }}
                      >
                        <Popup>
                          <div className="text-black p-3 min-w-[200px]">
                            <strong className="text-lg block mb-2">{formatBuoyId(buoy)}</strong>
                            <div className="space-y-1 text-sm">
                              <div><strong>Network:</strong> {buoy.type}</div>
                              <div><strong>Status:</strong> 
                                <span className={buoy.status === 'active' ? 'text-green-600 ml-1' : 'text-red-600 ml-1'}>
                                  {buoy.status === 'active' ? '🟢 Active' : '🔴 Inactive'}
                                </span>
                              </div>
                              <div><strong>Coordinates:</strong> {buoy.lat?.toFixed(6)}°N, {buoy.lon?.toFixed(6)}°E</div>
                              <div><strong>Region:</strong> {getRegionFromCoordinates(buoy.lat, buoy.lon)}</div>
                              
                              {buoy.type === 'RAMA' && buoy.reportingStatus && (
                                <div><strong>Reporting:</strong> 
                                  <span className={buoy.reportingStatus.toLowerCase().includes('reporting') && !buoy.reportingStatus.toLowerCase().includes('not') ? 'text-green-600 ml-1' : 'text-red-600 ml-1'}>
                                    {buoy.reportingStatus}
                                  </span>
                                </div>
                              )}
                              
                              <div className="pt-2 border-t border-gray-300">
                                <span className="text-blue-600 cursor-pointer underline text-xs">
                                  🤖 Click to open AI chat assistant
                                </span>
                              </div>
                            </div>
                          </div>
                        </Popup>
                      </Marker>
                    );
                  })}
                </MapContainer>
              </div>
            </div>
          </div>

          {/* RIGHT: Buoy Analysis - INCREASED SIZE */}
          <div className="lg:col-span-1">
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50 h-full min-h-[700px]">
              {selectedBuoy ? (
                <>
                  {/* Buoy Analysis Header */}
                  <div className="p-4 border-b border-slate-700/50 bg-gradient-to-r from-blue-500/10 to-cyan-500/10">
                    <div className="flex justify-between items-center">
                      <div>
                        <h3 className="text-lg font-semibold text-white flex items-center">
                          <div className="w-8 h-8 rounded-full bg-blue-500 flex items-center justify-center text-white text-sm font-bold mr-3">
                            {selectedBuoy.type.charAt(0)}
                          </div>
                          {formatBuoyId(selectedBuoy)}
                        </h3>
                        <p className="text-sm text-blue-200 ml-11">
                          {selectedBuoy.type} Network Analysis
                        </p>
                      </div>
                      <button
                        onClick={() => {
                          setSelectedBuoy(null);
                          setShowChatInterface(false);
                          setChatMessages([]);
                        }}
                        className="text-slate-400 hover:text-white transition-colors"
                      >
                        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"/>
                        </svg>
                      </button>
                    </div>
                  </div>

                  {/* Real Buoy Details Content */}
                  <div className="p-4 space-y-4 overflow-y-auto" style={{ height: 'calc(700px - 100px)' }}>
                    
                    {/* Location Details */}
                    <div className="bg-gradient-to-r from-blue-500/10 to-cyan-500/10 border border-blue-500/20 rounded-lg p-4">
                      <h4 className="text-sm font-semibold text-blue-400 mb-3 flex items-center">
                        <svg className="w-4 h-4 text-blue-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"/>
                        </svg>
                        📍 Location Details
                      </h4>
                      <div className="grid grid-cols-1 gap-3 text-sm">
                        <div className="bg-slate-700/30 rounded p-2">
                          <span className="text-slate-400">Coordinates:</span>
                          <div className="text-white font-medium">{selectedBuoy.lat?.toFixed(6)}°N, {selectedBuoy.lon?.toFixed(6)}°E</div>
                        </div>
                        <div className="bg-slate-700/30 rounded p-2">
                          <span className="text-slate-400">Region:</span>
                          <div className="text-white font-medium">{getRegionFromCoordinates(selectedBuoy.lat, selectedBuoy.lon)}</div>
                        </div>
                      </div>
                    </div>

                    {/* REAL INCOIS Data System Status */}
                    <div className="bg-gradient-to-r from-cyan-500/10 to-blue-500/10 border border-cyan-500/20 rounded-lg p-4">
                      <h4 className="text-sm font-semibold text-cyan-400 mb-3 flex items-center">
                        <svg className="w-4 h-4 text-cyan-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 10V3L4 14h7v7l9-11h-7z"/>
                        </svg>
                        🌊 Real-time INCOIS Data System
                      </h4>
                      <div className="space-y-3 text-sm">
                        <div className="bg-slate-700/30 rounded p-3">
                          <div className="text-cyan-300 font-medium mb-2">
                            {selectedBuoy.type === 'MOORED' 
                              ? 'Live oceanographic charts with direct INCOIS interface access'
                              : 'INCOIS network connectivity available'
                            }
                          </div>
                          <div className="grid grid-cols-2 gap-2 text-xs">
                            <div className="flex justify-between">
                              <span className="text-slate-400">✓ Precision:</span>
                              <span className="text-green-300">6 decimal places</span>
                            </div>
                            <div className="flex justify-between">
                              <span className="text-slate-400">📊 Status:</span>
                              <span className="text-green-300">active</span>
                            </div>
                            <div className="flex justify-between">
                              <span className="text-slate-400">💫 Guarantee:</span>
                              <span className="text-cyan-300">100% real data</span>
                            </div>
                            <div className="flex justify-between">
                              <span className="text-slate-400">Network:</span>
                              <span className="text-cyan-300">{selectedBuoy.type}</span>
                            </div>
                          </div>
                        </div>
                        
                        {/* CHART BUTTON FOR MOORED BUOYS */}
                        {selectedBuoy.type === 'MOORED' && (
                          <div className="text-center">
                            <button
                              onClick={() => {
                                setShowINCOISInterface(true);
                                loadINCOISProxy(selectedBuoy.buoyId, selectedParameter);
                              }}
                              className="px-6 py-3 bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 rounded-lg text-white font-semibold transition-all duration-200 shadow-lg transform hover:scale-105"
                            >
                              📊 Show Charts
                            </button>
                          </div>
                        )}
                        
                        {selectedBuoy.type === 'MOORED' && showINCOISInterface ? (
                          <div className="bg-green-500/10 border border-green-500/20 rounded p-2 text-xs">
                            <div className="text-green-400 font-medium">🎯 INCOIS Charts Active</div>
                            <div className="text-slate-300">Real-time oceanographic charts are displayed below</div>
                          </div>
                        ) : selectedBuoy.type !== 'MOORED' ? (
                          <div className="bg-orange-500/10 border border-orange-500/20 rounded p-2 text-xs">
                            <div className="text-orange-400 font-medium">📊 {selectedBuoy.type} Network Data</div>
                            <div className="text-slate-300">Select a MOORED buoy for live INCOIS charts</div>
                          </div>
                        ) : null}
                      </div>
                    </div>

                    {/* Organization Info */}
                    <div className="bg-gradient-to-r from-green-500/10 to-emerald-500/10 border border-green-500/20 rounded-lg p-4">
                      <h4 className="text-sm font-semibold text-green-400 mb-3 flex items-center">
                        <svg className="w-4 h-4 text-green-400 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-2m-2 0H7m0 0H5m2 0h2"/>
                        </svg>
                        🏛️ Organization Info
                      </h4>
                      <div className="space-y-2 text-sm">
                        <div className="flex justify-between bg-slate-700/20 rounded px-2 py-1">
                          <span className="text-slate-300">Status:</span>
                          <span className={`font-medium ${selectedBuoy.status === 'active' ? 'text-green-400' : 'text-red-400'}`}>
                            ● {selectedBuoy.status}
                          </span>
                        </div>
                        <div className="flex justify-between bg-slate-700/20 rounded px-2 py-1">
                          <span className="text-slate-300">Buoy ID:</span>
                          <span className="text-white font-medium">{formatBuoyId(selectedBuoy)}</span>
                        </div>
                        <div className="flex justify-between bg-slate-700/20 rounded px-2 py-1">
                          <span className="text-slate-300">Data Source:</span>
                          <span className="text-white font-medium">{selectedBuoy.dataSource || 'INCOIS'}</span>
                        </div>
                        <div className="flex justify-between bg-slate-700/20 rounded px-2 py-1">
                          <span className="text-slate-300">Network:</span>
                          <span className="text-white font-medium">{selectedBuoy.type}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </>
              ) : (
                <div className="flex items-center justify-center h-full">
                  <div className="text-center p-8">
                    <div className="w-20 h-20 bg-gradient-to-br from-blue-500/20 to-cyan-500/20 rounded-full flex items-center justify-center mx-auto mb-4 border border-blue-500/30">
                      <svg className="w-10 h-10 text-blue-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"/>
                      </svg>
                    </div>
                    <h3 className="text-xl font-semibold text-slate-200 mb-2">Select a Buoy for Analysis</h3>
                    <p className="text-sm text-slate-400 mb-6 max-w-sm">
                      Click on any buoy marker on the map to view real oceanographic data and access AI chat assistant.
                    </p>
                    <div className="grid grid-cols-2 gap-3 text-xs text-slate-500">
                      <div className="bg-slate-700/30 rounded p-3">
                        <div className="text-green-400 font-medium">🌊 RAMA</div>
                        <div>Network analysis</div>
                      </div>
                      <div className="bg-slate-700/30 rounded p-3">
                        <div className="text-purple-400 font-medium">🎯 ARGO</div>
                        <div>Profile data</div>
                      </div>
                      <div className="bg-slate-700/30 rounded p-3">
                        <div className="text-orange-400 font-medium">⚓ MOORED</div>
                        <div>REAL INCOIS charts!</div>
                      </div>
                      <div className="bg-slate-700/30 rounded p-3">
                        <div className="text-blue-400 font-medium">🔵 OMNI</div>
                        <div>Multi-parameter</div>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Chat Interface - Appears BELOW the map when buoy selected */}
        {selectedBuoy && showChatInterface && (
          <div className="mt-6">
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50">
              {/* Chat Header */}
              <div className="p-4 border-b border-slate-700/50 bg-gradient-to-r from-purple-500/20 to-pink-500/20">
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="text-lg font-semibold text-white flex items-center">
                      <div className="w-8 h-8 rounded-full bg-purple-500 flex items-center justify-center text-white text-sm font-bold mr-3">
                        🤖
                      </div>
                      AI Buoy Analysis Assistant
                    </h3>
                    <p className="text-sm text-purple-200 ml-11">
                      Analyzing: <strong>{formatBuoyId(selectedBuoy)}</strong> ({selectedBuoy.type} Network)
                    </p>
                  </div>
                  <button
                    onClick={() => {
                      setShowChatInterface(false);
                      setChatMessages([]);
                    }}
                    className="text-red-400 hover:text-red-300 bg-red-500/20 px-3 py-1 rounded transition-colors"
                  >
                    ✕ Close Chat
                  </button>
                </div>
              </div>

              {/* Chat Messages */}
              <div className="p-4 h-80 overflow-y-auto bg-slate-900/30">
                <div className="space-y-4">
                  {chatMessages.map((message) => (
                    <div key={message.id} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                      <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                        message.type === 'user' 
                          ? 'bg-blue-500 text-white' 
                          : message.type === 'system'
                            ? 'bg-purple-500/20 text-purple-300 border border-purple-500/30'
                            : 'bg-slate-600 text-white'
                      }`}>
                        <div className="text-sm">{message.content}</div>
                        <div className="text-xs opacity-70 mt-1">{message.timestamp}</div>
                      </div>
                    </div>
                  ))}
                  {chatLoading && (
                    <div className="flex justify-start">
                      <div className="bg-slate-600 text-white px-4 py-2 rounded-lg">
                        <div className="flex items-center space-x-2">
                          <div className="animate-bounce w-2 h-2 bg-white rounded-full"></div>
                          <div className="animate-bounce w-2 h-2 bg-white rounded-full" style={{animationDelay: '0.1s'}}></div>
                          <div className="animate-bounce w-2 h-2 bg-white rounded-full" style={{animationDelay: '0.2s'}}></div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Chat Input */}
              <div className="p-4 border-t border-slate-700/50">
                <div className="flex space-x-4">
                  <textarea
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder={`Ask about ${formatBuoyId(selectedBuoy)} data, location, or measurements...`}
                    className="flex-1 bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-white placeholder-slate-400 focus:ring-2 focus:ring-purple-400 focus:border-transparent resize-none"
                    rows="2"
                    disabled={chatLoading}
                  />
                  <button
                    onClick={sendChatMessage}
                    disabled={!userInput.trim() || chatLoading}
                    className="px-4 py-2 bg-purple-600 hover:bg-purple-700 disabled:bg-gray-600 disabled:cursor-not-allowed rounded-lg text-white font-medium transition-colors"
                  >
                    {chatLoading ? '⏳' : 'Send'}
                  </button>
                </div>
                <p className="text-xs text-slate-500 mt-2">Press Enter to send, Shift+Enter for new line</p>
              </div>
            </div>
          </div>
        )}

        {/* WORKING INCOIS Interface - Appears BELOW when MOORED buoy selected */}
        {selectedBuoy && selectedBuoy.type === 'MOORED' && showINCOISInterface && (
          <div className="mt-6">
            <div className="bg-slate-800/50 backdrop-blur-sm rounded-2xl border border-slate-700/50">
              {/* Header */}
              <div className="p-4 border-b border-slate-700/50 bg-gradient-to-r from-green-500/20 to-blue-500/20">
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="text-lg font-semibold text-white">🌊 REAL INCOIS Ocean Data System</h3>
                    <p className="text-sm text-gray-300">
                      Buoy: <strong>{selectedBuoy?.buoyId}</strong> • Parameter: <strong>{selectedParameter}</strong>
                    </p>
                    {dataSource && (
                      <p className="text-xs mt-1">
                        Source: <span className="text-green-400">{dataSource}</span>
                      </p>
                    )}
                  </div>
                  <button
                    onClick={() => {
                      setShowINCOISInterface(false);
                      setIncoisProxyUrl('');
                      setDataSource('');
                      setProxyError(null);
                    }}
                    className="text-red-400 hover:text-red-300 bg-red-500/20 px-3 py-1 rounded transition-colors"
                  >
                    ✕ Close
                  </button>
                </div>
              </div>

              {/* Parameter Controls */}
              <div className="p-4 bg-white/10 border-b border-white/20">
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 items-end">
                  <div>
                    <label className="block text-white font-medium mb-2">Parameter:</label>
                    <select
                      value={selectedParameter}
                      onChange={(e) => handleParameterChange(e.target.value)}
                      className="w-full px-3 py-2 rounded-lg bg-white/20 border border-white/30 text-white backdrop-blur-sm focus:outline-none focus:ring-2 focus:ring-blue-400 max-h-40 overflow-y-auto"
                      style={{ fontSize: '14px' }}
                    >
                      {incoisParameters.map(param => (
                        <option key={param} value={param} className="bg-blue-800 text-white py-1">
                          {param}
                        </option>
                      ))}
                    </select>
                  </div>
                  
                  <div>
                    <label className="block text-white font-medium mb-2">Time Range:</label>
                    <div className="grid grid-cols-3 gap-2">
                      {timeRangeOptions.map(option => (
                        <button
                          key={option.value}
                          onClick={() => handleTimeRangeChange(option.value)}
                          className={`px-3 py-2 text-sm rounded-lg font-medium transition-all duration-200 ${
                            selectedTimeRange === option.value
                              ? 'bg-blue-500 text-white shadow-lg'
                              : 'bg-white/20 text-white hover:bg-white/30'
                          }`}
                        >
                          {option.label}
                        </button>
                      ))}
                    </div>
                  </div>

                  <div className="flex justify-center">
                    <button
                      onClick={handleSubmit}
                      disabled={loadingProxy}
                      className="px-8 py-3 bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 disabled:from-gray-500 disabled:to-gray-600 border-0 rounded-lg text-white font-semibold transition-all duration-200 shadow-lg disabled:cursor-not-allowed transform hover:scale-105 active:scale-95"
                    >
                      {loadingProxy ? '⏳ Loading...' : '🚀 Submit'}
                    </button>
                  </div>
                </div>
              </div>

              {/* INCOIS Proxy Display Area */}
              <div className="p-6">
                {loadingProxy ? (
                  <div className="text-center py-8">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-400 mx-auto"></div>
                    <p className="text-gray-200 mt-2">Loading REAL INCOIS interface...</p>
                    <p className="text-xs text-gray-400 mt-1">Parameter: {selectedParameter}</p>
                  </div>
                ) : proxyError ? (
                  <div className="text-center py-8">
                    <div className="bg-red-500/20 border border-red-500/30 rounded-lg p-6">
                      <p className="text-red-400 text-lg">❌ {proxyError}</p>
                      <div className="mt-4 space-y-2">
                        <button
                          onClick={handleSubmit}
                          className="px-4 py-2 bg-green-500/20 hover:bg-green-500/30 border border-green-500/30 rounded-lg text-white font-medium transition-all duration-200 shadow-lg"
                        >
                          🔄 Retry
                        </button>
                      </div>
                    </div>
                  </div>
                ) : incoisProxyUrl ? (
                  <div>
                    <div className="mb-4 p-3 bg-green-500/20 border border-green-500/30 rounded-lg">
                      <h4 className="text-green-300 font-semibold">🌊 REAL INCOIS Interface Active</h4>
                      <p className="text-sm text-gray-300">
                        Direct access to INCOIS system • Parameter: <strong>{selectedParameter}</strong>
                      </p>
                    </div>
                    
                    <div className="bg-white rounded-lg overflow-hidden shadow-2xl" style={{ height: '600px' }}>
                      <iframe
                        key={`${incoisProxyUrl}-${Date.now()}`}
                        src={incoisProxyUrl}
                        width="100%"
                        height="100%"
                        title={`INCOIS Data for ${selectedBuoy.buoyId} - ${selectedParameter}`}
                        frameBorder="0"
                        className="w-full h-full"
                        allow="fullscreen"
                        onLoad={() => {
                          console.log('✅ Iframe loaded successfully');
                          setIframeLoaded(true);
                        }}
                        onError={(e) => {
                          console.error('❌ Iframe error:', e);
                          setProxyError('Iframe failed to load');
                        }}
                      />
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <div className="bg-blue-500/20 border border-blue-500/30 rounded-lg p-6">
                      <h4 className="text-blue-300 font-semibold mb-3">🌊 Ready to Load INCOIS Data</h4>
                      <p className="text-gray-300 mb-4">
                        Select a parameter above and click <strong>"Submit"</strong> to load the real INCOIS interface
                      </p>
                      <button
                        onClick={handleSubmit}
                        className="px-6 py-3 bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 rounded-lg text-white font-semibold transition-all duration-200 shadow-lg transform hover:scale-105"
                      >
                        🚀 Submit
                      </button>
                      <p className="text-xs text-green-400 mt-3">
                        ✅ 100% REAL INCOIS DATA - No dummy/mock data
                      </p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default BuoyMap;
